            score += randN;
            randScores[i] = randN;
            i++;
        }
        }


        if (score >= 36305) {
            return (Rarity.Legendary,randScores);
        } else if (score >= 30573) {
            return (Rarity.Epic,randScores);
        } else if (score >= 23313) {
            return (Rarity.Rare,randScores);
        } else {
            return (Rarity.Common,randScores);
        }
    }

    function analyzeRarity(uint256 tokenID, bytes32 blockHash)
    public
    view
    returns (Rarity, uint256[5] memory randScores)
    {
        require(_exists(tokenID), "Token Not Exist");
        require(
            block.number > tokenRarity[tokenID].revealBlock,
            "Token Not Revealed"
        );

        return calcRarity(tokenID, blockHash);
    }

