        require(enableUnStake, "Unstake Disabled");
        for (uint256 i = 0; i < _ids.length;) {
            require(ownerOf(_ids[i]) == msg.sender);
            IERC721x(address(this)).unlockId(_ids[i]);

        unchecked {
            i++;
        }
        }
    }

