{{
  "language": "Solidity",
  "sources": {
    "contracts/CHAPC.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import {IVRFCoordinatorV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/interfaces/IVRFCoordinatorV2Plus.sol";
import {VRFConsumerBaseV2Plus} from "@chainlink/contracts/src/v0.8/vrf/dev/VRFConsumerBaseV2Plus.sol";
import {VRFV2PlusClient} from "@chainlink/contracts/src/v0.8/vrf/dev/libraries/VRFV2PlusClient.sol";


import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
    * @dev The order of operations from the beginning of the Presale to the end:
    *
    * 1. Set up Chainlink account.
    * 2. Deploy contract and set deployed contract address to Chainlink as a consumer.
    * 3. Set whitelist users and airdrop winners.
    * 4. Unpause contract and start minting process.
    * 5. There are 3 packages and 10,000 tokens divided into 3 packages in appropriate numbers.
    *    When 1 batch (500 tokens) is minted from each package, call the requestRandomSeeds function to generate a random number,
    *    and then call the revealBatchOfPackages function for randomness in minting.
    *    The tokenURI function returns the appropriate token ID based on the random number.
    * 6. After all bones are sold, the baseURI is changed once to link the allocated tokens to bones and convert common NFTs to mythical NFTs.
    * 7. Tokens are collected for every transaction by connecting the tokenDistribution function to the transferFrom function.
    *    All collected tokens should be distributed to users after the launch of the JC token (ERC20).
*/

// Main contract definition, inheriting from ERC721, VRFConsumerBaseV2, and ConfirmedOwner
contract CHAPC is ERC721Enumerable, VRFConsumerBaseV2Plus, ReentrancyGuard {

     // Structs to hold various data
    struct RequestStatus {
        bool fulfilled;
        bool exists;
        uint256[] randomWords;
        uint8 packageId;
    }

    struct FirstOwner {
        address ownerAddress;
        uint256 collectedToken;
    }

    struct AirdropWinner {
        address winnerAddress;
        uint8 packageId;
        bool isClaimed;
    }

    struct Cart {
        uint8 packageId;
        uint8 quantity;
    }

    using Strings for uint256; // Importing library for string manipulation
    string public baseURI; // Base URI for token metadata
    // Contract variables
    IVRFCoordinatorV2Plus COORDINATOR;
    uint256 immutable s_subscriptionId;
    bytes32 immutable keyHash = 0x3fd2fec10d06ee8f65e7f2e95f5c56511359ece3f33960ad8a866ae24a8ff10b;
    address immutable coordinator = 0xD7f86b4b8Cae7D942340FF628F82735b7a20893a;


    bytes32 public whitelistMerkleRoot;
    bytes32 public ogMerkleRoot;
    bytes32 public airdropWinnersMerkleRoot;
    uint32 numWords = 1;
    uint32 callbackGasLimit = 2.5e6;
    uint8 public constant totalPackages = 3;
    bool public isTokenDistributing = true;
    bool public presaleMintOnlyOgOrWhitelisted = true;
    bool public paused = true;
    uint256 constant tokenCollectLimit = 1 days;
    uint256 public totalCollectedToken = 0;
    uint256 public creatorCollectedToken = 0;
    uint256 public bonePrice = 0.01 ether;
    uint256 public boneCurrentSupply = 20;
    uint256 public constant batchSize = 500;
    uint256 public constant boneMaxSupply = 2000;
    uint16 public constant tokenLimit = 1e4;
    uint16 requestConfirmations = 3;
    uint16 public maxLimitPerSession = 2;
    uint16 public nftPerAddressLimit = 2;
    uint16 public bonePerAddressLimit = 2;
    uint16[totalPackages] public tokenLimitOfPackages = [5500, 3500, 1000];
    uint16[totalPackages] public lastRevealedTokenOfPackages;
    uint16[totalPackages] public totalSupplyOfPackages;
    uint16[totalPackages] internal startIdsOfPackages = [0, 5500, 9000];
    mapping(uint256 => RequestStatus) public s_requests;
    mapping(uint256 => uint256) lastExecutionTime;
    mapping(address => AirdropWinner) public winners;
    mapping(uint8 => mapping(uint256 => uint256)) public randomSeeds;
    mapping(address => uint8) public presaleMintersMintedCount;
    mapping(address => uint256) public buyerCollectedToken;
    mapping(address => uint256) public boneBuyers;
    mapping(uint256 => FirstOwner) public firstOwners;
    mapping(uint8 => mapping(uint256 => uint256)) public revealedOrder;
    address[] public tokenCollectors;
    mapping(address => bool) public boneWinners;
    mapping(uint256 => uint8) public tokenRarities; // tokenId => rarity (1 to 6)

    enum Phase { OG, Whitelist }
    Phase public currentPhase = Phase.OG;

     // Event declarations
    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);
    event TokenCollectStatus(
        address from, 
        address buyer,
        uint256 tokenId,
        uint256 collected, 
        string message
    );

    event MintPresale(
        address from,
        uint256 tokenId
    );

    event MintAirdrop(
        address from,
        uint256 tokenId
    );

    event OwnerMint(
        uint256 tokenId
    );

    event PurchaseBone(
        address from,
        uint256 quantity
    );

    event BaseUriChange(
        string newUri
    );

    // Constructor function to initialize the contract
    constructor(
        string memory _name,
        string memory _symbol,
        uint256 subscriptionId,
        string memory _initBaseURI
    )
        ERC721(_name, _symbol)
        VRFConsumerBaseV2Plus(coordinator)
    {
        setBaseURI(_initBaseURI);
        COORDINATOR = IVRFCoordinatorV2Plus(coordinator);
        s_subscriptionId = subscriptionId;
    }

    function presaleMint(Cart[] memory cart, bytes32[] calldata _merkleProof) external payable nonReentrant {
        require(!paused, "the contract is paused");

        uint256 perAddressLimit = boneWinners[msg.sender] ? 5 : nftPerAddressLimit;
        uint256 maxLimitPS = boneWinners[msg.sender] ? 5 : maxLimitPerSession;
        bytes32 leaf = keccak256(abi.encode(msg.sender));

        if(presaleMintOnlyOgOrWhitelisted) {
            if (currentPhase == Phase.OG) {
            require(
                MerkleProof.verify(_merkleProof, ogMerkleRoot, leaf),
                "Not in OG list"
                );
            } else if (currentPhase == Phase.Whitelist) {
                require(
                    MerkleProof.verify(_merkleProof, whitelistMerkleRoot, leaf),
                    "Not whitelisted"
                );
            }
        }

        // Check if the sender is a contract
        require(!isContract(msg.sender), "Contracts are not allowed to mint");

        uint256 discount = currentPhase == Phase.OG ? 0.005 ether : 0 ether;
        uint256 totalWishQuantity;
        uint256 totalPrice;

        // Calculate total price and total quantity to be minted
        for (uint8 i = 0; i < cart.length; i++) {
            require(cart[i].packageId >= 0 && cart[i].packageId <= 2, "Invalid package id");
            require(cart[i].quantity > 0, "need to mint at least 1 NFT");
            require(
                totalSupplyOfPackages[cart[i].packageId] + cart[i].quantity <= tokenLimitOfPackages[cart[i].packageId],
                "Exceeds total supply for package"
            );

            totalWishQuantity += cart[i].quantity;

            uint256 price;
            if (cart[i].packageId == 0) {
                price = 0.03 ether - discount;
            } else if (cart[i].packageId == 1) {
                price = 0.06 ether - discount;
            } else {
                price = 0.09 ether - discount;
            }

            totalPrice += price * cart[i].quantity;
        }

        require(msg.value == totalPrice, "Incorrect amount sent");
        require(totalWishQuantity <= maxLimitPS, "max mint quantity per session exceeded.");
        require(
            presaleMintersMintedCount[msg.sender] + totalWishQuantity <= perAddressLimit,
            "max mint quantity per minter exceeded"
        );
        require(totalSupply() + totalWishQuantity <= tokenLimit, "Exceeds total supply");

        // Update state variables before mintingc
        for (uint8 i = 0; i < cart.length; i++) {
            uint256 packageId = cart[i].packageId;
            for (uint8 j = 0; j < cart[i].quantity; j++) {
                uint256 tokenId = startIdsOfPackages[packageId] + totalSupplyOfPackages[packageId];
                totalSupplyOfPackages[packageId] += 1;
                firstOwners[tokenId] = FirstOwner({ownerAddress: msg.sender, collectedToken: 0});
            }
        }

        presaleMintersMintedCount[msg.sender] += uint8(totalWishQuantity);

        // Check if the OG phase should end
        if (currentPhase == Phase.OG && totalSupply() >= 1000) {
            currentPhase = Phase.Whitelist;
        }

        // Mint tokens after updating state variables
        for (uint8 i = 0; i < cart.length; i++) {
            uint256 packageId = cart[i].packageId;
            for (uint8 j = 0; j < cart[i].quantity; j++) {
                uint256 tokenId = startIdsOfPackages[packageId] + totalSupplyOfPackages[packageId] - cart[i].quantity + j;
                _safeMint(msg.sender, tokenId);
                emit MintPresale(msg.sender, tokenId);
            }
        }
    }

    function airdropMint() external nonReentrant {
        require(!paused, "the contract is paused");
        require(!isContract(msg.sender), "Contracts are not allowed to mint");
        require(winners[msg.sender].winnerAddress == msg.sender, "not in airdrop");
        require(!winners[msg.sender].isClaimed, "already claimed");

        uint256 _packageId = winners[msg.sender].packageId;
        uint16 _mintAmount = 1;

        require(totalSupply() + _mintAmount <= tokenLimit, "Exceeds total supply");
        require(
            totalSupplyOfPackages[_packageId] + _mintAmount <= tokenLimitOfPackages[_packageId],
            "Exceeds total supply for package"
        );

        uint256 tokenId = startIdsOfPackages[_packageId] + totalSupplyOfPackages[_packageId];
        totalSupplyOfPackages[_packageId] += _mintAmount;
        winners[msg.sender].isClaimed = true;
        firstOwners[tokenId] = FirstOwner({
            ownerAddress: msg.sender,
            collectedToken: 0
        });

        // Mint token after updating state variables
        _safeMint(msg.sender, tokenId);
        emit MintAirdrop(msg.sender, tokenId);
    }

    function ownerMint(Cart[] memory cart) external onlyOwner nonReentrant {
        require(!paused, "the contract is paused");     
        uint256 totalWishQuantity;

        // Calculate total price and total quantity to be minted
        for (uint8 i = 0; i < cart.length; i++) {
            require(cart[i].packageId >= 0 && cart[i].packageId <= 2, "Invalid package id");
            require(cart[i].quantity > 0, "need to mint at least 1 NFT");
            require(
                totalSupplyOfPackages[cart[i].packageId] + cart[i].quantity <= tokenLimitOfPackages[cart[i].packageId],
                "Exceeds total supply for package"
            );

            totalWishQuantity += cart[i].quantity;
        }
        require(totalSupply() + totalWishQuantity <= tokenLimit, "Exceeds total supply");

        // Update state variables before minting
        for (uint8 i = 0; i < cart.length; i++) {
            uint256 packageId = cart[i].packageId;
            for (uint8 j = 0; j < cart[i].quantity; j++) {
                uint256 tokenId = startIdsOfPackages[packageId] + totalSupplyOfPackages[packageId];
                totalSupplyOfPackages[packageId] += 1;
                firstOwners[tokenId] = FirstOwner({ownerAddress: msg.sender, collectedToken: 0});
            }
        }

        // Mint tokens after updating state variables
        for (uint8 i = 0; i < cart.length; i++) {
            uint256 packageId = cart[i].packageId;
            for (uint8 j = 0; j < cart[i].quantity; j++) {
                uint256 tokenId = startIdsOfPackages[packageId] + totalSupplyOfPackages[packageId] - cart[i].quantity + j;
                _safeMint(msg.sender, tokenId);
                emit OwnerMint(tokenId);
            }
        }
    } 

    // Utility function to check if an address is a contract
    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    // Function to purchase bones
    function purchaseBone(uint8 quantity) external payable nonReentrant {
        require(
            boneCurrentSupply + quantity <= boneMaxSupply,
            "Exceeds total supply"
        );
        require(boneBuyers[msg.sender] + quantity <= bonePerAddressLimit, "max mint quantity per minter exceeded");
        require(msg.value == bonePrice * quantity, "Incorrect amount sent");
        boneBuyers[msg.sender] += quantity;
        boneCurrentSupply += quantity;
        emit PurchaseBone(msg.sender, quantity);
    }

    // Function to request random seeds from Chainlink VRF
    function requestRandomSeeds(uint8 packageId)
        external
        onlyOwner
        returns (uint256 requestId)
    {
        require(packageId < totalPackages, "Invalid packageId");
        require(
            totalSupplyOfPackages[packageId] >=
                lastRevealedTokenOfPackages[packageId] + batchSize,
            "Insufficient total supply for package"
        );

        requestId = COORDINATOR.requestRandomWords(
            VRFV2PlusClient.RandomWordsRequest({
                keyHash: keyHash,
                subId: s_subscriptionId,
                requestConfirmations: requestConfirmations,
                callbackGasLimit: callbackGasLimit,
                numWords: numWords,
                extraArgs: VRFV2PlusClient._argsToBytes(
                    VRFV2PlusClient.ExtraArgsV1({nativePayment: false})
                )
            })
        );

        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false,
            packageId: packageId
        });
        emit RequestSent(requestId, numWords);
        return requestId;
    }

    // Internal function to handle fulfillment of random seeds
    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] calldata  _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;
        setRandomSeed(s_requests[_requestId].packageId, _randomWords[0]);
        emit RequestFulfilled(_requestId, _randomWords);
    }

    function setTokenRarities(uint256[] memory tokenIds, uint8[] memory rarities) public onlyOwner {
        require(tokenIds.length == rarities.length, "Arrays must be of equal length");
        for (uint256 i = 0; i < tokenIds.length; i++) {
            require(rarities[i] >= 1 && rarities[i] <= 6, "Invalid rarity level");
            tokenRarities[tokenIds[i]] = rarities[i];
        }
    }

    // Common, Rare, Mythical, Epic, Legendary, Ultra Legendary
    function getTokenCountByRarity(uint8 rarity) private pure returns (uint256) {
        if (rarity == 1) {
            return 90; 
        } else if (rarity == 2) {
            return 120;
        } else if (rarity == 3) {
            return 150;
        } else if (rarity == 4) {
            return 180;
        } else if (rarity == 5) {
            return 210;
        } else if (rarity == 6) {
            return 250;
        }
        return 0;
    }

    // Private function to distribute tokens to buyers
    function tokenDistribution(address from, address buyer, uint256 tokenId) private {
        if (lastExecutionTime[tokenId] + tokenCollectLimit < block.timestamp) {
            uint8 rarity = tokenRarities[tokenId];
            uint256 totalAssignedTokenCount;

            if (rarity == 0) {
                // If the rarity is not set yet, distribute a static amount of 90 tokens.
                totalAssignedTokenCount = 90;
            } else {
                totalAssignedTokenCount = getTokenCountByRarity(rarity);
            }

            uint256 creatorTokens = (totalAssignedTokenCount * 60) / 100;
            uint256 buyerTokens = (totalAssignedTokenCount * 35) / 100;
            uint256 firstOwnerTokens = totalAssignedTokenCount - creatorTokens - buyerTokens;
            firstOwners[tokenId].collectedToken += firstOwnerTokens;
            totalCollectedToken += totalAssignedTokenCount;
            creatorCollectedToken += creatorTokens;
            buyerCollectedToken[buyer] += buyerTokens;
            lastExecutionTime[tokenId] = block.timestamp;
            emit TokenCollectStatus(
                from,
                buyer,
                tokenId,
                buyerTokens,
                "Successfully collected."
            );
        } else {
            emit TokenCollectStatus(
                from,
                buyer,
                tokenId,
                0,
                "Tokens already collected within the last 24 hours on this NFT."
            );
        }
    }

    // Override transferFrom function to distribute tokens on transfer
    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual override(ERC721, IERC721) {
        super.transferFrom(from, to, id);
        if (isTokenDistributing) {
            tokenDistribution(from, to, id);
        }
    }

    function setRandomSeed(uint8 packageId, uint256 randomness) internal {
        uint256 batchNumber = lastRevealedTokenOfPackages[packageId] /
            batchSize;
        randomSeeds[packageId][batchNumber] = randomness;
    }

    // Function to reveal a batch of packages
    function revealBatchOfPackages(uint8 packageId) external onlyOwner {
        require(
            totalSupplyOfPackages[packageId] >=
                lastRevealedTokenOfPackages[packageId] + batchSize,
            "Insufficient total supply for package"
        );
        require(
            randomSeeds[packageId][
                lastRevealedTokenOfPackages[packageId] / batchSize
            ] != 0,
            "Random seed not yet generated."
        );
        require(
            lastRevealedTokenOfPackages[packageId] +
                uint8(uint256(batchSize)) <=
                tokenLimitOfPackages[packageId],
            "All supplies revealed."
        );

        uint256 starts = startIdsOfPackages[packageId] + lastRevealedTokenOfPackages[packageId];
        uint256[batchSize] memory ranges;
        for (uint256 i = 0; i < batchSize; i++) {
            ranges[i] = starts + i;
        }

        uint256 batchNumber = lastRevealedTokenOfPackages[packageId] / batchSize;
        shuffleArray(ranges, randomSeeds[packageId][batchNumber]);

        for (uint256 i = 0; i < batchSize; i++) {
            revealedOrder[packageId][starts + i] = ranges[i];
        }

        lastRevealedTokenOfPackages[packageId] += uint8(uint256(batchSize));
    }


    // Override baseURI function to return token URI
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    // Function to pause/unpause contract functionality
    function pause(bool _state) external onlyOwner {
        paused = _state;
    }

    // Function to shuffle an array using Fisher-Yates algorithm
    function shuffleArray(uint256[batchSize] memory arr, uint256 seed)
        internal
        pure
    {
        uint256 divisor = 1e12;
        uint256 smallerSeed = seed / divisor;
        for (uint256 i = batchSize - 1; i > 0; i--) {
            uint256 j = (smallerSeed * i) % (i + 1);
            (arr[i], arr[j]) = (arr[j], arr[i]);
        }
    }

    // Function to set whitelist merkle root
    function setWhitelistMerkleRoot(bytes32 _newMerkleRoot) external onlyOwner {
        whitelistMerkleRoot = _newMerkleRoot;
    }

    function setOgMerkleRoot(bytes32 _newMerkleRoot) external onlyOwner {
        ogMerkleRoot = _newMerkleRoot;
    }

    // Function to enable/disable token distribution
    // Token distribution will be paused until JC token minting begins.
    function setTokenDistributing(bool value) external onlyOwner {
        isTokenDistributing = value;
    }

    // Function to set chainlink callback gas limit
    function setCallbackGasLimit(uint32 gasLimit) external onlyOwner {
        callbackGasLimit = gasLimit;
    }

    // Function to set base URI for token metadata
    // This function will be execute once for merge bones
    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
        emit BaseUriChange(_newBaseURI);
    }

    function setAirdropWinners(address[] memory _winners, uint8[] memory _packageIds) external onlyOwner {
        require(_winners.length == _packageIds.length, "Length mismatch");
        for (uint256 i = 0; i < _winners.length; i++) {
            require(_packageIds[i] >= 0 && _packageIds[i] < 2, "Invalid package id");
            require(!winners[_winners[i]].isClaimed, "Winner already claimed");
            winners[_winners[i]] = AirdropWinner({
                winnerAddress: _winners[i],
                packageId: _packageIds[i],
                isClaimed: false
            });
        }
    }

    function setPresaleMintOnlyWhitelisted(bool _newValue) external onlyOwner {
        presaleMintOnlyOgOrWhitelisted = _newValue;
    }

    // Function to set bone winners
    function setBoneWinners(address[] memory _winners) external onlyOwner {
        for (uint256 i = 0; i < _winners.length; i++) {
            boneWinners[_winners[i]] = true;
        }
    }

    function setPhase(Phase _phase) external onlyOwner {
        require(_phase == Phase.OG || _phase == Phase.Whitelist, "Invalid phase");
        currentPhase = _phase;
    }

    // Function to withdraw Ether from the contract
    function withdraw() external payable onlyOwner {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }

    // Function to return token URI
    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        uint8 packageId;

        if (tokenId < 5500) {
            packageId = 0;
        } else if (tokenId < 9000) {
            packageId = 1;
        } else if (tokenId < 10000) {
            packageId = 2;
        } else {
            return "out-of-bounds";
        }

        if (tokenId >= (lastRevealedTokenOfPackages[packageId] + startIdsOfPackages[packageId])) {
            return string(abi.encodePacked("https://storage.junglebox.io/unrevealed.json"));
        } else {
            return string(abi.encodePacked(_baseURI(), revealedOrder[packageId][tokenId].toString()));
        }
    }
}"
    },
