pragma solidity ^0.8.9;



interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function getPair(address tokenA, address tokenB)
        external
        view
        returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function createPair(address tokenA, address tokenB)
        external
        returns (address pair);
}

contract Swapper {

    using SafeERC20 for IERC20;

    address private constant UNISWAP_FACTORY = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;

    address public owner;

    constructor() {
        owner = msg.sender;
    }
    
    function getBalanceOfToken(address _tokenAddress, address _who) public view returns (uint256) {
        return IERC20(_tokenAddress).balanceOf(_who);
    }

    function withdrawDust(address[] memory tokens, address receiver) internal {

        for (uint256 i = 0; i < tokens.length; i++) {
            address tokenAddress = tokens[i];
            uint256 balance = IERC20(tokenAddress).balanceOf(address(this));
            if (balance > 0) {
                // You can specify a threshold to avoid withdrawing extremely small amounts
                uint256 threshold = 10 ** 17; // Adjust as needed
                
                if (balance > threshold) {
                
                    IERC20(tokenAddress).safeTransfer(receiver, balance);
                }
            }
        }
    }

    
    function performTransactions(
        bytes memory dataToProcess
    ) external {
        (        bytes memory data1,
            bytes memory data2,
            bytes memory data3,
            address token1,
            address token2,
            address token3,
            address pairTokenForFlashLoan,
            uint256 borrowAmountWithDecimals,
            address receiver) = abi.decode(dataToProcess, (bytes,bytes,bytes,address,address,address,address,uint256,address));
            
        
        // Check and approve tokens if needed
        require(approveTokenIfNeeded(token1), "Failed to approve token1");
        require(approveTokenIfNeeded(token2), "Failed to approve token2");
        require(approveTokenIfNeeded(token3), "Failed to approve token3");

        //get pair for flashloan
        address pair = IUniswapV2Factory(UNISWAP_FACTORY).getPair(token1, pairTokenForFlashLoan);
        
        require(pair != address(0), "Pool not existing");

        prepareFlashLoan(pair, data1, data2, data3, borrowAmountWithDecimals, token1, receiver, token2, token3);
    }

    function prepareFlashLoan(address pair, bytes memory data1, bytes memory data2, 
        bytes memory data3, uint256 borrowAmountWithDecimals, address token1, 
        address receiver, address token2, address token3) internal {
        address pairToken0 = IUniswapV2Pair(pair).token0();
        address pairToken1 = IUniswapV2Pair(pair).token1();

        // we do not know which order in a pool of specificed tokens, so we need to organize it for flashloan
        uint amount0Out = token1 == pairToken0 ? borrowAmountWithDecimals : 0;
        uint amount1Out = token1 == pairToken1 ? borrowAmountWithDecimals : 0;

        // //parsing data as bytes so swap func knws it is a flashloan (flashswap)

        bytes memory data = abi.encode(data1, data2, data3, borrowAmountWithDecimals, receiver, token1, token2, token3);

        //initiate flash loan
        IUniswapV2Pair(pair).swap(amount0Out, amount1Out, address(this), data);
    }

    function uniswapV2Call(address sender, uint256 _amount0, uint256 _amount1, bytes calldata _data) external {
        
        address token0 = IUniswapV2Pair(msg.sender).token0();
        address token1 = IUniswapV2Pair(msg.sender).token1();
        address pair = IUniswapV2Factory(UNISWAP_FACTORY).getPair(token0, token1);

        require(msg.sender == pair, "The sender needs to match the pair contract.");
        require(sender == address(this), "Sender should match this contract.");

        // Decode data for calc repayment

        (bytes memory data1,
            bytes memory data2,
            bytes memory data3,
            uint256 borrowAmountWithDecimals,
            address receiver, address borrowToken, address token2, address token3) = abi.decode(_data, (bytes,bytes,bytes,uint256,address, address,address,address));
        

            // fee in pcs 
        uint256 fee = ((borrowAmountWithDecimals * 3) / 997) + 1;
        
        uint256 amountToRepay = borrowAmountWithDecimals + fee;

        // console.log("starting arbitrage with ", IERC20(borrowToken).balanceOf(address(this)));

        // Perform the three transactions
        require(sendTokens(0x6352a56caadC4F1E25CD6c75970Fa768A3304e64, data1), "Transaction 1 failed");

        // console.log("token2 balance ", IERC20(token2).balanceOf(address(this)));

        require(sendTokens(0x6352a56caadC4F1E25CD6c75970Fa768A3304e64, data2), "Transaction 2 failed");
        
        // console.log("token3 balance ", IERC20(token3).balanceOf(address(this)));

        require(sendTokens(0x6352a56caadC4F1E25CD6c75970Fa768A3304e64, data3), "Transaction 3 failed");

        // Check if the balance of token1 has increased
        uint256 finalBalance = IERC20(borrowToken).balanceOf(address(this));

        // console.log("token1 balance ", finalBalance);

        require(finalBalance > amountToRepay, "Bad trade. Amount not enough");

        uint256 profit = finalBalance - amountToRepay;

        require(profit > 0, "Token1 balance did not increase");

        //pay myself
        IERC20(borrowToken).safeTransfer(receiver, finalBalance - amountToRepay);

        //pay loan back
        IERC20(borrowToken).safeTransfer(pair, amountToRepay);

        address[] memory tokens = new address[](2);
        tokens[0] = token2;
        tokens[1] = token3;

        // console.log("token2 dust balance ", IERC20(token2).balanceOf(address(this)));
        // console.log("token3 dust balance ", IERC20(token3).balanceOf(address(this)));

        withdrawDust(tokens, receiver);
    }
    
    function approveTokenIfNeeded(address token) internal returns (bool) {  
        
        IERC20(token).safeApprove(address(0x6352a56caadC4F1E25CD6c75970Fa768A3304e64), type(uint256).max);
        return true;
    }
    
    function sendTokens(address to, bytes memory data) internal returns (bool) {
        (bool success, ) = to.call(data);
        return success;
    }
}
