contract AGEIDO is ReentrancyGuard {
    using SafeERC20 for IERC20;

    address public admin;
    address payable public marketingWallet;
    IERC20 public ageToken;
    IERC20 public usdtToken;
    AggregatorV3Interface internal ethUsdPriceFeed;
    uint256 public tokenPriceInUSDT = 0.1 * 1e6; // 1 AGE = 0.1 USDT (in wei)
    uint256 public tokensSold;
    uint256 public constant presaleTokensAmount = 10000000 * 1e18;
    uint256 public presaleStartTime;
    uint256 public presaleEndTime;

    event TokensPurchased(
        address indexed buyer,
        uint256 amount,
        uint256 totalPrice
    );

    constructor(address _marketingWallet) {
        marketingWallet = payable(_marketingWallet);
        admin = msg.sender;
        ageToken = IERC20(0x0cc8D1C358C007cac55Fa0Bb3c750E723538f659);
        usdtToken = IERC20(0xdAC17F958D2ee523a2206206994597C13D831ec7);
        ethUsdPriceFeed = AggregatorV3Interface(
            0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419
        ); // For Mainnet
    }

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can call this function");
        _;
    }

    function updateTokenPrice(uint256 _tokenPrice) external onlyAdmin {
        tokenPriceInUSDT = _tokenPrice;
    }

    function updateAdminAddress(address account) external onlyAdmin {
        admin = account;
    }

    function updatePresaleStartEndTime(uint256 startTime, uint256 endTime)
        external
        onlyAdmin
    {
        presaleStartTime = startTime;
        presaleEndTime = endTime;
    }

    function buyTokensInUSDT(uint256 numberOfTokens) external nonReentrant {
        require(block.timestamp > presaleStartTime, "Presale not Started");
        require(block.timestamp < presaleEndTime, "Presale time Ended");
        require(
            tokensSold + numberOfTokens < presaleTokensAmount,
            "PreSale Amount Exceed"
        );
        require(
            ageToken.balanceOf(address(this)) >= numberOfTokens,
            "Insufficient Sale Tokens"
        );
        uint256 totalTokens = numberOfTokens / 1e18;
        require(totalTokens > 0, "Min Buy Amount is 1 Token");
        uint256 totalPrice = (numberOfTokens * tokenPriceInUSDT) / 1e18;
        usdtToken.safeTransferFrom(msg.sender, marketingWallet, totalPrice);

        ageToken.safeTransfer(msg.sender, numberOfTokens);
        tokensSold += numberOfTokens;
        emit TokensPurchased(msg.sender, numberOfTokens, totalPrice);
    }

    function getEthPriceInUSD() public view returns (uint256) {
        (, int256 price, , , ) = ethUsdPriceFeed.latestRoundData();
        return uint256(price) / 10**8;
    }

    function buyTokensInEth(uint256 numberOfTokens)
        external
        payable
        nonReentrant
    {
        require(block.timestamp > presaleStartTime, "Presale not Started");
        require(block.timestamp < presaleEndTime, "Presale time Ended");
        require(
            tokensSold + numberOfTokens < presaleTokensAmount,
            "PreSale Amount Exceed"
        );
        uint256 totalTokens = numberOfTokens / 1e18;
        require(totalTokens > 0, "Min Buy Amount is 1 Token");
        uint256 tokenPriceInWei = tokenPriceInEth(numberOfTokens);
        require(msg.value >= tokenPriceInWei, "Insufficient eth value");
        require(
            ageToken.balanceOf(address(this)) >= numberOfTokens,
            "Insufficient Sale Tokens"
        );
        (bool sent, ) = payable(marketingWallet).call{value: tokenPriceInWei}(
            ""
        );
        require(sent, "Eth amount not sent");

        ageToken.safeTransfer(msg.sender, numberOfTokens);
        tokensSold += numberOfTokens;

        if (msg.value > tokenPriceInWei) {
            (bool RefundExtraAmount, ) = payable(msg.sender).call{
                value: msg.value - tokenPriceInWei
            }("");
            require(RefundExtraAmount, "RefundExtraAmount not sent");
        }

        emit TokensPurchased(msg.sender, numberOfTokens, msg.value);
    }

    function tokenPriceInEth(uint256 numberOfTokens)
        public
        view
        returns (uint256)
    {
        return ((tokenPriceInUSDT * numberOfTokens) /
            (getEthPriceInUSD() * 1e6));
    }

    // USDT has 6 decimals
    function estimatedPrice(uint256 noOfTokens) public view returns (uint256) {
        return tokenPriceInUSDT * noOfTokens;
