pragma solidity ^0.8.0;

struct Operation {
    address contractAddress;
    bytes4 functionSelector;
}

struct Operations {
    Operation[] operations;
}

/// @title An operations whitelist
/// @author Tomas A. Puricelli
/// @notice You can use this contract to manage the whitelisting of operations
contract AllowedOperations {
    /// @notice An event emitted when an operation is added to the whitelist
    /// @param operation the operation added
    event OperationAdded(Operation operation);
    /// @notice An event emitted when an operation is removed from the whitelist
    /// @param operation the operation removed
    event OperationRemoved(Operation operation);

    bytes32 public constant OPERATION_SCHEMA_HASH =
        keccak256(
            abi.encodePacked(
                "Operation(address contractAddress,bytes4 functionSelector)"
            )
        );

    bytes32 public constant OPERATIONS_SCHEMA_HASH =
        keccak256(
            abi.encodePacked(
                "Operations(Operation[] operations)Operation(address contractAddress,bytes4 functionSelector)"
            )
        );

    mapping(bytes32 => bool) internal allowedOperations;

    /// @notice Checks if an operation is allowed
    /// @param operationHash the hash of the operation
    function _isAllowed(bytes32 operationHash) internal view returns (bool) {
        return allowedOperations[operationHash];
    }

    /// @notice Adds an operation to the whitelist
    /// @param operationHash the hash of the operation
    /// @param operation the operation to add
    function _addAllowedOperation(
        bytes32 operationHash,
        Operation memory operation
    ) internal {
        if (!allowedOperations[operationHash]) {
            allowedOperations[operationHash] = true;
            emit OperationAdded(operation);
        }
    }

    /// @notice Removes an operation from the whitelist
    /// @param operationHash the hash of the operation
    /// @param operation the operation to remove
    function _removeAllowedOperation(
        bytes32 operationHash,
        Operation memory operation
    ) internal {
        if (allowedOperations[operationHash]) {
            allowedOperations[operationHash] = false;
            emit OperationRemoved(operation);
        }
    }
}
