      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract GemSwapper is Ownable {
    IERC20 public constant gemToken = IERC20(0x0447D3454b25935eeD47F65b4Bd22b9b23bE326a);
    IERC20 public constant iGemToken = IERC20(0x5b25614814E5123ffE188C4421a34c5Fbd8f0c48);

    bool public allowSwap;

    constructor() Ownable(msg.sender) {
        _transferOwnership(msg.sender);
    }

    function swapGems() external {
        require(allowSwap, "GemSwapper: swapping is not allowed");
        uint256 balance = iGemToken.balanceOf(msg.sender);
        iGemToken.transferFrom(msg.sender, address(this), balance);
        gemToken.transfer(msg.sender, balance);
    }

    function toggleSwap(bool _allowSwap) external {
        allowSwap = _allowSwap;
    }

    function withdrawGems(uint256 _amount) external onlyOwner {
        gemToken.transfer(msg.sender, _amount);
    }
}

