// File: contracts/LowGas.sol

pragma solidity >=0.4.0 <0.9.0;

contract KadenaMiningClub is ERC721, Ownable {
    using Strings for uint256;
    using Counters for Counters.Counter;

    Counters.Counter private kadenaSupply;
    Counters.Counter private ethSupply;
    bytes32 public root;

    string public uriPrefix = "";
    string public uriSuffix = ".json";
    string public hiddenMetadataUri;

    uint256 public cost = 0.046 ether;
    uint256 public costInUSDT = 85 * 10**6;
    uint256 public maxKadenaSupply = 8500;
    uint256 public maxMintAmountPerTx = 600;

    bool public paused = false;
    bool public revealed = false;
    bool public allowListOnly = false;

    IERC20 public usdt; 

    constructor() ERC721("Kadena Mining Club", "KMC") {
        setHiddenMetadataUri("ipfs://Qmd9JS8kB94swABnpF3ReqEwDfGhYuo7Kx7ZBvMxJCFPB4/");
        // usdt = IERC20(0xf0034B35bA8eE706D57ABe8818b0efbd6A30b2ED); //sepolia USDT contract address
        usdt = IERC20(0xdAC17F958D2ee523a2206206994597C13D831ec7); // mainnet USDT contract address
    }

    modifier mintCompliance(uint256 _mintAmount) {
        require(
            _mintAmount > 0 && _mintAmount <= maxMintAmountPerTx,
            "Invalid mint amount!"
        );
        require(
            kadenaSupply.current() + _mintAmount <= maxKadenaSupply,
            "Max kadenaSupply exceeded!"
        );
        _;
    }

    function totalSupply() public view returns (uint256) {
        return kadenaSupply.current();
    }

    function totalEthSupply() public view returns (uint256) {
        return ethSupply.current();
    }

    function mintWithETH(
        uint256 _mintAmount,
        string memory kadenaAddress
    ) public payable mintCompliance(_mintAmount) {
        require(!paused, "The contract is paused!");
        require(msg.value >= cost * _mintAmount, "Insufficient funds!");
        _mintLoop(msg.sender, _mintAmount, kadenaAddress);
    }

    function mintWithUSDT(
        uint256 _mintAmount,
        string memory kadenaAddress
    ) public mintCompliance(_mintAmount) {
        require(!paused, "The contract is paused!");
        uint256 totalCost = costInUSDT * _mintAmount;
        require(usdt.balanceOf(msg.sender) >= totalCost, "Insufficient funds!");
        require(usdt.transferFrom(msg.sender, address(this), totalCost), "Token transfer failed!");
        _mintLoop(msg.sender, _mintAmount, kadenaAddress);
    }


    function mintForAddress(
        uint256 _mintAmount,
        address _receiver,
        string memory kadenaAddress
    ) public mintCompliance(_mintAmount) onlyOwner {
        _mintLoop(_receiver, _mintAmount, kadenaAddress);
    }

    function walletOfOwner(
        address _owner
    ) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory ownedTokenIds = new uint256[](ownerTokenCount);
        uint256 currentTokenId = 1;
        uint256 ownedTokenIndex = 0;

        while (
            ownedTokenIndex < ownerTokenCount &&
            currentTokenId <= maxKadenaSupply
        ) {
            address currentTokenOwner = ownerOf(currentTokenId);
            if (currentTokenOwner == _owner) {
                ownedTokenIds[ownedTokenIndex] = currentTokenId;
                ownedTokenIndex++;
            }
            currentTokenId++;
        }
        return ownedTokenIds;
    }

    function tokenURI(
        uint256 _tokenId
    ) public view virtual override returns (string memory) {
        require(
            _exists(_tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );

        if (revealed == false) {
            return hiddenMetadataUri;
        }

        string memory currentBaseURI = _baseURI();
        return
            bytes(currentBaseURI).length > 0
                ? string(
                    abi.encodePacked(
                        currentBaseURI,
                        _tokenId.toString(),
                        uriSuffix
                    )
                )
                : "";
    }

    function setRevealed(bool _state) public onlyOwner {
        revealed = _state;
    }

    function setCost(uint256 _cost) public onlyOwner {
        cost = _cost;
    }

    function setCostInUSDT(uint256 _costInUSDT) public onlyOwner {
        costInUSDT = _costInUSDT;
    }

    function setUSDTAddress(address usdtAddress) public onlyOwner {
        usdt =  IERC20(usdtAddress);
    }

    function setMaxMintAmountPerTx(
        uint256 _maxMintAmountPerTx
    ) public onlyOwner {
        maxMintAmountPerTx = _maxMintAmountPerTx;
    }

    function setHiddenMetadataUri(
        string memory _hiddenMetadataUri
    ) public onlyOwner {
        hiddenMetadataUri = _hiddenMetadataUri;
    }

    function setUriPrefix(string memory _uriPrefix) public onlyOwner {
        uriPrefix = _uriPrefix;
    }

    function setUriSuffix(string memory _uriSuffix) public onlyOwner {
        uriSuffix = _uriSuffix;
    }

    function setPaused(bool _state) public onlyOwner {
        paused = _state;
    }

    function setAllowListOnly(bool _state) public onlyOwner {
        allowListOnly = _state;
    }

    function setroot(bytes32 _newRoot) public onlyOwner {
        root = _newRoot;
    }

    function withdraw() public onlyOwner {
        // Withdraw ETH
        uint256 ethBalance = address(this).balance;
        if (ethBalance > 0) {
            (bool os, ) = payable(owner()).call{value: ethBalance}("");
            require(os, "ETH withdrawal failed!");
        }

        // Withdraw USDT
        uint256 usdtBalance = usdt.balanceOf(address(this));
        if (usdtBalance > 0) {
            require(usdt.transfer(owner(), usdtBalance), "USDT withdrawal failed!");
        }
    }

    function _mintLoop(
        address _receiver,
        uint256 _mintAmount,
        string memory kadenaAddress
    ) internal {
        for (uint256 i = 0; i < _mintAmount; i++) {
            kadenaSupply.increment();
        }
        ethSupply.increment();
        _safeMint(_receiver, ethSupply.current(), kadenaAddress, _mintAmount);
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return uriPrefix;
    }
}
