    "contracts/Presale.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
contract Presale is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    AggregatorV3Interface internal priceFeedETH;
    AggregatorV3Interface internal priceFeedUsdt;

    IERC20 public DthToken; // The token being sold
    IERC20 public UsdtToken; // The token being sold
    IERC20 public EthToken; // The token being sold

  uint256 public constant DTH_PER_USD = 40;

mapping(address => uint256) private totalUsdtContributions;
    mapping(address => uint256) private totalEthContributions;

    bool public presaleStart; // Ön satışın başlangıç zamanı
    bool public presaleEnd;

    bool public whitelistEnabled = true;
    mapping(address => uint256) private contributions;
    mapping(address => uint256) private ethcontributions;
    mapping(address => bool) private whitelisted;

    address[] private whitelistedAddresses;
    address[] private participants;
    address[] private ethparticipants;

    uint256 private totalContributionsUsdt;
    uint256 private totalContributionsEth;

    event EthPurchase(address indexed buyer, uint256 amount);
    event UsdtPurchase(address indexed buyer, uint256 amount);
    event RewardClaimed(address indexed claimer, uint256 amount);

  


  // Mapping to keep track of the user's last claim time
mapping(address => uint256) public lastClaimTime;
mapping(address => uint256) public claimCount;
// Add a state variable to track the divided amount
mapping(address => uint256) private dividedAmount;

// Mapping to keep track of the user's claimed tokens
mapping(address => uint256) public claimedTokens;



    constructor(
    address _dthToken,
    address _usdtToken,
    address _ethToken,
    address _priceFeedETH,
    address _priceFeedUsdt
    
) {
    DthToken = IERC20(_dthToken);
    UsdtToken = IERC20(_usdtToken);
    EthToken = IERC20(_ethToken);
    priceFeedETH = AggregatorV3Interface(_priceFeedETH); // Address of the Chainlink Price Feed contract for Token1
    priceFeedUsdt = AggregatorV3Interface(_priceFeedUsdt);
    
}


function calculateDthReward(address user) public view returns (uint256) {
    uint256 totalContributionUsd = getUserTotalContributionInUsd(user);
    return totalContributionUsd.mul(DTH_PER_USD);
}

       function getLatestUsdtPrice() public view returns (uint256) {
    (, int256 price,,,) = priceFeedUsdt.latestRoundData();
    return uint256(price);
}


function getUserTotalContributionInUsd(address user) public view returns (uint256) {
    uint256 usdtContribution = contributions[user];
    uint256 ethContribution = ethcontributions[user];

    uint256 usdtInUsd = usdtContribution.mul(getLatestUsdtPrice()).div(1e6); // USDT contributions converted to USD
    uint256 ethInUsd = ethContribution.mul(getLatestEthPrice()).div(1e18); // ETH contributions converted to USD

    return usdtInUsd.add(ethInUsd); // Total contributions in USD
}

function getTotalContributionsInUsd() public view returns (uint256) {
    uint256 totalEthInUsd = totalContributionsEth.mul(getLatestEthPrice()).div(1e18); // ETH contributions converted to USD
    uint256 totalUsdtInUsd = totalContributionsUsdt.mul(getLatestUsdtPrice()).div(1e6); // USDT contributions converted to USD
    return totalEthInUsd.add(totalUsdtInUsd); // Total contributions in USD
}

function isUserWhitelisted(address user) public view returns (bool) {
    return whitelisted[user];
}

function buyTokenWithUsdt(uint256 _amount) public nonReentrant {
    require(presaleStart == true, "Presale has not started yet");
    require(presaleEnd == false, "Presale has ended");
    require(whitelistEnabled == false || whitelisted[msg.sender] == true, "You are not whitelisted");
    require(_amount > 0, "You need to send some USDT");
    require(_amount <= UsdtToken.allowance(msg.sender, address(this)), "You need to allow the contract to spend the USDT you are sending");

    // Get the latest USDT price
    uint256 usdtUsdPrice = getLatestUsdtPrice();

    // Calculate the value of the transaction in USD
    uint256 transactionValueUsd = _amount.mul(usdtUsdPrice).div(1e14); // Adjust for USDT's 6 decimal places and Chainlink's 8 decimal places

    // Check if the total contribution after this transaction would exceed the max limit
    require(totalUsdtContributions[msg.sender].add(transactionValueUsd) <= 2000, "Cannot purchase more than $2000 worth of USDT");

    UsdtToken.safeTransferFrom(msg.sender, address(this), _amount);

    if (contributions[msg.sender] == 0) {
        participants.push(msg.sender);
    }

    contributions[msg.sender] = contributions[msg.sender].add(_amount);
    totalContributionsUsdt += _amount;
    totalUsdtContributions[msg.sender] = totalUsdtContributions[msg.sender].add(transactionValueUsd);

    emit UsdtPurchase(msg.sender, _amount);
}

 

function calculateDthUsdt(address participant) public view returns (uint256) {
    uint256 contribution = contributions[participant];
    uint256 reward = contribution.mul(40).mul(10**12);
    return reward;
}




function buyTokenWithEth() public payable nonReentrant {
    require(presaleStart == true, "Presale has not started yet");
    require(presaleEnd == false, "Presale has ended");
    require(whitelistEnabled == false || whitelisted[msg.sender] == true, "You are not whitelisted");
    require(msg.value > 0, "You need to send some ETH");

    // Get the latest ETH price
    uint256 ethPrice = getLatestEthPrice();

    // Calculate the value of the transaction in USD
    uint256 transactionValueUsd = (msg.value.mul(ethPrice)).div(1e26); // Adjusted for Chainlink's 8 decimal places

    // Check if the total contribution after this transaction would exceed the max limit
    require(totalEthContributions[msg.sender].add(transactionValueUsd) <= 2000, "Cannot purchase more than $2000 worth of ETH");

    if (ethcontributions[msg.sender] == 0) {
        ethparticipants.push(msg.sender);
    }

    ethcontributions[msg.sender] = ethcontributions[msg.sender].add(msg.value);
    totalContributionsEth += msg.value;
    totalEthContributions[msg.sender] = totalEthContributions[msg.sender].add(transactionValueUsd);

    emit EthPurchase(msg.sender, msg.value);
}

function getUserContributionInEth(address user) public view returns (uint256) {
    return ethcontributions[user];
}
    
function calculateDthForParticipant(address participant) public view returns (uint256) {
    uint256 ethContributionWei = ethcontributions[participant]; // Get the ETH contribution of the participant in Wei
    uint256 ethPriceWei = getLatestEthPrice(); // Get the latest ETH price in Wei
    uint256 usdContributionWei = ethContributionWei.mul(ethPriceWei); // Convert the ETH contribution to USD (in Wei)
    uint256 dthRewardWei = usdContributionWei.mul(40); // Calculate the DTH reward (in Wei)
    uint256 dthReward = dthRewardWei.div(1e8);
    return dthReward;
}

function calculateTotalDthForParticipant(address participant) public view returns (uint256) {
    uint256 dthRewardEth = calculateDthForParticipant(participant);
    uint256 dthRewardUsdt = calculateDthUsdt(participant);
    uint256 totalDthReward = dthRewardEth.add(dthRewardUsdt);
    return totalDthReward;
}



function claimDthReward() external nonReentrant {
        require(presaleEnd == true, "Presale has not ended yet");
        require(contributions[msg.sender] > 0 || ethcontributions[msg.sender] > 0, "You have no DTH to claim");

        uint256 dthReward = calculateTotalDthForParticipant(msg.sender);
        require(dthReward > 0, "You have no DTH to claim");

        uint256 currentTime = block.timestamp;
        uint256 timeSinceLastClaim = currentTime.sub(lastClaimTime[msg.sender]);

        uint256 claimableAmount;
        if (lastClaimTime[msg.sender] == 0) {
            // This is the user's first claim, they can claim 50% of their tokens
            claimableAmount = dthReward.div(2);
            dividedAmount[msg.sender] = claimableAmount.div(5); // Calculate divided amount here for the next claims
            claimCount[msg.sender] = 1; // Initialize claim count here
        } else if (timeSinceLastClaim >= 2592000 && claimCount[msg.sender] <= 5) { // 1 month = 2592000 seconds
            // The user can claim 1/5 of their remaining tokens every 1 month
            claimableAmount = dividedAmount[msg.sender];
            claimCount[msg.sender] = claimCount[msg.sender].add(1); // Increment claim count here
        } else {
            // The user cannot claim tokens yet or has already claimed 5 times
            revert("You cannot claim tokens yet or you have already claimed 5 times");
        }
        // Update the user's last claim time and claimed tokens amount
        lastClaimTime[msg.sender] = currentTime;
        claimedTokens[msg.sender] = claimedTokens[msg.sender].add(claimableAmount);

        // Transfer the claimable tokens to the user
        DthToken.safeTransfer(msg.sender, claimableAmount);

        emit RewardClaimed(msg.sender, claimableAmount);
    }

 function getRemainingDthForUser(address user) public view returns (uint256) {
        uint256 totalDth = calculateTotalDthForParticipant(user);
        uint256 claimedDth = claimedTokens[user];

        if (totalDth >= claimedDth) {
            return totalDth.sub(claimedDth);
        } else {
            return 0;
        }
    }


function getLatestEthPrice() public view returns (uint256) {
    (,int price,,,) = priceFeedETH.latestRoundData();
    return uint256(price);
}

function setPresaleStart(bool _presaleStart) public onlyOwner {
        
            presaleStart = _presaleStart;
        }
          
    
    function setpresaleEnd(bool _presaleEnd) public onlyOwner {
        presaleEnd = _presaleEnd;
    }

    function getTotalUsdtInContract() public view returns (uint256) {
    return UsdtToken.balanceOf(address(this));
}
function getTotalEthInContract() public view returns (uint256) {
    return address(this).balance;
}

function getTotalEthInContractInUsd() public view returns (uint256) {
    uint256 totalEthInWei = address(this).balance;
    uint256 totalEth = totalEthInWei ; // Convert Wei to Ether.
    uint256 ethPriceInUsd = getLatestEthPrice();
    return totalEth * ethPriceInUsd; // Convert Ether to USD.
}

function withdrawUsdt(uint256 _amount) external onlyOwner nonReentrant {
    uint256 contractBalance = UsdtToken.balanceOf(address(this));
    require(contractBalance >= _amount, "Not enough USDT in the contract to withdraw");
    UsdtToken.safeTransfer(msg.sender, _amount);
}
function withdrawEth(uint256 _amount) external onlyOwner nonReentrant {
    uint256 contractBalance = address(this).balance;
    require(contractBalance >= _amount, "Not enough ETH in the contract to withdraw");
    payable(msg.sender).transfer(_amount);
}

function setWhitelistStatuses(address[] calldata _participants) external onlyOwner nonReentrant {
    require(_participants.length <= 200, "Can't whitelist more than 100 addresses at a time");
    for (uint i = 0; i < _participants.length; i++) {
        if (!whitelisted[_participants[i]]) {
            whitelisted[_participants[i]] = true;
            whitelistedAddresses.push(_participants[i]);
        }
    }
}
function setWhitelistStatus(bool _status) public onlyOwner {
    whitelistEnabled = _status;
}

function isWhitelisted(address _address) public view returns (bool) {
    return whitelisted[_address];
}

function getWhitelistedAddresses() public view returns (address[] memory) {
    return whitelistedAddresses;
}



}

    
 
 




    "
