    "contracts/BlueChipGenesis2ndSale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "./interface/IBlueChipGenesis.sol";
import "hardhat/console.sol";

error InvalidMerkleProof();
error NotEnoughFunds(uint256 balance);
error NoMintFromContract();
error ExceedsAllocation();
error WrongMintAmount();
error MaxSupplyOver();
error SaleClosed();
error InvalidPhase();

contract BlueChipGenesis2ndSale is AccessControl {
    event PhaseChanged(uint8 phase);

    struct Saledata {
        bytes32 merkleRoot;
        uint256 mintCost;
    }

    Saledata[4] public saleData;

    mapping(uint8 => uint256[]) public fullMintFlag;
    mapping(uint8 => mapping(address => uint256)) public mintCount;

    // 1st+2ndセールでの合計
    uint256 public constant MAX_SUPPLY = 9300;
    uint256 private constant MAX_INT =
        0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    IBlueChipGenesis public blueChipGenesis;
    address public withdrawAddress = 0x4f4823F3639DdCC2B14093a28802E214C7C28D03;

    bytes32 internal constant ADMIN = keccak256("ADMIN");

    uint8 public phase = 4;

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN, msg.sender);
        _grantRole(ADMIN, 0xDFF35863CD9986E2d4271C0aCa86a235A1774e24);

        saleData[1].mintCost = 0.003 ether;
        saleData[2].mintCost = 0.005 ether;
        saleData[3].mintCost = 0.005 ether;

        fullMintFlag[1] = [MAX_INT];
        fullMintFlag[2] = [MAX_INT];
        fullMintFlag[3] = [MAX_INT];
    }

    /**
     * セール用ミント関数
     */
    /// @dev マークルツリーによるALミント関数（フルミント用）
    function allowListFullMint(
        uint256 ticketNumber,
        uint256 allocated,
        bytes32[] calldata merkleProof,
        address receiver
    ) external payable {
        /**
         * @notice
         *  Piementからのクレカ払いミント : msg.senderはPiement、ミント先はreceiver指定
         *  ミントサイトからの直接ミント : receiverを指定せず(ゼロアドレス)、ミント先はmsg.sender
         */
        address _receiver = receiver != address(0) ? receiver : msg.sender;

        // コントラクトからのミントガード
        if (tx.origin != msg.sender) revert NoMintFromContract();

        // セールフェーズのチェック
        if (phase > 3) revert SaleClosed();

        //　部分ミント済みの場合はリバート
        if (mintCount[phase][_receiver] > 0) revert ExceedsAllocation();

        // merkleProofのチェック
        bytes32 leaf = keccak256(
            abi.encodePacked(
                phase,
                "|",
                ticketNumber,
                "|",
                _receiver,
                "|",
                allocated
            )
        );

        if (
            !MerkleProof.verifyCalldata(
                merkleProof,
                saleData[phase].merkleRoot,
                leaf
            )
        ) revert InvalidMerkleProof();

        // ミント代金のチェック
        if (msg.value < allocated * saleData[phase].mintCost)
            revert NotEnoughFunds(msg.value);

        // ミントによりMAX SUPPLYを超えないかチェック
        if (allocated + totalSupply() > MAX_SUPPLY) revert MaxSupplyOver();

        // フルミント済みかどうかのチェックと登録
        claimTicketOrBlockTransaction(ticketNumber);

        // ミント
        blueChipGenesis.mint(_receiver, allocated);
    }

    /// @dev マークルツリーによるALミント関数（部分ミント用）
    function allowListPartialMint(
        uint256 ticketNumber,
        uint256 allocated,
        bytes32[] calldata merkleProof,
        uint256 _mintAmount,
        address receiver
    ) external payable {
        /**
         * @notice
         *  Piementからのクレカ払いミント : msg.senderはPiement、ミント先はreceiver指定
         *  ミントサイトからの直接ミント : receiverを指定せず(ゼロアドレス)、ミント先はmsg.sender
         */
        address _receiver = receiver != address(0) ? receiver : msg.sender;

        // コントラクトからのミントガード
        if (tx.origin != msg.sender) revert NoMintFromContract();

        // セールフェーズのチェック
        if (phase > 3) revert SaleClosed();

        // フルミント済みの場合はリバート
        if (getStoredBit(phase, ticketNumber) != 1) revert ExceedsAllocation();

        // merkleProofのチェック
        bytes32 leaf = keccak256(
            abi.encodePacked(
                phase,
                "|",
                ticketNumber,
                "|",
                _receiver,
                "|",
                allocated
            )
        );
        if (
            !MerkleProof.verifyCalldata(
                merkleProof,
                saleData[phase].merkleRoot,
                leaf
            )
        ) revert InvalidMerkleProof();

        // ミント代金のチェック
        if (msg.value < _mintAmount * saleData[phase].mintCost)
            revert NotEnoughFunds(msg.value);

        // ミントによりMAX SUPPLYを超えないかチェック
        if (_mintAmount + totalSupply() > MAX_SUPPLY) revert MaxSupplyOver();

        // ミント数の保有枠上限チェック
        if (mintCount[phase][_receiver] + _mintAmount > allocated)
            revert ExceedsAllocation();

        // ミント数済み数加算
        unchecked {
            mintCount[phase][_receiver] += _mintAmount;
        }

        // ミント
        blueChipGenesis.mint(_receiver, _mintAmount);
    }

    // ウォレット番号（ticket）ごとにミント済フラグを管理する関数
    function claimTicketOrBlockTransaction(uint256 _ticketNumber) private {
        require(_ticketNumber < fullMintFlag[phase].length * 256, "bad ticket");

        uint256 storageOffset;
        uint256 offsetWithin256;
        uint256 localGroup;
        uint256 storedBit;

        unchecked {
            storageOffset = _ticketNumber / 256;
            offsetWithin256 = _ticketNumber % 256;
        }
        localGroup = fullMintFlag[phase][storageOffset];
        storedBit = (localGroup >> offsetWithin256) & uint256(1);
        // ミント済みの場合はリバート
        if (storedBit != 1) revert ExceedsAllocation();
        // 未ミントの場合はフラグを立てる
        localGroup = localGroup & ~(uint256(1) << offsetWithin256);
        fullMintFlag[phase][storageOffset] = localGroup;
    }

    /// @dev ミント済フラグのgetter関数
    function getStoredBit(
        uint8 _phase,
        uint256 _ticketNumber
    ) public view returns (uint256) {
        require(
            _ticketNumber < fullMintFlag[_phase].length * 256,
            "bad ticket"
        );

        uint256 storageOffset;
        uint256 offsetWithin256;
        uint256 localGroup;
        uint256 storedBit;

        unchecked {
            storageOffset = _ticketNumber / 256;
            offsetWithin256 = _ticketNumber % 256;
        }

        localGroup = fullMintFlag[_phase][storageOffset];
        storedBit = (localGroup >> offsetWithin256) & uint256(1);

        return storedBit;
    }

    /**
     * withdraw関数
     */
    /// @dev 引出し先アドレスのsetter関数
    function setWithdrawAddress(
        address _withdrawAddress
    ) external onlyRole(ADMIN) {
        withdrawAddress = _withdrawAddress;
    }

    /// @dev 引出し用関数
    function withdraw() external payable onlyRole(ADMIN) {
        require(withdrawAddress != address(0), "withdrawAddress can't be 0");
        (bool sent, ) = payable(withdrawAddress).call{
            value: address(this).balance
        }("");
        require(sent, "failed to withdraw");
    }

    /**
     * ADMIN用 setter関数
     */
    /// @dev 親コントラクトのsetter
    function setBlueChipGenesis(
        address _contractAddress
    ) external onlyRole(ADMIN) {
        blueChipGenesis = IBlueChipGenesis(_contractAddress);
    }

    /// @dev マークルルートのsetter
    function setMerkleRoot(
        uint8 _phase,
        bytes32 _merkleRoot
    ) external onlyRole(ADMIN) {
        saleData[_phase].merkleRoot = _merkleRoot;
    }

    /// @dev セールフェーズのsetter
    function setPhase(uint8 _phase) external onlyRole(ADMIN) {
        if (_phase < 1) revert InvalidPhase();
        phase = _phase;
        emit PhaseChanged(_phase);
    }

    /**
     * その他の関数
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControl) returns (bool) {
        return (AccessControl.supportsInterface(interfaceId) ||
            super.supportsInterface(interfaceId));
    }

    function totalSupply() public view returns (uint256) {
        return blueChipGenesis.totalSupply();
    }
}
"
    },
    "contracts/interface/IBlueChipGenesis.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

interface IBlueChipGenesis {
    function mint(address _to, uint256 _amount) external;

    //ERC721Psi
    function totalSupply() external view returns (uint256);
}
"
