// File contracts/TEH.sol


pragma solidity ^0.8.0;

contract TEH is Context, Ownable, ERC20, ReentrancyGuard {
    using SafeMath for uint256;
    using Address for address;
    using SafeERC20 for IERC20;

    address public adminAddress;

    mapping(address => bool) public blacklists;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isExcludedFromMaxWalletAmount;

    IUniswapV2Router02 public uniswapV2Router;
    IERC721 public nft;
    address public uniswapV2PairAddress;

    uint256 public txFee = 3;
    uint256 public buyTxFee = 3;
    uint256 public sellTxFee = 3;

    address public txFeeAddress;

    uint256 public swapFeeTokensAtAmount = 4625000 * 10 ** decimals();
    uint256 public maxWalletAmount = 13875000 * 10 ** decimals();
    uint256 public maxTxAmount = 9250000 * 10 ** decimals();

    bool public escapeTradingRestriction = false;
    bool public tradingOpen = false;
    bool public feeOnTrades = true;

    // Error Codes
    error BlacklistedAddress(address from, address to);
    error InvalidNFTAddress(address _address);
    error TradingNotOpen();
    error AdminOnly();
    error ZeroAddressNotAllowed();
    error InvalidTxFees(uint256 _txFee, uint256 _buyTxFee, uint256 _sellTxFee);
    error InvalidMaxWalletAmount(uint256 _maxWalletAmount);
    error InvalidMaxTxAmount(uint256 _maxTxAmount);

    // Events
    event TradingOpen(bool tradingOpen);
    event Blacklist(address _address, bool _blacklist);
    event ExcludeFromFee(address _address, bool _exclude);
    event ExcludeFromMaxWalletAmount(address _address, bool _exclude);
    event SetTxFees(uint256 _txFee, uint256 _buyTxFee, uint256 _sellTxFee);
    event SetTxFeeAddr(address _txFeeAddr);
    event SetNftAddr(address _nftAddr);
    event SetEscapeTradingRestriction(bool _escapeTradingRestriction);
    event SetRouterAndPairAddress(
        IUniswapV2Router02 _uniswapV2Router,
        address _uniswapV2PairAddress
    );
    event SetSwapFeeTokensAtAmount(uint256 _swapFeeTokensAtAmount);
    event SetAdminAddress(address _adminAddress);
    event SetFeeOnTrades(bool _feeOnTrades);
    event SetMaxWalletAmount(uint256 _maxWalletAmount);
    event SetMaxTxAmount(uint256 _maxTxAmount);
    event Withdraw(address _tokenAddress);

    constructor(
        IUniswapV2Router02 _uniswapV2Router,
        address _admin
    ) ERC20("TEH Market", "TEH") {
        txFeeAddress = owner();
        adminAddress = _admin;

        uniswapV2Router = _uniswapV2Router;

        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        _isExcludedFromMaxWalletAmount[owner()] = true;
        _isExcludedFromMaxWalletAmount[address(this)] = true;

        _mint(_msgSender(), 925000000 * 10 ** decimals()); // 925,000,000 TEH
    }

    receive() external payable {}

    modifier tradingPeriod(address from, address to) {
        if (
            from == owner() &&
            uniswapV2PairAddress == address(0) &&
            Address.isContract(to)
        ) {
            if (IUniswapV2Pair(to).totalSupply() == 0) {
                uniswapV2PairAddress = to;
                _isExcludedFromMaxWalletAmount[to] = true;

                _;
                return;
            }
        }

        if (escapeTradingRestriction) {
            _;
            return;
        }

        if (to == uniswapV2PairAddress || from == uniswapV2PairAddress) {
            if (!tradingOpen) {
                revert TradingNotOpen();
            }
        }
        _;
    }

    modifier onlyAdmin() {
        if (_msgSender() == adminAddress || _msgSender() == owner()) {
            _;
            return;
        }

        revert AdminOnly();
    }

    function setTradingOpen(bool _tradingOpen) external onlyAdmin {
        tradingOpen = _tradingOpen;

        emit TradingOpen(_tradingOpen);
    }

    function blacklist(address _address, bool _blacklist) external onlyOwner {
        blacklists[_address] = _blacklist;

        emit Blacklist(_address, _blacklist);
    }

    function excludeFromFee(
        address _address,
        bool _exclude
    ) external onlyOwner {
        _isExcludedFromFee[_address] = _exclude;

        emit ExcludeFromFee(_address, _exclude);
    }

    function excludeFromMaxWalletAmount(
        address _address,
        bool _exclude
    ) external onlyOwner {
        _isExcludedFromMaxWalletAmount[_address] = _exclude;

        emit ExcludeFromMaxWalletAmount(_address, _exclude);
    }

    function setTxFees(
        uint256 _txFee,
        uint256 _buyTxFee,
        uint256 _sellTxFee
    ) external onlyOwner {
        if (_txFee > 100 || _buyTxFee > 100 || _sellTxFee > 100) {
            revert InvalidTxFees(_txFee, _buyTxFee, _sellTxFee);
        }

        txFee = _txFee;
        buyTxFee = _buyTxFee;
        sellTxFee = _sellTxFee;

        emit SetTxFees(_txFee, _buyTxFee, _sellTxFee);
    }

    function setTxFeeAddr(address _txFeeAddr) external onlyOwner {
        _isExcludedFromFee[_txFeeAddr] = true;
        _isExcludedFromMaxWalletAmount[_txFeeAddr] = true;

        txFeeAddress = _txFeeAddr;

        emit SetTxFeeAddr(_txFeeAddr);
    }

    function setNftAddr(address _nftAddr) external onlyOwner {
        if (IERC721(_nftAddr).supportsInterface(0x80ac58cd) != true) {
            revert InvalidNFTAddress(_nftAddr);
        }

        nft = IERC721(_nftAddr);

        emit SetNftAddr(_nftAddr);
    }

    function setEscapeTradingRestriction(
        bool _escapeTradingRestriction
    ) external onlyOwner {
        escapeTradingRestriction = _escapeTradingRestriction;

        emit SetEscapeTradingRestriction(_escapeTradingRestriction);
    }

    function setRouterAndPairAddress(
        IUniswapV2Router02 _uniswapV2Router,
        address _uniswapV2PairAddress
    ) external onlyOwner {
        uniswapV2Router = _uniswapV2Router;
        uniswapV2PairAddress = _uniswapV2PairAddress;

        _isExcludedFromMaxWalletAmount[_uniswapV2PairAddress] = true;

        emit SetRouterAndPairAddress(_uniswapV2Router, _uniswapV2PairAddress);
    }

    function setSwapFeeTokensAtAmount(
        uint256 _swapFeeTokensAtAmount
    ) external onlyOwner {
        swapFeeTokensAtAmount = _swapFeeTokensAtAmount;

        emit SetSwapFeeTokensAtAmount(_swapFeeTokensAtAmount);
    }

    function setAdminAddress(address _adminAddress) external onlyOwner {
        if (_adminAddress == address(0)) {
            revert ZeroAddressNotAllowed();
        }

        adminAddress = _adminAddress;

        emit SetAdminAddress(_adminAddress);
    }

    function setFeeOnTrades(bool _feeOnTrades) external onlyOwner {
        feeOnTrades = _feeOnTrades;

        emit SetFeeOnTrades(_feeOnTrades);
    }

    function setMaxWalletAmount(uint256 _maxWalletAmount) external onlyOwner {
        maxWalletAmount = _maxWalletAmount;

        emit SetMaxWalletAmount(_maxWalletAmount);
    }

    function setMaxTxAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTxAmount = _maxTxAmount;

        emit SetMaxTxAmount(_maxTxAmount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override tradingPeriod(from, to) {
        if (blacklists[to] || blacklists[from]) {
            revert BlacklistedAddress(from, to);
        }

        if (!_isExcludedFromMaxWalletAmount[to]) {
            if (amount > maxTxAmount) {
                revert InvalidMaxTxAmount(amount);
            }

            if (balanceOf(to).add(amount) > maxWalletAmount) {
                revert InvalidMaxWalletAmount(amount);
            }
        }

        bool isSell = to == uniswapV2PairAddress;
        bool isBuy = from == uniswapV2PairAddress;

        bool isIgnoredAddress = _isExcludedFromFee[from] ||
            _isExcludedFromFee[to];

        if (!isIgnoredAddress && address(nft) != address(0)) {
            if (nft.balanceOf(from) > 0 || nft.balanceOf(to) > 0) {
                isIgnoredAddress = true;
            }
        }

        uint256 feeAmount = 0;
        uint256 _txFee = _getTxFee(isBuy, isSell);

        if (!isIgnoredAddress) {
            feeAmount = amount.mul(_txFee).div(100);
            super._transfer(from, address(this), feeAmount);

            bool isTrading = tradingOpen || escapeTradingRestriction;

            if (
                !isBuy &&
                isTrading &&
                balanceOf(address(this)) >= swapFeeTokensAtAmount
            ) {
                _takeFeeAndSwap(balanceOf(address(this)));
            }
        }

        super._transfer(from, to, amount.sub(feeAmount));
    }

    function _getTxFee(
        bool isBuy,
        bool isSell
    ) internal view returns (uint256) {
        uint256 _txFee = 0;

        if (isBuy && feeOnTrades) {
            _txFee = buyTxFee;
        } else if (isSell && feeOnTrades) {
            _txFee = sellTxFee;
        } else if (!isBuy && !isSell) {
            _txFee = txFee;
        }

        return _txFee;
    }

    function _takeFeeAndSwap(uint256 feeAmount) internal virtual nonReentrant {
        _swapTokensForEth(feeAmount);
    }

    function _swapTokensForEth(uint256 tokenAmount) internal virtual {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            txFeeAddress,
            block.timestamp
        );
    }

    function withdraw(address _tokenAddress) external onlyOwner returns (bool) {
        if (_tokenAddress == address(0)) {
            payable(owner()).transfer(address(this).balance);

            emit Withdraw(_tokenAddress);

            return true;
        }

        uint256 balance = IERC20(_tokenAddress).balanceOf(address(this));
        IERC20(_tokenAddress).safeTransfer(owner(), balance);

        emit Withdraw(_tokenAddress);

        return true;
    }
}
