// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;
        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    function div(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        return c;
    }

    function max(uint256 a, uint256 b) internal pure returns (uint256) {
        return a > b ? a : b;
    }

    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}

contract NomotaAIStaking {
    // boolean to prevent reentrancy
    bool internal locked;

    // Library usage
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // Contract owner
    address public owner;

    // Timestamp related variables
    // Token amount variables

    mapping(address => uint256) public balances;
    mapping(address => uint256) public timestamps;

    uint256 public apyPerDay;
    uint256 public apyMax;
    uint256 public durationInSeconds;
    uint256 public maxStaked;
    uint256 public totalStaked;

    bool public enabled;

    // ERC20 contract address
    IERC20 public erc20Contract;

    // Events
    event TokensStaked(address from, uint256 amount);
    event TokensUnstaked(address to, uint256 amount, uint256 rewards);

    /// @dev Deploys contract and links the ERC20 token which we are staking, also sets owner as msg.sender
    /// @param _erc20_contract_address.
    constructor(
        IERC20 _erc20_contract_address,
        uint256 _durationInSeconds,
        uint256 _apyPerDay,
        uint256 _apyMax,
        uint256 _maxStaked
    ) {
        owner = msg.sender;
        apyPerDay = _apyPerDay;
        apyMax = _apyMax;
        durationInSeconds = _durationInSeconds;
        maxStaked = _maxStaked;
        totalStaked = 0;
        require(
            address(_erc20_contract_address) != address(0),
            "_erc20_contract_address address can not be zero"
        );
        erc20Contract = _erc20_contract_address;

        locked = false; // Initialize the reentrancy variable to not locked
        enabled = false;
    }

    modifier noReentrant() {
        require(!locked, "No re-entrancy");
        locked = true;
        _;
        locked = false;
    }

    modifier onlyOwner() {
        require(
            msg.sender == owner,
            "Message sender must be the contract's owner."
        );
        _;
    }

    function deposit(uint256 amount) public onlyOwner {
        require(
            msg.sender == owner,
            "Message sender must be the contract's owner."
        );
        require(
            amount <= erc20Contract.balanceOf(msg.sender),
            "Not enough tokens in your wallet, please try a lower amount"
        );

        erc20Contract.safeTransferFrom(msg.sender, address(this), amount);
    }

    function withdraw(uint256 amount) public onlyOwner {
        // maximum to take out makes sure there is enough rewards for current stakers
        uint256 maxRewards = (totalStaked * apyMax) / 1_000_000;
        uint256 maxToWithdraw = erc20Contract.balanceOf(address(this)) -
            maxRewards;
        require(
            maxToWithdraw >= amount,
            "Amount to withdraw is larger than max"
        );

        erc20Contract.safeTransfer(msg.sender, amount);
    }

    function stake(uint256 amount) public noReentrant {
        require(enabled, "Staking is not enabled");
        require(
            amount <= erc20Contract.balanceOf(msg.sender),
            "Not enough tokens in your wallet, please try a lower amount"
        );
        require(totalStaked.add(amount) <= maxStaked, "Staking pool is full");
        require(
            balances[msg.sender] == 0,
            "You already have staked, you can't stake again"
        );
        erc20Contract.safeTransferFrom(msg.sender, address(this), amount);
        balances[msg.sender] = amount;
        timestamps[msg.sender] = block.timestamp; // start counting from now
        totalStaked = totalStaked.add(amount);

        emit TokensStaked(msg.sender, amount);
    }

    function unstake() public noReentrant {
        require(
            balances[msg.sender] > 0,
            "Insufficient token balance - nothing to unstake"
        );
        uint256 rewardsToGive = _rewardsForAddress(msg.sender);
        require(
            _availableRewards() > rewardsToGive,
            "Insufficient rewards available, try again later"
        );

        if (_unstakeable(msg.sender)) {
            // only unstake after time has passed
            uint256 amount = balances[msg.sender];
            balances[msg.sender] = 0;
            timestamps[msg.sender] = 0;
            totalStaked = totalStaked.sub(amount);
            erc20Contract.safeTransfer(msg.sender, amount.add(rewardsToGive));
            emit TokensUnstaked(msg.sender, amount, rewardsToGive);
        } else {
            revert("Tokens are only available after timeperiod has elapsed");
        }
    }

    function unstakeable(address address_) external view returns (bool) {
        return _unstakeable(address_);
    }

    function _unstakeable(address address_) internal view returns (bool) {
        return (block.timestamp >= timestamps[address_].add(durationInSeconds));
    }

    function rewardsForAddress(
        address address_
    ) external view returns (uint256) {
        return _rewardsForAddress(address_);
    }

    function _rewardsForAddress(
        address address_
    ) internal view returns (uint256) {
        uint256 calculatedRewards = ((block.timestamp - timestamps[address_]) *
            balances[address_]) / ((apyPerDay / 100) * 1 days);
        uint256 maxRewards = (balances[address_] * apyMax) / 1_000_000;

        return SafeMath.min(calculatedRewards, maxRewards);
    }

    function availableRewards() external view returns (uint) {
        return _availableRewards();
    }

    function _availableRewards() internal view returns (uint) {
        return erc20Contract.balanceOf(address(this)) - totalStaked;
    }

    function changeMaxStaked(uint256 maxStaked_) public onlyOwner {
        maxStaked = maxStaked_;
    }

    function enable() public onlyOwner {
        enabled = true;
    }

    function disable() public onlyOwner {
        enabled = false;
    }

    /// @dev Transfer accidentally locked ERC20 tokens.
    /// @param token - ERC20 token address.
    /// @param amount of ERC20 tokens to remove.
    function transferAccidentallyLockedTokens(
        IERC20 token,
        uint256 amount
    ) public onlyOwner noReentrant {
        require(address(token) != address(0), "Token address can not be zero");
        // This function can not access the official timelocked tokens; just other random ERC20 tokens that may have been accidently sent here
        require(
            token != erc20Contract,
            "Token address can not be ERC20 address which was passed into the constructor"
        );
        // Transfer the amount of the specified ERC20 tokens, to the owner of this contract
        token.safeTransfer(owner, amount);
    }
}
