contract  TokenSwapAgentImpl is Ownable, Initializable {

    using SafeERC20 for IERC20;
    using Address for address;

    mapping(address => address) public swapMappingCoin2Token;
    mapping(address => address) public swapMappingToken2Coin;
    mapping(bytes32 => bool) public filledCoinTx;

    address public tokenProxyAdmin;
    address public tokenImplementation;
    uint256 public swapFee;

    event SwapPairCreated(bytes32 indexed coinRegisterTxHash, address indexed tokenAddr, address indexed coinAddr, string symbol, string name, uint8 decimals);
    event SwapStarted(address indexed tokenAddr, address indexed coinAddr, address indexed fromAddr, uint256 amount, uint256 feeAmount);
    event SwapFilled(address indexed tokenAddr, bytes32 indexed ethTxHash, address indexed toAddress, uint256 amount);
    event SwapFeeUpdated(uint256 fee);

    constructor() {
    }

    modifier notContract() {
        require(!msg.sender.isContract(), "Contract is not allowed to swap");
        require(msg.sender == tx.origin, "No proxy contract is allowed");
       _;
    }

    function initialize(address _tokenImpl, uint256 _fee, address _tokenProxyAdminAddr) public initializer {
        tokenImplementation = _tokenImpl;
        swapFee = _fee;
        tokenProxyAdmin = _tokenProxyAdminAddr;
    }

    /**
     * @dev Returns set minimum swap fee from BEP20 to ERC20
     */
    function setSwapFee(uint256 _fee) onlyOwner external {
        swapFee = _fee;
        emit SwapFeeUpdated(_fee);
    }

    /**
     * @dev createSwapPair
     */
    function createSwapPair(bytes32 _coinTxHash, address _wfieroAddr, string calldata _name, string calldata _symbol, uint8 _decimals) onlyOwner external returns (address) {
        require(swapMappingCoin2Token[_wfieroAddr] == address(0x0), "Duplicated swap pair");

        BEP20UpgradeableProxy proxyToken = new BEP20UpgradeableProxy(tokenImplementation, tokenProxyAdmin, "");
        IProxyInitialize token = IProxyInitialize(address(proxyToken));
        token.initialize(_name, _symbol, _decimals, 0, true, address(this));

        swapMappingCoin2Token[_wfieroAddr] = address(token);
        swapMappingToken2Coin[address(token)] = _wfieroAddr;

        emit SwapPairCreated(_coinTxHash, address(token), _wfieroAddr, _symbol, _name, _decimals);
        return address(token);
    }

    /**
     * @dev fillETH2BSCSwap 
     */
    function fillCoin2TokenSwap(bytes32 _coinTxHash, address _wfieroAddr, address _toAddress, uint256 _amount) onlyOwner external returns (bool) {
        require(!filledCoinTx[_coinTxHash], "Coin tx filled already");
        address tokenAddr = swapMappingCoin2Token[_wfieroAddr];
        require(tokenAddr != address(0x0), "No swap pair created for this token");
        filledCoinTx[_coinTxHash] = true;

        // IERC20(tokenAddr).safeTransfer(_toAddress, _amount);
        ISwap(tokenAddr).mintTo(_amount, _toAddress);
        emit SwapFilled(tokenAddr, _coinTxHash, _toAddress, _amount);

        return true;
    }
    
    /**
     * @dev swapBSC2ETH
     */
    function swapToken2Coin(address _tokenAddr, uint256 _amount) payable external notContract returns (bool) {
        address coinAddr = swapMappingToken2Coin[_tokenAddr];
        require(coinAddr != address(0x0), "No swap pair created for this token");
        require(msg.value == swapFee, "Swap fee not correct");

        IERC20(_tokenAddr).safeTransferFrom(msg.sender, address(this), _amount);
        ISwap(_tokenAddr).burn(_amount);
        if (msg.value != 0) {
            payable(owner()).transfer(msg.value);
        }

        emit SwapStarted(_tokenAddr, coinAddr, msg.sender, _amount, msg.value);
        return true;
    }

    function failSafe(address _token, address _toAddress, uint256 _amount) external onlyOwner {
        if(_token == address(0)) {
            payable(_toAddress).transfer(address(this).balance);
        } else {
            IERC20(_token).safeTransfer(_toAddress, _amount);
        }
    }
}
