pragma solidity ^0.8.24;

import { PIXL } from "./Pixl.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract MultiTransfer is Ownable {
    address constant STAKING = 0x1C70D0A86475CC707b48aA79F112857e7957274f;
    address constant ECOSYSTEM_FUND = 0xa5197d124AaDEc85f1335462B245120409C0dC6C;

    PIXL public immutable token;

    error ArrayLengthMismatch();

    constructor(address _token) Ownable(msg.sender) {
        token = PIXL(_token);
    }

    function finaliseTokenomics() external onlyOwner {
        uint256 currentSupply = token.totalSupply();
        uint256 remaining = 500_000_000 ether - currentSupply;

        token.whitelist_mint(address(this), remaining);
        token.transfer(STAKING, 50_000_000 ether);
        token.transfer(ECOSYSTEM_FUND, 110_000_000 ether);
    }

    function multiTransfer(address[] calldata recipients, uint256[] calldata amounts) external onlyOwner {
        if (recipients.length != amounts.length) revert ArrayLengthMismatch();

        for (uint256 i = 0; i < recipients.length; i++) {
            token.transfer(recipients[i], amounts[i]);
        }
    }

    function withdraw() external onlyOwner {
        token.transfer(STAKING, token.balanceOf(address(this)));
    }
}
