// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.25;

interface IOracle {
    function getUnderlyingPrice(address cToken) external view returns(uint);

    function price(address token) external view returns(uint);
}

contract FixedOverrideOracle {

    /// @notice Indicator that this is a PriceOracle contract (for inspection)
    bool public constant isPriceOracle = true;

    mapping(address => uint) public priceOverrides;

    address public constant TRIBE = 0xc7283b66Eb1EB5FB86327f08e1B5816b0720212B;
    address public constant CTRIBE = 0xFd3300A9a74b3250F1b2AbC12B47611171910b07;

    IOracle public constant master = IOracle(0x4d10BC156FBaD2474a94f792fe0D6c3261469cdd);

    constructor() {
        priceOverrides[TRIBE] =  1 ether / 5_000;
        priceOverrides[CTRIBE] =  1 ether / 5_000;
    }

    /**
      * @notice Get the underlying price of a cToken asset
      * @param cToken The cToken to get the underlying price of
      * @return The underlying asset price mantissa (scaled by 1e18).
      *  Zero means the price is unavailable.
      */
    function getUnderlyingPrice(address cToken) external view returns (uint) {
        if (priceOverrides[cToken] > 0) {
            return priceOverrides[cToken];
        }
        return master.getUnderlyingPrice(cToken);
    }

    function price(address token) external view returns (uint) {
        if (priceOverrides[token] > 0) {
            return priceOverrides[token];
        }
        return master.price(token);
    }
}
