    "contracts/EigenLayerDelegationSummary.sol": {
      "content": "// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

contract EigenLayerDelegationSummary is
    Initializable,
    AccessControlUpgradeable,
    UUPSUpgradeable
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    /**
     * @notice Describes the current state of the delegated shares.
     */
    enum State {
        Usable,
        Locked
    }

    /**
     * @notice DelegatedBalance summary entry of the delegated shares to a strategy
     * and the corresponding underlying asset amount.
     */
    struct StrategySummary {
        IStrategy strategy;
        IERC20 underlyingToken;
        uint256 shares;
        uint256 amount;
    }

    /**
     * @notice Summary of all the delegated shares managed by the delegation manager.
     */
    struct DelegationSummary {
        IDelegationManager delegationManager;
        State state;
        StrategySummary[] strategies;
    }

    IDelegationManager public configuredDelegationManager;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IDelegationManager delegationManager,
        address defaultAdmin,
        address upgrader
    ) public initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, defaultAdmin);
        _grantRole(ADMIN_ROLE, defaultAdmin);
        _grantRole(UPGRADER_ROLE, upgrader);

        configuredDelegationManager = delegationManager;
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(UPGRADER_ROLE)
    {}

    /**
     * @notice List the strategies a staker delegated to containing the specified underlying assets.
     * @param staker The address of the staker.
     * @param tokens The IERC20 tokens used to filter strategies.
     * @dev Strategy filter by token is only applied when tokens is not empty.
     * @return A list of strategies `staker` has delegated shares to with underlying `tokens`
     */
    function _filterStrategies(address staker, IERC20[] memory tokens)
        private
        view
        returns (
            IStrategy[] memory,
            uint256[] memory,
            IERC20[] memory
        )
    {
        (
            IStrategy[] memory strategies,
            uint256[] memory shares
        ) = configuredDelegationManager.getDelegatableShares(staker);

        IStrategy beaconStrategy = configuredDelegationManager
            .beaconChainETHStrategy();

        // filter
        IStrategy[] memory filteredStrategies = new IStrategy[](
            strategies.length
        );
        uint256[] memory filteredShares = new uint256[](strategies.length);
        IERC20[] memory filteredTokens = new IERC20[](strategies.length);
        uint256 hits;
        for (uint256 i = 0; i < strategies.length; i++) {
            IERC20 underlyingToken = IERC20(address(0));
            if (strategies[i] != beaconStrategy) {
                underlyingToken = strategies[i].underlyingToken();
            }

            bool included = true;
            for (uint256 j = 0; j < tokens.length; j++) {
                included = underlyingToken == tokens[j];
                if (included) {
                    break;
                }
            }

            if (included) {
                filteredStrategies[hits] = strategies[i];
                filteredShares[hits] = shares[i];
                filteredTokens[hits] = underlyingToken;
                hits++;
            }
        }

        // defrag
        IStrategy[] memory retStrategies = new IStrategy[](hits);
        uint256[] memory retShares = new uint256[](hits);
        IERC20[] memory retUnderlyingTokens = new IERC20[](hits);
        for (uint256 i = 0; i < hits; i++) {
            retStrategies[i] = filteredStrategies[i];
            retShares[i] = filteredShares[i];
            retUnderlyingTokens[i] = filteredTokens[i];
        }
        return (retStrategies, retShares, retUnderlyingTokens);
    }

    /**
     * @notice Returns the current delegation state from the delegation manager.
     * @param staker The address of the staker.
     * @return The State of the delegated shares.
     */
    function _getDelegationState(address staker) private view returns (State) {
        if (configuredDelegationManager.isDelegated(staker)) {
            return State.Usable;
        }
        return State.Locked;
    }

    /**
     * @notice Summary of delegated shares by strategy filtered by underlying token.
     * @param staker The address of the staker.
     * @param tokens The IERC20 tokens used to filter strategies.
     * @dev Strategy filter by token is only applied when tokens is not empty.
     * @return A summary containing a list of strategies `staker` has delegated shares to with underlying `tokens`
     */
    function _getSummary(address staker, IERC20[] memory tokens)
        private
        view
        returns (DelegationSummary memory)
    {
        (
            IStrategy[] memory strategies,
            uint256[] memory shares,
            IERC20[] memory underlyingTokens
        ) = _filterStrategies(staker, tokens);

        DelegationSummary memory summary = DelegationSummary({
            delegationManager: configuredDelegationManager,
            state: _getDelegationState(staker),
            strategies: new StrategySummary[](strategies.length)
        });

        IStrategy beaconStrategy = configuredDelegationManager
            .beaconChainETHStrategy();
        for (uint256 i = 0; i < strategies.length; i++) {
            summary.strategies[i] = StrategySummary({
                strategy: strategies[i],
                underlyingToken: underlyingTokens[i],
                shares: shares[i],
                amount: shares[i]
            });

            if (strategies[i] != beaconStrategy) {
                summary.strategies[i].amount = strategies[i].userUnderlyingView(
                    staker
                );
            }
        }

        return summary;
    }

    function setDelegationManager(IDelegationManager delegationManager)
        public
        onlyRole(ADMIN_ROLE)
    {
        configuredDelegationManager = delegationManager;
    }

    /**
     * @notice Summary of delegated shares by strategy filtered by underlying tokens.
     * @param staker The address of the staker.
     * @param tokens The IERC20 tokens used to filter strategies.
     * @dev Strategy filter by token is only applied when tokens is not empty.
     * @return A summary containing a list of strategies `staker` has delegated shares to with underlying `tokens`
     */
    function delegationSummaryFiltered(address staker, IERC20[] memory tokens)
        external
        view
        returns (DelegationSummary memory)
    {
        return _getSummary(staker, tokens);
    }

    /**
     * @notice Summary of delegated shares by strategy.
     * @param staker The address of the staker.
     * @return A summary containing a list of strategies `staker` has delegated shares to with underlying `token`
     */
    function delegationSummary(address staker)
        external
        view
        returns (DelegationSummary memory)
    {
        return _getSummary(staker, new IERC20[](0));
    }

    enum WithdrawalState {
        /// @notice Signifies that the start block of a provided withdrawal object precedes the block at which the contract is executing
        NotStarted,
        /// @notice Signifies that the block at which the contract is executing is prior to the block at which the user may complete the queued withdrawal
        Unbonding,
        /// @notice Signifies that the user may complete the queued withdrawal at the block at which the contract is executing
        Unbonded,
        /// @notice Signifies that the user has completed the withdrawal at the block at which the contract is executing, and the withdrawal is therefore no longer queued
        Withdrawn
    }

    struct WithdrawalSummary {
        /**
         * @notice Withdrawal root of the corresponding withdrawal object. Provided to potentially make it easier for the caller to connect the results with the call parameters.
         */
        bytes32 withdrawalRoot;
        /**
         * @notice Index within the withdrawal of the strategy to which this summary corresponds. Provided to potentially make it easier for the caller to connect the results with the call parameters.
         */
        uint256 strategyIndex;
        /**
         * @notice Address of the staker to which the withdrawal corresponds. Provided to potentially make it easier for the caller to connect the results with the call parameters.
         */
        address staker;
        /**
         * @notice State of the withdrawal.
         */
        WithdrawalState state;
        /**
         * @notice The underlying token being withdrawn.
         */
        IERC20 underlyingToken;
        /**
         * @notice The shares being withdrawn.
         */
        uint256 shares;
        /**
         * @notice The amount being withdrawn.
         */
        uint256 amount;
    }

    /**
     * @notice Summary of pending withdrawals based on the provided withdrawal objects
     * @param withdrawals The withdrawal objects to return information about.
     * @return A list of summaries corresponding to the different strategies being withdrawn from, including their current state, their underlying tokens and the respective amounts.
     */
    function getPendingWithdrawalsSummary(
        IDelegationManager.Withdrawal[] calldata withdrawals
    ) external view returns (WithdrawalSummary[] memory) {
        uint256 summarySize = 0;
        for (uint256 i = 0; i < withdrawals.length; i++) {
            summarySize += withdrawals[i].strategies.length;
        }
        WithdrawalSummary[] memory summaries = new WithdrawalSummary[](
            summarySize
        );

        uint256 summaryCursor = 0;
        for (uint256 i = 0; i < withdrawals.length; i++) {
            IDelegationManager.Withdrawal calldata withdrawal = withdrawals[i];

            assert(withdrawal.shares.length == withdrawal.strategies.length);

            for (uint256 j = 0; j < withdrawal.strategies.length; j++) {
                summaries[summaryCursor] = _summarizeWithdrawalStrategy(
                    withdrawal,
                    j
                );
                summaryCursor++;
            }
        }

        return summaries;
    }

    /**
     * @notice Summarize a specific strategy of a pending withdrawal based on the provided data
     * @param withdrawal The withdrawal mentioning the strategy
     * @param strategyIndex The index of the strategy within the withdrawal
     * @return A summarized withdrawal object
     */
    function _summarizeWithdrawalStrategy(
        IDelegationManager.Withdrawal memory withdrawal,
        uint256 strategyIndex
    ) private view returns (WithdrawalSummary memory) {
        IStrategy strategy = withdrawal.strategies[strategyIndex];
        bytes32 withdrawalRoot = configuredDelegationManager
            .calculateWithdrawalRoot(withdrawal);
        bool isPending = configuredDelegationManager.pendingWithdrawals(
            withdrawalRoot
        );

        IStrategy beaconStrategy = configuredDelegationManager
            .beaconChainETHStrategy();
        uint256 delayBlocks = Math.max(
            configuredDelegationManager.minWithdrawalDelayBlocks(),
            configuredDelegationManager.strategyWithdrawalDelayBlocks(strategy)
        );

        WithdrawalSummary memory summary;
        summary.withdrawalRoot = withdrawalRoot;
        summary.strategyIndex = strategyIndex;
        summary.staker = withdrawal.staker;
        summary.underlyingToken = IERC20(address(0));
        summary.shares = withdrawal.shares[strategyIndex];
        summary.amount = withdrawal.shares[strategyIndex];

        if (strategy != beaconStrategy) {
            try strategy.underlyingToken() returns (IERC20 result) {
                summary.underlyingToken = result;
            } catch {}
            try strategy.sharesToUnderlyingView(summary.shares) returns (
                uint256 result
            ) {
                summary.amount = result;
            } catch {}
        }

        if (block.number < withdrawal.startBlock) {
            summary.state = WithdrawalState.NotStarted;
        } else if (!isPending) {
            summary.state = WithdrawalState.Withdrawn;
        } else if (block.number >= withdrawal.startBlock + delayBlocks) {
            summary.state = WithdrawalState.Unbonded;
        } else {
            summary.state = WithdrawalState.Unbonding;
        }

        return summary;
    }
}

/**
 * @title DelegationManager
 * @author Layr Labs, Inc.
 * @notice Terms of Service: https://docs.eigenlayer.xyz/overview/terms-of-service
 */
interface IDelegationManager {
    /// @notice Canonical, virtual beacon chain ETH strategy
    function beaconChainETHStrategy() external view returns (IStrategy);

    /**
     * @notice Returns the number of actively-delegatable shares a staker has across all strategies.
     * @dev Returns two empty arrays in the case that the Staker has no actively-delegateable shares.
     */
    function getDelegatableShares(address staker)
        external
        view
        returns (IStrategy[] memory, uint256[] memory);

    /**
     * @notice Returns 'true' if `staker` *is* actively delegated, and 'false' otherwise.
     */
    function isDelegated(address staker) external view returns (bool);

    /// @notice Returns the keccak256 hash of `withdrawal`.
    function calculateWithdrawalRoot(Withdrawal memory withdrawal)
        external
        view
        returns (bytes32);

    /**
     * @notice Returns 'true' if `withdrawalRoot` is a pending withdrawal, and 'false' otherwise.
     */
    function pendingWithdrawals(bytes32 withdrawalRoot)
        external
        view
        returns (bool);

    /**
     * @notice Minimum delay enforced by this contract for completing queued withdrawals. Measured in blocks, and adjustable by this contract's owner,
     * up to a maximum of `MAX_WITHDRAWAL_DELAY_BLOCKS`. Minimum value is 0 (i.e. no delay enforced).
     * Note that strategies each have a separate withdrawal delay, which can be greater than this value. So the minimum number of blocks that must pass
     * to withdraw a strategy is MAX(minWithdrawalDelayBlocks, strategyWithdrawalDelayBlocks[strategy])
     */
    function minWithdrawalDelayBlocks() external view returns (uint256);

    /**
     * @notice Minimum delay enforced by this contract per Strategy for completing queued withdrawals. Measured in blocks, and adjustable by this contract's owner,
     * up to a maximum of `MAX_WITHDRAWAL_DELAY_BLOCKS`. Minimum value is 0 (i.e. no delay enforced).
     */
    function strategyWithdrawalDelayBlocks(IStrategy strategy)
        external
        view
        returns (uint256);

    /**
     * Struct type used to specify an existing queued withdrawal. Rather than storing the entire struct, only a hash is stored.
     * In functions that operate on existing queued withdrawals -- e.g. completeQueuedWithdrawal`, the data is resubmitted and the hash of the submitted
     * data is computed by `calculateWithdrawalRoot` and checked against the stored hash in order to confirm the integrity of the submitted data.
     */
    struct Withdrawal {
        // The address that originated the Withdrawal
        address staker;
        // The address that the staker was delegated to at the time that the Withdrawal was created
        address delegatedTo;
        // The address that can complete the Withdrawal + will receive funds when completing the withdrawal
        address withdrawer;
        // Nonce used to guarantee that otherwise identical withdrawals have unique hashes
        uint256 nonce;
        // Block number when the Withdrawal was created
        uint32 startBlock;
        // Array of strategies that the Withdrawal contains
        IStrategy[] strategies;
        // Array containing the amount of shares in each Strategy in the `strategies` array
        uint256[] shares;
    }
}

/**
 * @title Minimal interface for an `Strategy` contract.
 * @author Layr Labs, Inc.
 * @notice Terms of Service: https://docs.eigenlayer.xyz/overview/terms-of-service
 * @notice Custom `Strategy` implementations may expand extensively on this interface.
 */
interface IStrategy {
    /**
     * @notice convenience function for fetching the current total shares of `user` in this strategy, by
     * querying the `strategyManager` contract
     */
    function shares(address user) external view returns (uint256);

    /**
     * @notice convenience function for fetching the current underlying value of all of the `user`'s shares in
     * this strategy. In contrast to `userUnderlying`, this function guarantees no state modifications
     */
    function userUnderlyingView(address user) external view returns (uint256);

    /// @notice The underlying token for shares in this Strategy
    function underlyingToken() external view returns (IERC20);

    /**
     * @notice Used to convert a number of shares to the equivalent amount of underlying tokens for this strategy.
     * @notice In contrast to `sharesToUnderlying`, this function guarantees no state modifications
     * @param amountShares is the amount of shares to calculate its conversion into the underlying token
     * @return The amount of shares corresponding to the input `amountUnderlying`
     * @dev Implementation for these functions in particular may vary significantly for different strategies
     */
    function sharesToUnderlyingView(uint256 amountShares)
        external
        view
        returns (uint256);
}
