      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "openzeppelin-contracts/contracts/access/Ownable.sol";
import "openzeppelin-contracts/contracts/access/AccessControl.sol";
import "openzeppelin-contracts/contracts/utils/ReentrancyGuard.sol";
import "openzeppelin-contracts/contracts/utils/Address.sol";
import "./interfaces/ITarget.sol";

contract Minter is Ownable, ReentrancyGuard, AccessControl {
    uint256 public price= 0.02 ether;
    bool public isSaleActive;

    bytes32 public constant MINTER_ROLE = keccak256('MINTER_ROLE');

    address public agentAddress;
    ITarget public targetNFTContract;

    constructor(
        address _targetNFTContract
    ) Ownable(msg.sender) {

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(MINTER_ROLE, DEFAULT_ADMIN_ROLE);
        _grantRole(MINTER_ROLE, msg.sender);
        targetNFTContract = ITarget(_targetNFTContract);
    }

    function overrideTransferOwnership(address _to) public onlyOwner {
        require(isSaleActive == false, "Minter: Sale is active. Cannot transfer ownership");
        targetNFTContract.transferOwnership(_to);
    }

    function withdrawETH(address payable _to) external onlyOwner {
        require(address(this).balance > 0, "Minter: Insufficient balance");
        require(_to != address(0), "Minter: Invalid address");

        _to.transfer(address(this).balance);
    }

    function giveaway(address _to) external onlyRole(MINTER_ROLE) {
        _mintNFT(_to);
    }

    function publicMint(address _to) external payable nonReentrant {
        require(isSaleActive, "Minter: Sale is not active");
        require(msg.value == price, "Minter: Incorrect payment amount");

        _mintNFT(_to);
    }

    function publicBulkMint(address _to, uint256 _quantity) public payable nonReentrant {
        require(isSaleActive, "Minter: Sale is not active");
        require(msg.value == price * _quantity, "Minter: Incorrect payment amount");

        for (uint256 i = 0; i < _quantity; i++) {
            _mintNFT(_to);
        }
    }

    function publicBulkMintByAgent(address _to, uint256 _quantity) external payable onlyAgent {
        publicBulkMint(_to, _quantity);
    }

    function setPrice(uint256 _newPrice) external onlyOwner {
        require(isSaleActive == false, "Minter: Sale is active. Cannot set price");
        price = _newPrice;
    }

    function setIsSaleActive(bool _state) external onlyOwner {
        isSaleActive = _state;
    }

    function setAgentAddress(address _agentAddress) external onlyOwner {
        agentAddress = _agentAddress;
    }

    function _mintNFT(address _to) internal {
        targetNFTContract.giveaway(_to);       
    }

    modifier onlyAgent() {
        require(msg.sender == agentAddress, "Minter: Invalid agent address");
        _;
    }
}
"
