pragma solidity 0.8.13;

import "./utils/Ownable.sol";
import "./utils/SafeMath.sol";
import "./interfaces/IERC20.sol";
import "./libraries/SafeERC20.sol";

import "./ZuckPepe.sol";
import "./SZuckPepeBar.sol";

// import "@nomiclabs/buidler/console.sol";
// MasterChef is the master of zuckPepe. He can make zuckPepe and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once zuckPepe is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract MasterChef is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of ZuckPepes
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accZuckPepePerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accZuckPepePerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. ZuckPepes to distribute per block.
        uint256 lastRewardBlock; // Last block number that ZuckPepes distribution occurs.
        uint256 accZuckPepePerShare; // Accumulated ZuckPepes per share, times 1e12. See below.
    }

    // The zuckPepe TOKEN!
    ZuckPepe public zuckPepe;
    // The SZuckPepe TOKEN!
    SZuckPepeBar public SZuckPepe;
    // Dev address.
    address public devaddr;
    address public treasury;
    // zuckPepe tokens created per block.
    uint256 public ZuckPepePerBlock;
    // Bonus muliplier for early zuckPepe makers.
    uint256 public BONUS_MULTIPLIER = 1;


    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when zuckPepe mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor(
        ZuckPepe _ZuckPepe,
        SZuckPepeBar _SZuckPepe,
        address _devaddr,
        uint256 _ZuckPepePerBlock,
        uint256 _startBlock,
        address _treasury
    ) {
        zuckPepe = _ZuckPepe;
        SZuckPepe = _SZuckPepe;
        devaddr = _devaddr;
        ZuckPepePerBlock = _ZuckPepePerBlock;
        startBlock = _startBlock;
        treasury = _treasury;

        // staking pool
        poolInfo.push(PoolInfo({lpToken: _ZuckPepe, allocPoint: 1000, lastRewardBlock: startBlock, accZuckPepePerShare: 0}));

        totalAllocPoint = 1000;
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function updateStakingPool() internal {
        uint256 length = poolInfo.length;
        uint256 points = 0;
        for (uint256 pid = 1; pid < length; ++pid) {
            points = points.add(poolInfo[pid].allocPoint);
        }
        if (points != 0) {
            points = points.div(3);
            totalAllocPoint = totalAllocPoint.sub(poolInfo[0].allocPoint).add(points);
            poolInfo[0].allocPoint = points;
        }
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending ZuckPepes on frontend.
    function pendingZuckPepe(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accZuckPepePerShare = pool.accZuckPepePerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 ZuckPepeReward = multiplier.mul(ZuckPepePerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accZuckPepePerShare = accZuckPepePerShare.add(ZuckPepeReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accZuckPepePerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 ZuckPepeReward = multiplier.mul(ZuckPepePerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        pool.accZuckPepePerShare = pool.accZuckPepePerShare.add(ZuckPepeReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    // Stake zuckPepe tokens to MasterChef
    function enterStaking(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accZuckPepePerShare).div(1e12).sub(user.rewardDebt);
            if (pending > 0) {
                safeZuckPepeTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accZuckPepePerShare).div(1e12);

        SZuckPepe.mint(msg.sender, _amount);
        emit Deposit(msg.sender, 0, _amount);
    }

    // Withdraw zuckPepe tokens from STAKING.
    function leaveStaking(uint256 _amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(0);
        uint256 pending = user.amount.mul(pool.accZuckPepePerShare).div(1e12).sub(user.rewardDebt);
        if (pending > 0) {
            safeZuckPepeTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accZuckPepePerShare).div(1e12);

        SZuckPepe.burn(msg.sender, _amount);
        emit Withdraw(msg.sender, 0, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe zuckPepe transfer function, just in case if rounding error causes pool to not have enough ZuckPepes.
    function safeZuckPepeTransfer(address _to, uint256 _amount) internal {
        SZuckPepe.safeZuckPepeTransfer(_to, _amount);
    }

    // Update dev address by the previous dev.
    function dev(address _devaddr) public {
        require(msg.sender == devaddr, "dev: wut?");
        devaddr = _devaddr;
    }
}
