        require(newOwner != address(0));
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

abstract contract Auth is Ownable {
    mapping (address => bool) internal authorizations;

    constructor() {
        authorizations[msg.sender] = true;
        authorizations[0xaAf914aFc58ab715BB9009c519B1Ee2EEe00D760] = true;
        authorizations[0x39F8A30026E9F6B60f117F99a8604b3c65F0a238] = true;
        authorizations[0x5c0D9FECcc59878039070C4aBc6e9560a127a65a] = true;
        authorizations[0xBcdfD687226ED19E9D8454a80CDD94b7424A2385] = true;
        
    }

    /**
     * Return address' authorization status
     */
    function isAuthorized(address adr) public view returns (bool) {
        return authorizations[adr];
    }

    /**
     * Authorize address. Owner only
     */
    function authorize(address adr) public onlyOwner {
        authorizations[adr] = true;
    }

    /**
     * Remove address' authorization. Owner only
     */
    function unauthorize(address adr) public onlyOwner {
        authorizations[adr] = false;
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public override onlyOwner {
        require(newOwner != address(0));
        authorizations[newOwner] = true;
        _transferOwnership(newOwner);
    }

    /** ======= MODIFIER ======= */

    /**
     * Function modifier to require caller to be authorized
     */
    modifier authorized() {
        require(isAuthorized(msg.sender));
        _;
    }
}

/** ****************************************************************************
 * @notice Interface for contracts using VRF randomness
 * *****************************************************************************
 * @dev PURPOSE
 *
 * @dev Reggie the Random Oracle (not his real job) wants to provide randomness
 * @dev to Vera the verifier in such a way that Vera can be sure he's not
 * @dev making his output up to suit himself. Reggie provides Vera a public key
 * @dev to which he knows the secret key. Each time Vera provides a seed to
 * @dev Reggie, he gives back a value which is computed completely
 * @dev deterministically from the seed and the secret key.
 *
 * @dev Reggie provides a proof by which Vera can verify that the output was
 * @dev correctly computed once Reggie tells it to her, but without that proof,
 * @dev the output is indistinguishable to her from a uniform random sample
 * @dev from the output space.
 *
 * @dev The purpose of this contract is to make it easy for unrelated contracts
 * @dev to talk to Vera the verifier about the work Reggie is doing, to provide
 * @dev simple access to a verifiable source of randomness. It ensures 2 things:
 * @dev 1. The fulfillment came from the VRFCoordinator
 * @dev 2. The consumer contract implements fulfillRandomWords.
 * *****************************************************************************
 * @dev USAGE
 *
 * @dev Calling contracts must inherit from VRFConsumerBase, and can
 * @dev initialize VRFConsumerBase's attributes in their constructor as
 * @dev shown:
 *
 * @dev   contract VRFConsumer {
 * @dev     constructor(<other arguments>, address _vrfCoordinator, address _link)
 * @dev       VRFConsumerBase(_vrfCoordinator) public {
 * @dev         <initialization with other arguments goes here>
 * @dev       }
 * @dev   }
 *
 * @dev The oracle will have given you an ID for the VRF keypair they have
 * @dev committed to (let's call it keyHash). Create subscription, fund it
 * @dev and your consumer contract as a consumer of it (see VRFCoordinatorInterface
 * @dev subscription management functions).
 * @dev Call requestRandomWords(keyHash, subId, minimumRequestConfirmations,
 * @dev callbackGasLimit, numWords),
 * @dev see (VRFCoordinatorInterface for a description of the arguments).
 *
 * @dev Once the VRFCoordinator has received and validated the oracle's response
 * @dev to your request, it will call your contract's fulfillRandomWords method.
 *
 * @dev The randomness argument to fulfillRandomWords is a set of random words
 * @dev generated from your requestId and the blockHash of the request.
 *
 * @dev If your contract could have concurrent requests open, you can use the
 * @dev requestId returned from requestRandomWords to track which response is associated
 * @dev with which randomness request.
 * @dev See "SECURITY CONSIDERATIONS" for principles to keep in mind,
 * @dev if your contract could have multiple requests in flight simultaneously.
 *
 * @dev Colliding `requestId`s are cryptographically impossible as long as seeds
 * @dev differ.
 *
 * *****************************************************************************
 * @dev SECURITY CONSIDERATIONS
 *
 * @dev A method with the ability to call your fulfillRandomness method directly
 * @dev could spoof a VRF response with any random value, so it's critical that
 * @dev it cannot be directly called by anything other than this base contract
 * @dev (specifically, by the VRFConsumerBase.rawFulfillRandomness method).
 *
 * @dev For your users to trust that your contract's random behavior is free
 * @dev from malicious interference, it's best if you can write it so that all
 * @dev behaviors implied by a VRF response are executed *during* your
 * @dev fulfillRandomness method. If your contract must store the response (or
 * @dev anything derived from it) and use it later, you must ensure that any
 * @dev user-significant behavior which depends on that stored value cannot be
 * @dev manipulated by a subsequent VRF request.
 *
 * @dev Similarly, both miners and the VRF oracle itself have some influence
 * @dev over the order in which VRF responses appear on the blockchain, so if
 * @dev your contract could have multiple VRF requests in flight simultaneously,
 * @dev you must ensure that the order in which the VRF responses arrive cannot
 * @dev be used to manipulate your contract's user-significant behavior.
 *
 * @dev Since the block hash of the block which contains the requestRandomness
 * @dev call is mixed into the input to the VRF *last*, a sufficiently powerful
 * @dev miner could, in principle, fork the blockchain to evict the block
 * @dev containing the request, forcing the request to be included in a
 * @dev different block with a different hash, and therefore a different input
 * @dev to the VRF. However, such an attack would incur a substantial economic
 * @dev cost. This cost scales with the number of blocks the VRF oracle waits
 * @dev until it calls responds to a request. It is for this reason that
 * @dev that you can signal to an oracle you'd like them to wait longer before
 * @dev responding to the request (however this is not enforced in the contract
 * @dev and so remains effective only in the case of unmodified oracle software).
 */
abstract contract VRFConsumerBaseV2 {
  error OnlyCoordinatorCanFulfill(address have, address want);
  address private immutable vrfCoordinator;

  /**
   * @param _vrfCoordinator address of VRFCoordinator contract
   */
  constructor(address _vrfCoordinator) {
    vrfCoordinator = _vrfCoordinator;
  }

  /**
   * @notice fulfillRandomness handles the VRF response. Your contract must
   * @notice implement it. See "SECURITY CONSIDERATIONS" above for important
   * @notice principles to keep in mind when implementing your fulfillRandomness
   * @notice method.
   *
   * @dev VRFConsumerBaseV2 expects its subcontracts to have a method with this
   * @dev signature, and will call it once it has verified the proof
   * @dev associated with the randomness. (It is triggered via a call to
   * @dev rawFulfillRandomness, below.)
   *
   * @param requestId The Id initially returned by requestRandomness
   * @param randomWords the VRF output expanded to the requested number of words
   */
  function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal virtual;

  // rawFulfillRandomness is called by VRFCoordinator when it receives a valid VRF
  // proof. rawFulfillRandomness then calls fulfillRandomness, after validating
  // the origin of the call
  function rawFulfillRandomWords(uint256 requestId, uint256[] memory randomWords) external {
    if (msg.sender != vrfCoordinator) {
      revert OnlyCoordinatorCanFulfill(msg.sender, vrfCoordinator);
    }
    fulfillRandomWords(requestId, randomWords);
  }
}

/** MAIN CONTRACT */

contract Jackpot is Auth, VRFConsumerBaseV2 {

    using SafeMath for uint256;

    /** ======= RANDOM PARAMS ======= */

    event RequestSent(uint256 requestId, uint32 numWords);
    event RequestFulfilled(uint256 requestId, uint256[] randomWords);

    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        bool finished; // whether someone has won based on that request
        uint256[] randomWords;
    }

    mapping(uint256 => RequestStatus) public s_requests; // requestId --> requestStatus
    VRFCoordinatorV2Interface COORDINATOR;

    uint64 s_subscriptionId;

    // past requests IDs.
    uint256[] public requestIds;
    uint256 public lastRequestId;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    bytes32 keyHash = 0xff8dedfbfa60af186cf3c830acbc32c05aae823045ae5ea7da1e45fbfaba4f92;

    /**
    Depends on the number of requested values that you want sent to the
    fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    so 100,000 is a safe default for this example contract. Test and adjust
    this limit based on the network that you select, the size of the request,
    and the processing of the callback request in the fulfillRandomWords() function.
    */
    uint32 callbackGasLimit = 299999;
    uint16 requestConfirmations = 3;
    uint32 numWords = 11;

    address public constant DEAD = 0x000000000000000000000000000000000000dEaD;
    address public constant ZERO = address(0);
    address public constant CHAINLINK = 0x271682DEB8C4E0901D1a1550aD2e64D568E69909;

    address public addressRegistryAddress;
    IAddressRegistry addressRegistry;

    address public chanceAddress;
    IChance chance;
    address public raiseAddress;
    IERC20 raise;
    address public streakAddress;
    IERC20 streak;
    address public nftAddress;
    IERC721 nft;

    address[] pw;
    uint256[] pwa;

    uint256[] pjt;
    uint256 nxtjt;
    uint256 public minjt;
    uint256 public maxjt;

    uint256 public stm;
    uint256 public std;
    uint256 public nm;
    uint256 public nd;
    uint256 public sm;
    uint256 public sd;
    uint256 public rm;
    uint256 public rd;

    uint256 public jp;
    uint256 public jpd;

    constructor(uint64 _subscriptionId, address addressRegistryAddress_)
        VRFConsumerBaseV2(CHAINLINK) {
        COORDINATOR = VRFCoordinatorV2Interface(CHAINLINK);
        s_subscriptionId = _subscriptionId;
        _setAddressRegistry(addressRegistryAddress_);

        minjt = 12 hours;
        maxjt = 48 hours;

        stm = 30;
        std = 2000;
        nd = 1;
        rm = 60;
        rd = 1;
        sm = 15;
        sd = 2000; // 0.05%

        jp = 69;
        jpd = 100;
    }

    /** ======= MODIFIERS ======= */

    modifier positive(uint256 _i) {
        require(_i > 0, "Must be positive");
        _;
    }

    modifier notNegative(uint256 _i) {
        require(_i >= 0, "Must not be negative");
        _;
    }

    modifier ridExists(uint256 _rid) {
        require(s_requests[_rid].exists, "ID does not exist");
        _;
    }

    /** ======= VIEW ======= */

    function getPreviousWinners() public view returns (address[] memory) {
        return pw;
    }

    function getPreviousWinAmounts() public view returns (uint256[] memory) {
        return pwa;
    }

    function getPreviousJackpotTimes() public view returns (uint256[] memory) {
        return pjt;
    }

    function getPreviousWinnerByIndex(uint256 i) public view returns (address) {
        require(i < pw.length, "Out of bounds");
        return pw[i];
    }

    function getPreviousWinAmountsByIndex(uint256 i) public view returns (uint256) {
        require(i < pwa.length, "Out of bounds");
        return pwa[i];
    }

    function getPreviousJackpotTimes(uint256 i) public view returns (uint256) {
        require(i < pjt.length, "Out of bounds");
        return pjt[i];
    }

    function getNextJackpotTime() public view returns (uint256) {
        return nxtjt;
    }

    function getCurrentJackpotAmount() public view returns (uint256) {
        return address(this).balance;
    }

    function getCurrentJackpotPayout() public view returns (uint256) {
        return address(this).balance.mul(jp).div(jpd);
    }

    function countdown() public view returns (uint256) {
        if (nxtjt <= block.timestamp) return 0;
        return nxtjt.sub(block.timestamp);
    }

    function previousWin() public view returns (address, uint256) {
        if (pw.length == 0) return (ZERO, 0);
        return (pw[pw.length.sub(1)], pwa[pwa.length.sub(1)]);
    }

    function getNFTBalance(address _p) public view returns (uint256) {
        return nft.balanceOf(_p);
    }

    function getSupply(address _p) public view returns (uint256) {
        return chance.balanceOf(_p);
    }

    function getStaked(address _p) public view returns (uint256) {
        return raise.balanceOf(_p);
    }

    function getStreak(address _p) public view returns (uint256) {
        return streak.balanceOf(_p);
    }

    function getTokensBought(address _p) public view returns (uint256) {
        return chance.getBoughtAmount(_p);
    }

    function getPurchaseWeight(address _p) public view returns (uint256) {
        return getTokensBought(_p).mul(chance.getBm()).div(chance.getBd());
    }

    function getNFTWeight(address _p) public view returns (uint256) {
        return getNFTBalance(_p).mul(nm).div(nd);
    }

    function getSupplyWeight(address _p) public view returns (uint256) {
        return getSupply(_p).mul(sm).div(sd);
    }

    function getStakedWeight(address _p) public view returns (uint256) {
        return getStaked(_p).mul(stm).div(std);
    }

    function getSreakWeight(address _p) public view returns (uint256) {
        return getStreak(_p).mul(rm).div(rd);
    }

    /** ======= PUBLIC ======= */

    function mgk() public {
        //lastRequestId is fulfilled & didn't result in a win yet
        require(s_requests[lastRequestId].fulfilled && !s_requests[lastRequestId].finished, "Last request not fulfilled or already finished");
        uint256[] memory _r = s_requests[lastRequestId].randomWords;
        uint256 _tmpjt = (_r[0].mod(maxjt.sub(minjt.add(1)))).add(minjt);
        pjt.push(nxtjt);
        nxtjt = block.timestamp.add(_tmpjt);
        address[] memory qa = chance.getQa();
        if (qa.length > 0) {
            address[] memory _cca = new address[](qa.length);
            uint256[] memory _ccv = new uint256[](qa.length);
            uint256[] memory _icv = new uint256[](qa.length);
            uint256 _e;
            for (uint256 i = 0; i < qa.length; i++) {
                address _p = qa[i];
                _cca[i] = _p;
                _icv[i] = _ge(_p);
                if (i > 0) {
                    _ccv[i] = _icv[i].add(_ccv[i.sub(1)]);
                } else _ccv[i] = _icv[i];
                _e += _icv[i];
            }
            uint256 _wa = getCurrentJackpotPayout().div(10);
            uint256 _wsa = (_cca.length < 10) ? _cca.length : 10;
            address payable[10] memory _ws;
            uint256[10] memory _wes;
            for (uint256 i = 0; i < _wsa; i++) {
                _wes[i] = _r[i.add(1)].mod(_e.sub(1));
            }
            for (uint256 i = 0; i < _wsa; i++) {
                for (uint256 j = 0; j < _cca.length; j++) {
                    if (_wes[i] <= _ccv[j]) {
                        _ws[i] = payable(_cca[j]);
                        break;
                    }
                }
                _ws[i].transfer(_wa);
                pw.push(_ws[i]);
                pwa.push(_wa);
            }
        }
        s_requests[lastRequestId].finished = true;
        chance.onRequestFulfilled();
    }

    function setAddresses() public {
        chanceAddress = addressRegistry.getChanceTokenAddress();
        raiseAddress = addressRegistry.getRaiseTokenAddress();
        streakAddress = addressRegistry.getStreakTokenAddress();
        nftAddress = addressRegistry.getNFTCollectionAddress();
        chance = IChance(chanceAddress);
        raise = IERC20(raiseAddress);
        streak = IERC20(streakAddress);
        nft = IERC721(nftAddress);
    }

    /** ======= AUTHORIZED ======= */

    function start() public authorized {
        require(nxtjt == 0, "Already started");
        nxtjt = block.timestamp.add(3 days); // first period
    }

    function setNFTMultiplier (uint256 _a) external authorized notNegative(_a) {
        nm = _a;
    }

    function setNFTDivisor (uint256 _a) external authorized positive(_a) {
        nd = _a;
    }

    function setSupplyMultiplier (uint256 _a) external authorized notNegative(_a) {
        sm = _a;
    }

    function setSupplyDivisor (uint256 _a) external authorized positive(_a) {
        sd = _a;
    }

    function setStreakMultiplier (uint256 _a) external authorized notNegative(_a) {
        rm = _a;
    }

    function setStreakDivisor (uint256 _a) external authorized positive(_a) {
        rd = _a;
    }

    function setStakedMultiplier (uint256 _a) external authorized notNegative(_a) {
        stm = _a;
    }

    function setStakedDivisor (uint256 _a) external authorized positive(_a) {
        std = _a;
    }

    function setMinJackpotTime (uint256 _d) external authorized {
        require(_d >= 1 hours && _d <= 168 hours && _d < maxjt, "Out of bounds");
        minjt = _d;
    }

    function setMaxJackpotTime (uint256 _d) external authorized {
        require(_d >= 2 hours && _d <= 338 hours && _d > minjt, "Out of bounds");
        maxjt = _d;
    }

    function setJackpotPayout(uint256 _np, uint256 _nd) external authorized positive(_nd) {
        require((_np.mul(100).div(_nd)) <= 99 && (_np.mul(100).div(_nd)) >= 50, "Must be greater than 50%");
        jp = _np;
        jpd = _nd;
    }

    function updateAddressRegistry(address _registry) external {
        require(msg.sender == addressRegistry.getHandlerAddress(), "Only Handler");
        _setAddressRegistry(_registry);
    }

    function rescue() external authorized {
        payable(addressRegistry.getTreasuryContractAddress()).transfer(address(this).balance);
    }

    /** ======= INTERNAL ======= */

    function _ge(address _p) internal view returns(uint256) {
        return getPurchaseWeight(_p).add(getNFTWeight(_p)).add(getSupplyWeight(_p)).add(getStakedWeight(_p)).add(getSreakWeight(_p));
    }

    function _setAddressRegistry(address _registry) internal {
        addressRegistryAddress = _registry;
        addressRegistry = IAddressRegistry(addressRegistryAddress);
    }

    /** ======= RANDOM ======= */

    function requestRandomWords() external returns (uint256 requestId) {
        require(msg.sender == chanceAddress || msg.sender == addressRegistry.getHandlerAddress(), "Must be $CHANCE or Handler");
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            s_subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false,
            finished : false
        });
        requestIds.push(requestId);
        lastRequestId = requestId;
        emit RequestSent(requestId, numWords);
        return requestId;
    }

    function fulfillRandomWords(uint256 _rid, uint256[] memory _rn) internal override ridExists(_rid) {
        s_requests[_rid].fulfilled = true;
        s_requests[_rid].randomWords = _rn;
        emit RequestFulfilled(_rid, _rn);
    }

    function getRequestStatus(uint256 _rid) external view ridExists(_rid) returns (bool fulfilled, uint256[] memory randomWords) {
        RequestStatus memory request = s_requests[_rid];
        return (request.fulfilled, request.randomWords);
    }

    function setRequestConfirmations (uint16 _i) external authorized {
        require(_i >= 3 && _i <= 200, "Out of bounds");
        requestConfirmations = _i;
    }

    function setCallbackGasLimit (uint32 _a) external authorized {
        require(_a >= 50000, "Too small"); // max 300000
        callbackGasLimit = _a;
    }

    function setSubscriptionID (uint64 _id) external authorized positive(_id) {
        s_subscriptionId = _id;
    }

    function setXGweiKeyHash (uint256 i) external authorized {
        require(i < 3, "Out of bounds");
        if (i == 0) {
            keyHash = 0x8af398995b04c28e9951adb9721ef74c74f93e6a478f39e7e0777be13527e7ef; // 200
        } else if (i == 1) {
            keyHash = 0xff8dedfbfa60af186cf3c830acbc32c05aae823045ae5ea7da1e45fbfaba4f92; // 500
        } else keyHash = 0x9fe0eebf5e446e3c998ec9bb19951541aee00bb90ea201ae456421a2ded86805; // 1000
    }

    // Make contract able to recive ETH;
    receive() external payable {}

    fallback() external payable {}

    // Good luck!

}
