      "content": "// SPDX-License-Identifier: MIT LICENSE

pragma solidity ^0.8.5;

import "../lib/openzeppelin-contracts/contracts/token/ERC721/IERC721Receiver.sol";
import "./IERC721Burnable.sol";
import "../lib/openzeppelin-contracts-upgradeable/contracts/proxy/utils/Initializable.sol";

contract PotionStaking is IERC721Receiver, Initializable {
    struct Stake {
        uint32[3] since;
        address owner;
    }

    address public nft;
    address[] public reward;
    address public owner;

    mapping(uint256 => Stake) public vault; //TOKEN ID => Staking Details

    mapping(address => uint256[]) private durationRate; // REWARD NFT ADDRESS => Rate OF EMMISION (Level)

    event NFTStaked(address owner, uint256 tokenId, uint256 value);
    event NFTUnstaked(address owner, uint256 tokenId, uint256 value);
    event Claimed(address owner, uint256 amount);
    event UpdateOwner(address indexed oldOwner, address newOwner);

    error CannotClaim();

    modifier onlyOwner() {
        require(owner == msg.sender, "LL:Invalid Owner");
        _;
    }

    function setOwner(address _owner) external onlyOwner {
        owner = _owner;
        emit UpdateOwner(msg.sender, owner);
    }

    function initialize(
        address _owner,
        address stakeTOken,
        address[] calldata _rewardTokenAddress,
        uint256[][] calldata _durationRate
    ) external initializer {
        owner = _owner;
        nft = stakeTOken;
        reward = _rewardTokenAddress;
        for (uint256 i; i < _rewardTokenAddress.length; ) {
            durationRate[_rewardTokenAddress[i]] = _durationRate[i];
            unchecked {
                ++i;
            }
        }
    }

    function stake(uint256[] calldata _tokenIds) external {
        require(_tokenIds.length > 0, "Stake atleast one nft");
        uint256 tokenId;
        for (uint256 i; i < _tokenIds.length; ) {
            tokenId = _tokenIds[i];
            require(
                IERC721Burnable(nft).ownerOf(tokenId) == msg.sender,
                "not your token"
            );
            require(vault[tokenId].owner == address(0), "already stakedUser");
            IERC721Burnable(nft).safeTransferFrom(
                msg.sender,
                address(this),
                tokenId
            );
            vault[tokenId] = Stake(
                [
                    uint32(block.timestamp),
                    uint32(block.timestamp),
                    uint32(block.timestamp)
                ],
                msg.sender
            );
            unchecked {
                ++i;
            }
            emit NFTStaked(msg.sender, tokenId, block.timestamp);
        }
    }

    function unstake(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "Un-Stake atleast one nft");
        for (uint256 i = 0; i < reward.length; ) {
            uint256 totalReward = _claim(msg.sender, tokenIds, reward[i], i);
            if (totalReward > 0) {
                IERC721Burnable(reward[i]).mint(msg.sender, totalReward);
            }
            unchecked {
                ++i;
            }
        }
        _unstakeMany(msg.sender, tokenIds);
    }

    function claim_elixir(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "Claim atleast one nft");
        uint256 totalReward = _claim(msg.sender, tokenIds, reward[0], 0);

        if (totalReward > 0) {
            for (uint256 i; i < tokenIds.length; ) {
                vault[tokenIds[i]].since[0] = uint32(block.timestamp);
                unchecked {
                    ++i;
                }
            }
            IERC721Burnable(reward[0]).mint(msg.sender, totalReward);
        } else {
            revert CannotClaim();
        }
    }

    function claim_immortal(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "Claim atleast one nft");
        uint256 totalReward = _claim(msg.sender, tokenIds, reward[1], 1);

        if (totalReward > 0) {
            for (uint256 i; i < tokenIds.length; ) {
                vault[tokenIds[i]].since[1] = uint32(block.timestamp);
                unchecked {
                    ++i;
                }
            }
            IERC721Burnable(reward[1]).mint(msg.sender, totalReward);
        } else {
            revert CannotClaim();
        }
    }

    function claim_advelixir(uint256[] calldata tokenIds) external {
        require(tokenIds.length > 0, "Claim atleast one nft");
        uint256 totalReward = _claim(msg.sender, tokenIds, reward[2], 2);

        if (totalReward > 0) {
            for (uint256 i; i < tokenIds.length; ) {
                vault[tokenIds[i]].since[2] = uint32(block.timestamp);
                unchecked {
                    ++i;
                }
            }
            IERC721Burnable(reward[2]).mint(msg.sender, totalReward);
        } else {
            revert CannotClaim();
        }
    }

    function _unstakeMany(address account, uint256[] calldata tokenIds)
        internal
    {
        uint256 tokenId;
        for (uint256 i; i < tokenIds.length; ) {
            tokenId = tokenIds[i];
            require(vault[tokenId].owner == msg.sender, "not an owner");
            delete vault[tokenId];
            IERC721Burnable(nft).safeTransferFrom(
                address(this),
                account,
                tokenId
            );
            unchecked {
                ++i;
            }
            emit NFTUnstaked(account, tokenId, block.timestamp);
        }
    }

    function _claim(
        address account,
        uint256[] calldata tokenIds,
        address _reward,
        uint256 _rewardPosition
    ) internal view returns (uint256) {
        uint256 tokenId;
        uint256 totalReward;
        uint256 totalTime;
        for (uint256 i; i < tokenIds.length; ) {
            tokenId = tokenIds[i];
            Stake storage stakedUser = vault[tokenId];
            require(stakedUser.owner == account, "not an owner");
            totalTime = block.timestamp - stakedUser.since[_rewardPosition];
            totalReward +=
                (totalTime * 10000) /
                durationRate[_reward][
                    IERC721Burnable(nft).checkLevel(tokenId) - 1
                ];
            unchecked {
                ++i;
            }
        }
        return (totalReward / 10000);
    }

    // should never be used inside of transaction because of gas fee
    function balanceOf(address account) external view returns (uint256) {
        uint256 balance = 0;
        uint256 supply = IERC721Burnable(nft).totalSupply();
        for (uint256 i = 0; i < supply; ) {
            if (vault[i].owner == account) {
                balance += 1;
            }
            unchecked {
                ++i;
            }
        }
        return balance;
    }

    // should never be used inside of transaction because of gas fee
    function tokensOfOwner(address account)
        public
        view
        returns (uint256[] memory ownerTokens)
    {
        uint256 supply = IERC721Burnable(nft).totalSupply();
        uint256[] memory tmp = new uint256[](supply);

        uint256 index = 0;
        for (uint256 tokenId = 0; tokenId < supply; ) {
            if (vault[tokenId].owner == account) {
                tmp[index] = tokenId;
                index += 1;
            }
            unchecked {
                tokenId++;
            }
        }

        uint256[] memory tokens = new uint256[](index);
        for (uint256 i = 0; i < index; ++i) {
            tokens[i] = tmp[i];
        }

        return tokens;
    }

    // should never be used inside of transaction because of gas fee
    function totalRewards(address account)
        public
        view
        returns (uint256[] memory)
    {
        uint256[] memory tokenIds = tokensOfOwner(account);
        uint256[] memory totalReward = new uint256[](reward.length);

        for (uint256 i; i < reward.length; ) {
            uint256 tokenId;
            uint256 totalTime;
            for (uint256 j; j < tokenIds.length; ) {
                tokenId = tokenIds[j];
                Stake storage stakedUser = vault[tokenId];
                totalTime = block.timestamp - stakedUser.since[i];
                totalReward[i] +=
                    (totalTime * 10000) /
                    durationRate[reward[i]][
                        IERC721Burnable(nft).checkLevel(tokenId) - 1
                    ];
                unchecked {
                    ++j;
                }
            }
            unchecked {
                ++i;
            }
        }

        return totalReward;
    }

    // should never be used inside of transaction because of gas fee
    function idRewards(uint256 tokenId) public view returns (uint256[] memory) {
        require(vault[tokenId].owner != address(0x0), "Token Not Staked");
        uint256[] memory totalReward = new uint256[](reward.length);
        uint256 totalTime;
        for (uint256 i; i < reward.length; ) {
            Stake storage stakedUser = vault[tokenId];
            totalTime = block.timestamp - stakedUser.since[i];
            totalReward[i] =
                (totalTime * 10000) /
                durationRate[reward[i]][
                    IERC721Burnable(nft).checkLevel(tokenId) - 1
                ];
            unchecked {
                ++i;
            }
        }

        return totalReward;
    }

    function onERC721Received(
        address,
        address from,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
        require(from != address(0x0), "Cannot send nfts to Vault directly");
        return IERC721Receiver.onERC721Received.selector;
    }
}
"
