    function burn(uint256 tokenId) external returns (bool);

    function mint(address user, uint256 quantity) external returns (bool);

    function burnFrom(address user, uint256[] calldata tokenId)
        external
        returns (bool);

    function checkLevel(uint256 tokenId) external view returns (uint256);
}
