// File contracts/solc-0_8_x/DistributorERC20V3.sol


pragma solidity ^0.8.17;






contract DistributorERC20V3 is OptimizedCommittee, ReentrancyGuard, NativeRepresentation {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    enum DISTRIBUTE_TYPE {
        TRANSFER_ERC20,
        TRANSFER_NATIVE,
        MINT
    }

    struct TokenConfig {
        address token;
        DISTRIBUTE_TYPE distributeType;
        address withdrawTo;
    }

    address public bridge;

    // The list of addresses that can transfer tokens.
    EnumerableSet.AddressSet private _whitelist;

    mapping(bytes32 => bool) public isSignatureUsed;

    mapping(address => DISTRIBUTE_TYPE) public distributeType;
    // token address => withdraw to Address
    mapping(address => address) public withdrawToByToken;

    event Distribute(
        address indexed token,
        DISTRIBUTE_TYPE indexed distributeType,
        address indexed recipient,
        uint256 amount
    );

    event AddAllowAddress(address indexed addr);
    event RemoveAllowAddress(address indexed addr);
    event WithdrawToAddressByTokenSet(address indexed sender, address indexed token, address indexed withdrawToAddress);
    event Withdraw(address indexed sender, address indexed token, address indexed withdrawToAddress, uint256 amount);

    receive() external payable {
        require(
            msg.sender == bridge || _whitelist.contains(msg.sender) || msg.sender == committee,
            "INVALID_NATIVE_SENDER"
        );
    }

    constructor(address committee_, address bridge_, TokenConfig[] memory tokenConfigs_, address[] memory whitelist_) {
        committee = committee_;
        bridge = bridge_;

        for (uint256 i; i < whitelist_.length; i++) {
            _whitelist.add(whitelist_[i]);
        }

        distributeType[NATIVE_REPRESENTATION] = DISTRIBUTE_TYPE.TRANSFER_NATIVE;
        for (uint256 i; i < tokenConfigs_.length; i++) {
            if (tokenConfigs_[i].token != NATIVE_REPRESENTATION) {
                require(tokenConfigs_[i].distributeType != DISTRIBUTE_TYPE.TRANSFER_NATIVE, "INVALID_TYPE");
                distributeType[tokenConfigs_[i].token] = tokenConfigs_[i].distributeType;
            }
            withdrawToByToken[tokenConfigs_[i].token] = tokenConfigs_[i].withdrawTo;
        }
    }

    modifier onlyWhitelisted() {
        require(_whitelist.contains(msg.sender), "ONLY_WHITELISTED");
        _;
    }

    function isAllowAddress(address addr_) external view returns (bool) {
        return _whitelist.contains(addr_);
    }

    function addAllowAddress(address addr_) external onlyCommittee {
        require(_whitelist.add(addr_), "ALREADY_EXISTED");
        emit AddAllowAddress(addr_);
    }

    function removeAllowAddress(address addr_) external onlyCommittee {
        require(_whitelist.remove(addr_), "NOT_FOUND");
        emit RemoveAllowAddress(addr_);
    }

    function setWithdrawToAddressByToken(address token_, address withdrawTo_) external onlyCommittee {
        withdrawToByToken[token_] = withdrawTo_;
        emit WithdrawToAddressByTokenSet(msg.sender, token_, withdrawTo_);
    }

    function setDistributeType(address token_, DISTRIBUTE_TYPE distributeType_) external onlyCommittee {
        if (token_ != NATIVE_REPRESENTATION) {
            require(distributeType_ != DISTRIBUTE_TYPE.TRANSFER_NATIVE, "INVALID_TYPE");
            distributeType[token_] = distributeType_;
        }
    }

    function setBridge(address bridge_) external onlyCommittee {
        bridge = bridge_;
    }

    function transfer(address token_, address recipient_, uint256 amount_) external onlyWhitelisted nonReentrant {
        _transfer(token_, recipient_, amount_);
    }

    function transfer(
        address token_,
        address recipient_,
        uint256 amount_,
        bytes32 signature_
    ) external onlyWhitelisted nonReentrant {
        require(!isSignatureUsed[signature_], "SIGNATURE_USED");
        isSignatureUsed[signature_] = true;

        _transfer(token_, recipient_, amount_);
    }

    function _transfer(address token_, address recipient_, uint256 amount_) private {
        DISTRIBUTE_TYPE distributeType_ = distributeType[token_];
        if (distributeType_ == DISTRIBUTE_TYPE.TRANSFER_ERC20) {
            require(IERC20(token_).balanceOf(address(this)) >= amount_, "NOT_ENOUGH_BALANCE");
            IERC20(token_).safeTransfer(recipient_, amount_);
        } else if (distributeType_ == DISTRIBUTE_TYPE.TRANSFER_NATIVE) {
            require(address(this).balance >= amount_, "NOT_ENOUGH_BALANCE_NATIVE");
            payable(recipient_).transfer(amount_);
        } else {
            IMint(token_).mint(recipient_, amount_);
        }
        emit Distribute(token_, distributeType_, recipient_, amount_);
    }

    function withdraw(address token_, uint256 amount_) external onlyCommittee {
        _withdraw(token_, amount_);
    }

    function withdraw(address token_) external onlyCommittee {
        uint256 bal = token_ == NATIVE_REPRESENTATION ? address(this).balance : IERC20(token_).balanceOf(address(this));
        _withdraw(token_, bal);
    }

    function _withdraw(address token_, uint256 amount_) private {
        address withdrawTo = withdrawToByToken[token_];
        require(withdrawTo != address(0), "WITHDRAW_TO_ZERO_ADDRESS");

        if (token_ == NATIVE_REPRESENTATION) {
            bool success;
            (success, ) = withdrawTo.call{ value: amount_ }("");
            require(success, "UNSUCCESSFUL_TRANSFER");
        } else {
            IERC20(token_).safeTransfer(withdrawTo, amount_);
        }

        emit Withdraw(msg.sender, token_, withdrawTo, amount_);
    }
}
