    "contracts/APELoot.sol": {
      "content": "pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";

interface IERC1155 {
    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external;

    function balanceOf(
        address account,
        uint256 id
    ) external view returns (uint256);
}

contract APELoot is VRFV2WrapperConsumerBase {
    uint32 callbackGasLimit = 200000;
    uint16 requestConfirmations = 3;
    uint32 numWords = 2;

    address constant linkAddress = 0x514910771AF9Ca656af840dff83E8264EcF986CA;
    address constant wrapperAddress = 0x5A861794B927983406fCE1D062e00b9368d97Df6;

    IERC1155 public APE_MINTER = IERC1155(address(0x4161857eDF466a6065441821c755D577Ed637c88));
    IERC1155 public APE_COLLECTION = IERC1155(address(0xe6ad00f61B9BedeA6595DD8d75bdeFbf79C33D59));
    uint256 public APE_IDS = 7;

    mapping(address => uint256) public addressToRequest;
    mapping(uint256 => uint256[]) public requestToRandomNumber;
    mapping(uint256 => bool) public requestIdFulfilled;
    mapping(address => uint256) public claimsPerAddress;

    uint256 public maxClaims = 0;

    address internal owner;
    uint256 public dividor = 2;
    uint256 public minimumCollectibles = 5;

    constructor() VRFV2WrapperConsumerBase(linkAddress, wrapperAddress) {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(owner == msg.sender, "Not owner");
        _;
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(
            requestIdFulfilled[_requestId] == false,
            "request already fulfilled"
        );
        requestToRandomNumber[_requestId] = _randomWords;
        requestIdFulfilled[_requestId] = true;
    }

    function claim() public returns (uint256 requestId) {
        require(
            addressToRequest[msg.sender] == 0,
            "You already have a request"
        );

        require(
            claimsPerAddress[msg.sender] < maxClaims,
            "You have reached the max claims"
        );

        uint256 collectibleCount = 0;
        for (uint256 i = 0; i <= APE_IDS; i++) {
            collectibleCount =
                collectibleCount +
                APE_COLLECTION.balanceOf(msg.sender, i);
        }

        require(
            collectibleCount >= minimumCollectibles,
            "You need to own the minimum no. of collectibles to claim"
        );

        requestId = requestRandomness(
            callbackGasLimit,
            requestConfirmations,
            numWords
        );

        addressToRequest[msg.sender] = requestId;
        return requestId;
    }

    function executeClaim() public {
        require(
            claimsPerAddress[msg.sender] < maxClaims,
            "You have reached the max claims"
        );

        uint256 request = addressToRequest[msg.sender];
        require(request != 0, "You don't have a request");

        require(requestIdFulfilled[request] == true, "Request not fulfilled");

        uint256[] memory chainlinkResponse = requestToRandomNumber[request];

        uint256 tokenId = chainlinkResponse[0] % dividor;

        claimsPerAddress[msg.sender]++;
        requestIdFulfilled[request] = false;

        APE_MINTER.mint(msg.sender, tokenId, 1, "");
    }

    function requestForAddressFullfilled(
        address _address
    ) public view returns (bool) {
        uint256 request = addressToRequest[_address];
        return requestIdFulfilled[request];
    }

    function withdrawLink() public onlyOwner {
        LinkTokenInterface link = LinkTokenInterface(linkAddress);
        require(
            link.transfer(msg.sender, link.balanceOf(address(this))),
            "Unable to transfer"
        );
    }

    function setApeMinter(address _apeMinter) public onlyOwner {
        APE_MINTER = IERC1155(_apeMinter);
    }

    function setApeCollection(address _apeCollection) public onlyOwner {
        APE_COLLECTION = IERC1155(_apeCollection);
    }

    function setApeIds(uint256 _apeIds) public onlyOwner {
        APE_IDS = _apeIds;
    }

    function setDividor(uint256 _dividor) public onlyOwner {
        dividor = _dividor;
    }

    function setMinimumCollectibles(uint256 _minimumCollectibles)
        public
        onlyOwner
    {
        minimumCollectibles = _minimumCollectibles;
    }

    function setCallbackGasLimit(uint32 _callbackGasLimit) public onlyOwner {
        callbackGasLimit = _callbackGasLimit;
    }

    function setRequestConfirmations(uint16 _requestConfirmations)
        public
        onlyOwner
    {
        requestConfirmations = _requestConfirmations;
    }

    function setMaxClaims(uint256 _maxClaims) public onlyOwner {
        maxClaims = _maxClaims;
    }

    function changeOwner(address _newOwner) public onlyOwner {
        owner = _newOwner;
    }
}
"
