      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v4.9.6/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract EUROCUP is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    enum Outcome {
        NotDecided,
        Win,
        Loss,
        Draw
    }
    enum BetOption {
        Win,
        Loss,
        Draw
    }

    struct Match {
        uint256 id;
        Outcome result;
    }

    struct Bet {
        uint256 matchId;
        BetOption option;
        uint256 amount;
        bool claimed;
    }

    IERC20 public token;
    uint256 public adminFeePercentage;
    uint256 public totalMatches;
    mapping(uint256 => Match) public matches;
    mapping(address => mapping(uint256 => Bet)) public bets;
    mapping(uint256 => mapping(BetOption => uint256)) public totalBets;

    event MatchAdded(uint256 matchId);
    event BetPlaced(address indexed user, uint256 matchId, BetOption option, uint256 amount);
    event ResultAnnounced(uint256 matchId, Outcome result);
    event RewardClaimed(address indexed user, uint256 matchId, uint256 reward);
    event AdminFeeUpdated(uint256 newFee);

    constructor(IERC20 _token, uint256 _adminFeePercentage) Ownable(msg.sender) {
        token = _token;
        adminFeePercentage = _adminFeePercentage;
    }

    function placeBet(uint256 matchId, BetOption option, uint256 amount) external nonReentrant {
        require(matches[matchId].result == Outcome.NotDecided, "Betting is closed for this match");
        require(amount > 0, "Bet amount must be greater than zero");
        require(bets[msg.sender][matchId].amount == 0, "Already placed a bet on this match");

        require(token.transferFrom(msg.sender, address(this), amount), "Token transfer failed");

        bets[msg.sender][matchId] = Bet(matchId, option, amount, false);
        totalBets[matchId][option] = totalBets[matchId][option].add(amount);

        emit BetPlaced(msg.sender, matchId, option, amount);
    }

    function announceResult(uint256 matchId, Outcome result) external onlyOwner {
        require(matches[matchId].result == Outcome.NotDecided, "Result already announced");
        require(result != Outcome.NotDecided, "Invalid result");

        matches[matchId].result = result;

        emit ResultAnnounced(matchId, result);
    }

    function claimReward(uint256 matchId) external nonReentrant {
        require(matches[matchId].result != Outcome.NotDecided, "Result not announced yet");

        Bet storage userBet = bets[msg.sender][matchId];
        require(userBet.amount > 0, "No bet placed on this match");
        require(!userBet.claimed, "Reward already claimed");

        BetOption winningOption;
        if (matches[matchId].result == Outcome.Win) {
            winningOption = BetOption.Win;
        } else if (matches[matchId].result == Outcome.Loss) {
            winningOption = BetOption.Loss;
        } else {
            winningOption = BetOption.Draw;
        }

        require(userBet.option == winningOption, "You did not win the bet");

        uint256 totalBetPool = totalBets[matchId][BetOption.Win]
            .add(totalBets[matchId][BetOption.Loss])
            .add(totalBets[matchId][BetOption.Draw]);

        uint256 adminFee = totalBetPool.mul(adminFeePercentage).div(100);
        uint256 rewardPool = totalBetPool.sub(adminFee);
        uint256 userShare = userBet.amount.mul(rewardPool).div(totalBets[matchId][winningOption]);

        userBet.claimed = true;

        require(token.transfer(owner(), adminFee), "Admin fee transfer failed");
        require(token.transfer(msg.sender, userShare), "Token transfer failed");

        emit RewardClaimed(msg.sender, matchId, userShare);
    }

    function setAdminFeePercentage(uint256 _adminFeePercentage) external onlyOwner {
        require(_adminFeePercentage <= 100, "Fee percentage cannot be more than 100");
        adminFeePercentage = _adminFeePercentage;
        emit AdminFeeUpdated(_adminFeePercentage);
    }

    function withdrawTokens(uint256 amount) external onlyOwner nonReentrant {
        require(amount <= token.balanceOf(address(this)), "Insufficient contract balance");
        require(token.transfer(owner(), amount), "Token transfer failed");
    }

    function getTotalBets(uint256 matchId, BetOption option) external view returns (uint256) {
        return totalBets[matchId][option];
    }

    function getUserBet(uint256 matchId) external view returns (Bet memory) {
        return bets[msg.sender][matchId];
    }
}"
