      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ComicBurn is Ownable {
    constructor() Ownable(msg.sender) {}
    mapping(address => uint256[]) public userBurnTokens;

    mapping(uint256 => bool) public animatedToken;

    uint256 public maxSupply = 625;
    uint256 public tokensToBurn = 10;
    address public comicAddress = 0x21493e9fD8aEEc95bf7608A51c75333F40eB192A;
    address public burnAddress = 0x000000000000000000000000000000000000dEaD;

    mapping(uint256 => uint256) public tokenEditions;

    uint256 public totalBurned;

    bool public burnEnabled = false;

    event BurnToken(uint256 token, uint256 timeStamp, address user);
    event TokenUpdated(uint256 token, uint256 timeStamp, address user);

    error BurnNotEnabled();
    error InvalidTokenCount();
    error MaxSupplyReached();
    error InvalidToken();
    error NotTokenOwner(uint256 tokenId);
    error InvalidTokenType(uint256 tokenId);

    function burn(uint256[] calldata tokenIds, uint256 _editionType) external {
        if (!burnEnabled) revert BurnNotEnabled();
        if (tokenIds.length != tokensToBurn) revert InvalidTokenCount();
        if (totalBurned + tokenIds.length > maxSupply)
            revert MaxSupplyReached();

        IERC721 comic = IERC721(comicAddress);

        if (
            comic.ownerOf(tokenIds[0]) != msg.sender ||
            tokenEditions[tokenIds[0]] != _editionType ||
            animatedToken[tokenIds[0]]
        ) {
            revert InvalidToken();
        }

        for (uint256 i = 1; i < tokenIds.length; i++) {
            if (comic.ownerOf(tokenIds[i]) != msg.sender)
                revert NotTokenOwner(tokenIds[i]);
            if (tokenEditions[tokenIds[i]] != _editionType)
                revert InvalidTokenType(tokenIds[i]);
            if (animatedToken[tokenIds[i]])
                revert InvalidTokenType(tokenIds[i]);

            comic.transferFrom(msg.sender, burnAddress, tokenIds[i]);
            userBurnTokens[msg.sender].push(tokenIds[i]);
            emit BurnToken(tokenIds[i], block.timestamp, msg.sender);
        }
        uint256 tokenToUpdate = tokenIds[0];
        emit TokenUpdated(tokenToUpdate, block.timestamp, msg.sender);
        animatedToken[tokenToUpdate] = true;
        totalBurned++;
    }

    function setMaxSupply(uint256 _maxSupply) public onlyOwner {
        maxSupply = _maxSupply;
    }

    function setTokensToBurn(uint256 _tokensToBurn) public onlyOwner {
        tokensToBurn = _tokensToBurn;
    }

    function isAnimated(uint256 _tokenId) public view returns (bool) {
        return animatedToken[_tokenId];
    }

    function setBurnEnabled(bool _state) public onlyOwner {
        burnEnabled = _state;
    }

    function setComicAddress(address _comicAddress) public onlyOwner {
        comicAddress = _comicAddress;
    }

    function setTokenEditions(
        uint256[] calldata _tokens,
        uint256 _editionType
    ) public onlyOwner {
        for (uint256 i = 0; i < _tokens.length; i++) {
            tokenEditions[_tokens[i]] = _editionType;
        }
    }

    function balanceOfBurned(address _user) public view returns (uint256) {
        return userBurnTokens[_user].length;
    }
}
