    "contracts/MTKCrowdsale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "./libraries/TransferHelper.sol";

contract MTKCrowdsale is Ownable, ReentrancyGuard {
    address public immutable USDT_ADDRESS;
    address public immutable USDC_ADDRESS;
    address public immutable MTK_ADDRESS;
    address public treasury;
    /**
     * @dev this value is calculated considering decimals
     * e.x 50000 = 0.05 USD as both USDT and USDC has equal decimals - 6.
     */
    uint256 public price;

    mapping(address => uint256) public purchasedAmounts;

    bool public saleEnabled;
    bool public claimEnabled;

    uint256 public constant MTK_DECIMALS = 18;

    event Sell(address indexed buyer, address indexed token, uint256 mtkAmount, uint256 price);
    event ChangeSaleStatus(bool status);
    event ChangeClaimStatus(bool status);
    event SetPrice(uint256 _price);
    event SetTreasury(address treasury);
    event Withdraw(address token, uint256 amount);
    event Claim(address indexed buyer, uint256 mtkAmount);

    constructor(address _USDT_ADDRESS, address _USDC_ADDRESS, address _MTK_ADDRESS) {
        USDT_ADDRESS = _USDT_ADDRESS;
        USDC_ADDRESS = _USDC_ADDRESS;
        MTK_ADDRESS = _MTK_ADDRESS;
    }

    modifier onlyWhenOnSale() {
        require(saleEnabled || price != 0, "Sale is not active now");
        _;
    }

    modifier onlyWhenClaimEnabled() {
        require(claimEnabled, "Claim is not enabled yet");
        _;
    }

    function changeSaleStatus() external onlyOwner {
        saleEnabled = !saleEnabled;

        emit ChangeSaleStatus(saleEnabled);
    }

    function changeClaimStatus(bool _claimEnabled) external onlyOwner {
        claimEnabled = _claimEnabled;

        emit ChangeClaimStatus(claimEnabled);
    }

    function setTreasury(address _treasury) external onlyOwner {
        treasury = _treasury;

        emit SetTreasury(_treasury);
    }

    function setPrice(uint256 _price) external onlyOwner {
        price = _price;

        emit SetPrice(_price);
    }

    /**
     * @dev Users can buy MTK based on the signed price
     * @param tokenAmount - this is the USDT amount
     */

    function buyMTK(uint256 tokenAmount, address token) external onlyWhenOnSale nonReentrant {
        require(token == USDT_ADDRESS || token == USDC_ADDRESS, "Only USDT and USDC are allowed.");

        address buyer = msg.sender;

        uint256 mtkAmount = (tokenAmount / price) * (10 ** MTK_DECIMALS);

        purchasedAmounts[buyer] += mtkAmount;

        if (treasury != address(0)) {
            TransferHelper.safeTransferFrom(token, buyer, treasury, tokenAmount);
        } else {
            TransferHelper.safeTransferFrom(token, buyer, address(this), tokenAmount);
        }

        emit Sell(buyer, token, mtkAmount, price);
    }

    function claim() external onlyWhenClaimEnabled nonReentrant {
        address buyer = msg.sender;
        uint256 mtkAmount = purchasedAmounts[buyer];
        require(mtkAmount > 0, "No token to claim");

        purchasedAmounts[buyer] = 0;

        TransferHelper.safeTransfer(MTK_ADDRESS, buyer, mtkAmount);

        emit Claim(buyer, mtkAmount);
    }

    function withdraw(address token, uint256 amount) external onlyOwner {
        TransferHelper.safeTransfer(token, msg.sender, amount);

        emit Withdraw(token, amount);
    }
}
