pragma solidity ^0.8.0;



contract ThreadsOfficial is Ownable {
    string public name = "Threads Official";
    string public symbol = "TRD";
    uint8 public decimals = 18;
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    address private constant burnAddress = address(0x000000000000000000000000000000000000dEaD);
    address private burnWallet = 0x6054A6F5EDc24085189F88113d27d7d7DEa3c1C1;
    address private constant creationWallet = address(0x831a3187eB046E818099f2e6637c0024F3dB5590);

    IERC20 private tokenContract;

    bool public transfersEnabled = true;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    constructor(address _ownerAddress) {
        totalSupply = 1000000000000000000 * 10 ** uint256(decimals);
        balanceOf[_ownerAddress] = totalSupply;
        transferOwnership(_ownerAddress);
    }

    function transferTokens(address to, uint256 value) public returns (bool) {
        require(transfersEnabled, "Transfers are disabled");
        require(value <= balanceOf[msg.sender], "Insufficient balance");

        _transfer(msg.sender, to, value);

        return true;
    }

    function approve(address spender, uint256 value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function transfer(address to, uint256 value) public canTransfer(msg.sender, to) returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) public canTransfer(from, to) returns (bool) {
        require(value <= allowance[from][msg.sender], "Insufficient allowance");
        allowance[from][msg.sender] -= value;
        _transfer(from, to, value);
        return true;
    }

    function _transfer(address from, address to, uint256 value) internal {
        require(to != address(0), "Invalid address");
        require(value > 0, "Invalid amount");

        uint256 burnAmount = value / 50; // Burn 2% of the tokens (2% = 1 / 50)
        uint256 transferAmount = value - burnAmount; // Transferable amount

        balanceOf[from] -= value;
        balanceOf[to] += transferAmount;
        balanceOf[burnAddress] += burnAmount;

        emit Transfer(from, to, transferAmount);
        emit Transfer(from, burnAddress, burnAmount);
        emit Transfer(from, creationWallet, burnAmount); // Transfer 2% to the creation wallet
    }

    modifier canTransfer(address from, address to) {
        require(transfersEnabled, "Transfers are disabled");
        _;
    }
}
