        lsdVaultAddress = IreshETH(reshETHAddress).timelock_address();

        //Give infinite approval to spend to LSDVault
        //UnshETH LSDs
        TransferHelper.safeApprove(wstETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(sfrxETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(rETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(cbETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(ankrETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(swETHAddress, lsdVaultAddress, type(uint256).max);

        //new LSDs
        //IERC20(wsgETHAddress).approve(lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(wsgETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(vETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(mpETHAddress, lsdVaultAddress, type(uint256).max);

        //META LSDs
        TransferHelper.safeApprove(unshETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(woETHAddress, lsdVaultAddress, type(uint256).max);
        TransferHelper.safeApprove(YieldETHAddress, lsdVaultAddress, type(uint256).max);

