    */

    function updateVault() external {
        lsdVaultAddress = IreshETH(reshETHAddress).timelock_address();
        emit VaultUpdated(lsdVaultAddress);
    }

    /*
    ============================================================================
    Direct LSD deposit functions
    ============================================================================
    */

    function deposit_lsd(address lsdAddress, uint256 amount) external nonReentrant {
        // Assume user has approved token
        TransferHelper.safeTransferFrom(lsdAddress, msg.sender, address(this), amount);
        _deposit_lsd(lsdAddress, amount);
    }

    function _deposit_lsd(address lsdAddress, uint256 amount) internal {
        // Call LSDVault to mint reshETH
        ILSDVault(lsdVaultAddress).deposit(lsdAddress, amount);
        // Send reshETH to the msg.sender
        uint256 reshETHMinted = IERC20(reshETHAddress).balanceOf(address(this));
        TransferHelper.safeTransfer(reshETHAddress, msg.sender, reshETHMinted);
        emit DepositLsd(msg.sender, lsdAddress, amount, reshETHMinted);
