    "veOrdiBankToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "./IOrdiBankToken.sol";
import "./IRewardsOrdiBank.sol";
import "hardhat/console.sol";

contract VeOrdiBankToken is
    Ownable(msg.sender),
    ReentrancyGuard,
    ERC20("Ordiswap OrdiBank Staking token", "veORBK")
{
    using Address for address;
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;
    using SafeERC20 for IOrdiBankToken;

    IOrdiBankToken public immutable ordiBankToken; // ORBK token to convert to/from
    EnumerableSet.AddressSet private _transferWhitelist; // addresses allowed to send/receive veORBK

    // Maximum allowed reward (40 million tokens)
    uint256 public maxReward = 40_000_000 * 10 ** 18;
    uint256 public blocksPerYear = 2_300_000;

    event StakeSucceeded(
        address sender,
        uint256 amount,
        uint256 period,
        uint256 startBlock
    );
    event RedeemSuceeded(
        address sender,
        uint256 amount,
        uint256 reward,
        uint256 period,
        uint256 startBlock,
        uint256 redeemBlock
    );
    event RedeemBurned(
        address sender,
        uint256 amount,
        uint256 burnedAmount,
        uint256 period,
        uint256 startBlock,
        uint256 burnBlock
    );
    event ClaimSucceeded(address sender, uint256 amount);

    struct Stake {
        uint256 amount;
        uint256 startBlock;
        uint256 lastClaimedBlock;
        uint256 startTime;
        uint256 lockPeriod;
    }

    // Constants for lock periods
    uint256[4] public lockPeriods = [3, 6, 12, 24];
    uint256[4] public totalStakedInPeriod = [0, 0, 0, 0];
    uint256[4] public totalRedeemRewards = [0, 0, 0, 0];
    uint256[4] public emissionRates = [
        4 * 10 ** 17, // 0.4 tokens per block
        6 * 10 ** 17, // 0.6 tokens per block
        1 * 10 ** 18, // 1 tokens per block
        2 * 10 ** 18 // 2 tokens per block
    ]; // tokens per block per pool in wei
    uint256[4] public burnPercentages = [20, 30, 40, 50];
    uint256 public tvl;
    uint256 public totalBurned;

    IRewardsOrdiBank public rewardBank;

    // Mapping of user to their stakes
    mapping(address => Stake[]) public stakes;

    constructor(address _ordiBankToken, address _rewardBankAddress) {
        require(
            _rewardBankAddress != address(0),
            "Reward bank address cannot be the zero address"
        );

        ordiBankToken = IOrdiBankToken(_ordiBankToken);
        rewardBank = IRewardsOrdiBank(_rewardBankAddress);
        _transferWhitelist.add(address(this));
    }

    /*
     * @dev Check if a stake entry exists
     */
    modifier validateStake(address userAddress, uint256 stakeIndex) {
        require(
            stakeIndex < stakes[userAddress].length,
            "validateStake: stake entry does not exist"
        );
        _;
    }

    function calculateReward(
        uint256 amount,
        uint256 period,
        uint256 lastClaimedBlock
    ) private view returns (uint256) {
        // Calculate total rewards distributed so far
        uint256 totalReward = 0;
        for (uint256 i = 0; i < totalRedeemRewards.length; i++) {
            totalReward += totalRedeemRewards[i];
        }

        // Check if the max reward has been reached
        if (totalReward >= maxReward) {
            return 0;
        }

        // Remaining reward that can be distributed
        uint256 remainingReward = maxReward - totalReward;

        // Calculate the reward based on user's share of the pool
        uint256 blockCount = block.number - lastClaimedBlock;
        uint256 poolEmissionRate = emissionRates[period];
        uint256 userShare = (amount * 1e18) / totalStakedInPeriod[period]; // user share in 1e18
        uint256 reward = (blockCount * poolEmissionRate * userShare) / 1e18;

        // Adjust reward if it exceeds the remaining available tokens
        if (reward > remainingReward) {
            reward = remainingReward;
        }

        return reward;
    }

    function stake(uint256 amount, uint256 period) public nonReentrant {
        /// Require that the period is in our lockPeriods array
        require(
            period == 0 || period == 1 || period == 2 || period == 3,
            "Invalid lock period"
        );

        ordiBankToken.transferFrom(msg.sender, address(this), amount);

        // * Amount is on wei
        _mint(msg.sender, amount);

        tvl += amount;
        totalStakedInPeriod[period] += amount;

        stakes[msg.sender].push(
            Stake(amount, block.number, block.number, block.timestamp, period)
        );

        emit StakeSucceeded(
            msg.sender,
            amount,
            lockPeriods[period],
            block.number
        );
    }

    function claim() public nonReentrant {
        require(
            stakes[msg.sender].length > 0,
            "validateStake: stake does not exist"
        );
        uint256 reward = 0;
        for (uint256 i = 0; i < stakes[msg.sender].length; i++) {
            Stake storage userStake = stakes[msg.sender][i];
            uint256 stakeReward = calculateReward(
                userStake.amount,
                userStake.lockPeriod,
                userStake.lastClaimedBlock
            );
            reward += stakeReward;
            totalRedeemRewards[userStake.lockPeriod] += stakeReward;
            userStake.lastClaimedBlock = block.number;
        }
        rewardBank.withdrawORBKTokens(msg.sender, reward);

        emit ClaimSucceeded(msg.sender, reward);
    }

    function redeem(
        uint256 index
    ) public validateStake(msg.sender, index) nonReentrant {
        Stake storage userStake = stakes[msg.sender][index];

        // Get back the veORBK and burn them
        _transfer(msg.sender, address(this), userStake.amount);
        uint256 reward = calculateReward(
            userStake.amount,
            userStake.lockPeriod,
            userStake.lastClaimedBlock
        );

        if (
            block.timestamp >=
            userStake.startTime + lockPeriods[userStake.lockPeriod] * 30 days
        ) {
            ordiBankToken.transfer(msg.sender, userStake.amount);

            emit RedeemSuceeded(
                msg.sender,
                userStake.amount,
                reward,
                userStake.lockPeriod,
                userStake.startBlock,
                block.number
            );
        } else {
            uint256 penalty = (userStake.amount *
                burnPercentages[userStake.lockPeriod]) / 100;
            totalBurned += penalty;
            ordiBankToken.safeTransfer(msg.sender, userStake.amount - penalty);
            ordiBankToken.burn(penalty);
            emit RedeemBurned(
                msg.sender,
                userStake.amount,
                penalty,
                userStake.lockPeriod,
                userStake.startBlock,
                block.number
            );
        }
        // Deduct the reward from the total reward pool
        totalRedeemRewards[userStake.lockPeriod] += reward;
        // Send the user the rewards
        rewardBank.withdrawORBKTokens(msg.sender, reward);

        tvl -= userStake.amount;
        totalStakedInPeriod[userStake.lockPeriod] -= userStake.amount;
        _burn(address(this), userStake.amount);
        _deleteStakeEntry(index);
    }

    function updateBankAddress(address _newBankAddress) public onlyOwner {
        rewardBank = IRewardsOrdiBank(_newBankAddress);
    }

    function updateEmissionRates(
        uint256[] memory newEmissionRates
    ) public onlyOwner {
        require(newEmissionRates.length == 4, "Invalid array length");

        uint256[4] memory tempEmissionRates;
        for (uint256 i = 0; i < 4; i++) {
            tempEmissionRates[i] = newEmissionRates[i]; // Convert to wei
        }

        emissionRates = tempEmissionRates;
    }

    function updateLockPeriod(
        uint256[] memory newLockPeriods
    ) public onlyOwner {
        require(newLockPeriods.length == 4, "Invalid array length");
        uint256[4] memory tempLockPeriods;
        for (uint256 i = 0; i < 4; i++) {
            tempLockPeriods[i] = newLockPeriods[i];
        }
        lockPeriods = tempLockPeriods;
    }

    function updateBurnPercentages(
        uint256[] memory newPercentages
    ) public onlyOwner {
        require(newPercentages.length == 4, "Invalid array length");

        uint256[4] memory tempRatio;
        for (uint256 i = 0; i < 4; i++) {
            tempRatio[i] = newPercentages[i];
        }

        burnPercentages = tempRatio;
    }

    function updateMaxRewards(uint256 _reward) public onlyOwner {
        require(_reward > 0, "Invalid max reward");
        maxReward = _reward;
    }

    function updateBlocksPerYear(uint256 _blocksPerYear) public onlyOwner {
        require(_blocksPerYear > 0, "Invalid blocks per year");
        blocksPerYear = _blocksPerYear;
    }

    function getTotalRewards(
        address userAddress
    ) public view returns (uint256) {
        require(
            stakes[userAddress].length > 0,
            "validateStake: stake does not exist"
        );
        uint256 ORBKrewards = 0;
        for (uint256 i = 0; i < stakes[userAddress].length; i++) {
            Stake memory userStake = stakes[userAddress][i];
            ORBKrewards += calculateReward(
                userStake.amount,
                userStake.lockPeriod,
                userStake.lastClaimedBlock
            );
        }
        return ORBKrewards;
    }

    function calculateExpectedYearlyRewards(
        uint256 amount,
        uint256 period
    ) external view returns (uint256) {
        require(
            period == 0 || period == 1 || period == 2 || period == 3,
            "Invalid lock period"
        );

        uint256 totalStaked = totalStakedInPeriod[period] + amount; // Total staked including the new amount
        uint256 userShare = (amount * 1e18) / totalStaked; // User share in 1e18

        // Calculate total rewards emitted over a year for the chosen period
        uint256 totalYearlyRewards = blocksPerYear * emissionRates[period];

        // Calculate the user's expected rewards
        uint256 userExpectedRewards = (totalYearlyRewards * userShare) / 1e18;

        return userExpectedRewards;
    }

    function getAPR(
        uint256 amount,
        uint256 lockPeriod
    ) external view returns (uint256) {
        uint256 totalStaked = totalStakedInPeriod[lockPeriod] + amount;
        uint256 userShare = (amount * 1e18) / totalStaked; // user share in 1e18
        uint256 totalRewards = (blocksPerYear *
            emissionRates[lockPeriod] *
            userShare);
        return totalRewards / amount / 1e18;
    }

    function getUserAverageAPR(
        address userAddress
    ) external view returns (uint256) {
        require(
            stakes[userAddress].length > 0,
            "validateStake: stake does not exist"
        );

        uint256 totalAPR = 0;
        // uint256 totalUserStaked = 0;
        uint256 periodsStaked = 0;

        for (
            uint256 lockPeriodIndex = 0;
            lockPeriodIndex < lockPeriods.length;
            lockPeriodIndex++
        ) {
            uint256 periodUserStaked = 0;
            uint256 periodTotalStaked = totalStakedInPeriod[lockPeriodIndex];

            if (periodTotalStaked == 0) {
                continue;
            }

            for (uint256 i = 0; i < stakes[userAddress].length; i++) {
                if (stakes[userAddress][i].lockPeriod == lockPeriodIndex) {
                    periodUserStaked += stakes[userAddress][i].amount;
                }
            }

            if (periodUserStaked > 0) {
                uint256 userShare = (periodUserStaked * 1e18) /
                    periodTotalStaked;
                uint256 totalRewards = blocksPerYear *
                    emissionRates[lockPeriodIndex] *
                    userShare;
                uint256 periodAPR = totalRewards / periodUserStaked / 1e18;
                totalAPR += periodAPR;
                periodsStaked += 1;
            }
        }

        // require(totalUserStaked > 0, "No active stakes found");

        return totalAPR / periodsStaked;
    }

    function getUserStake(
        address userAddress,
        uint256 stakeIndex
    )
        external
        view
        validateStake(userAddress, stakeIndex)
        returns (
            uint256 index,
            uint256 amount,
            uint256 startBlock,
            uint256 lastClaimedBlock,
            uint256 startTime,
            uint256 lockPeriod,
            uint256 reward
        )
    {
        Stake storage stakeInfo = stakes[userAddress][stakeIndex];

        uint256 ORBKreward = calculateReward(
            stakeInfo.amount,
            stakeInfo.lockPeriod,
            stakeInfo.lastClaimedBlock
        );
        return (
            stakeIndex,
            stakeInfo.amount,
            stakeInfo.startBlock,
            stakeInfo.lastClaimedBlock,
            stakeInfo.startTime,
            stakeInfo.lockPeriod,
            ORBKreward
        );
    }

    /**
     * @dev returns quantity of "userAddress" stakes
     */
    function getUserStakesLength(
        address userAddress
    ) external view returns (uint256) {
        return stakes[userAddress].length;
    }

    function _deleteStakeEntry(uint256 index) internal {
        require(
            index < stakes[msg.sender].length,
            "deleteStakeEntry: Index out of bounds"
        );

        for (uint256 i = index; i < stakes[msg.sender].length - 1; i++) {
            stakes[msg.sender][i] = stakes[msg.sender][i + 1];
        }
        stakes[msg.sender].pop();
    }
}
