      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "solmate/src/tokens/WETH.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";

/**
 * @title Arbitrage
 * @dev A contract for executing arbitrage trades between two decentralized exchanges (DEXes).
 */
contract ArbitrageTrader1 is ReentrancyGuard {
    address payable private owner;
    address payable private profitAddress;
    uint32 private constant TRADE_DEADLINE_EXTENSION = 1800; // 5 minutes
    bool private isActive;

    constructor (address pAddress) {
        owner = payable(msg.sender);
        profitAddress = payable(pAddress);
        isActive = true;
    }  

    // Allow the contract to receive ETH
    //@dev Fallback function to 
    receive() external payable {}

    // Events
    event FundsDepositedEth();
    event ArbitrageTradeComplete(uint256 finalAmountAfterBuy, uint256 finalAmountAfterSell);
    event WithdrawERC20Complete(uint256 amountSent);
    event WithdrawETHComplete(uint256 amountSent);
    event ContractShutdown(); // Optional: Notify that the contract is being shut down

    /**
     * @dev Executes an arbitrage trade between two DEXes.
     * @param amountIn Amount of input asset to be traded.
     * @param firstDexPath Array representing the path on the first DEX for the trade.
     * @param dexes Array of addresses of the DEX routers.
     * @notice The input token of the second DEX swap is the output token of the first DEX swap
     */      //* @param secondDexOutputToken Address of the output token on the second DEX.
    function arbitrageTrade(uint amountIn, uint minAmountOut, address[] calldata firstDexPath, address[] calldata dexes) external onlyOwner onlyWhenActive nonReentrant {
        
        uint deadline = block.timestamp + TRADE_DEADLINE_EXTENSION; // Ensure trade is valid for 5 minutes

        // Access both DEX routers
        IUniswapV2Router02 dexRouter1 = IUniswapV2Router02(dexes[0]);
        IUniswapV2Router02 dexRouter2 = IUniswapV2Router02(dexes[1]);

        IERC20(firstDexPath[0]).approve(address(dexRouter1), amountIn); // Approve first swap

        // Buy input asset on first DEX
        uint[] memory amounts = dexRouter1.swapExactTokensForTokens(
            amountIn,
            minAmountOut,
            firstDexPath,
            address(this),
            deadline
        );

        // Create path for second swap
        address[] memory outputPath = new address[](2);
        outputPath[0] = firstDexPath[1];
        outputPath[1] = firstDexPath[0]; //secondDexOutputToken;

        IERC20(outputPath[0]).approve(address(dexRouter2), amounts[1]); // Approve second swap

        // Sell tokens on second DEX for secondDexOutputToken
        uint[] memory amounts2 = dexRouter2.swapExactTokensForTokens(
            amounts[1],
            minAmountOut,
            outputPath,
            address(this),
            deadline
        );

        // triger event
        emit ArbitrageTradeComplete(amounts[1], amounts2[1]);
    }

    function deposit(IERC20 token, uint256 amount) external onlyOwner onlyWhenActive nonReentrant {
        token.transferFrom(msg.sender, address(this), amount);
        
        // triger event
        emit FundsDepositedEth();
    }

    function withdrawERC20(IERC20 token, uint256 amount) external onlyOwner nonReentrant{
        // Check the addresses
        token.transfer(profitAddress, amount);
        
        // triger event
        emit WithdrawERC20Complete(amount);
    }

    // Withdraw ETH  from the contract
    function withdrawEth() external onlyOwner nonReentrant {
        uint ethBalance = address(this).balance; // get the balance of ETH
        
        // Using transfer to automatically revert on failure
        profitAddress.transfer(ethBalance);

        // triger event
        emit WithdrawETHComplete(ethBalance);
    }

    //get the ETH balance on the contract
    function getEthBalance() external view onlyOwner onlyWhenActive returns (uint256) {
        return address(this).balance;
    }

    //get the balance of ERC20 tokens on the contract
    function getERC20Balance(IERC20 Token) external view onlyOwner onlyWhenActive returns (uint256) {
        return IERC20(Token).balanceOf(address(this)); // get the balance of the token on this contract
    }

    function shutdownContract() external onlyOwner nonReentrant {
        isActive = false;
        
        // triger event
        emit ContractShutdown();
    }

    //Only owner function
    modifier onlyOwner() {
        require(owner == msg.sender, "Only the owner can perform this function.");
        _;
    }
    
    // Modifier to allow function execution only when the contract is active
    modifier onlyWhenActive() {
        require(isActive, "This Contract is not active and can not be used");
        _;
    }
}"
