      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract ContractBuyCard is Ownable2Step, ReentrancyGuard {
    address private immutable _contractAddressTokenUsdt;
    address private immutable _contractAddressTokenUsdc;
    address private _seller;
    ERC20 private _tokenUSDT;
    ERC20 private _tokenUSDC;

    mapping(string => address) private _transactionUser;
    mapping(string => uint256) private _transactionAmount;
    mapping(string => address) private _transactionContract;
    mapping(string => uint256) private _transactionTimestamp;

    event Purchase(address indexed buyer, string transactionId, uint256 amount, address contractAddress);
    event Recharge(address indexed buyer, uint256 amount, address contractAddress);

    constructor(
        address initialOwner,
        address contractAddressTokenUsdt,
        address contractAddressTokenUsdc,
        address seller
    ) Ownable(initialOwner) {
        _contractAddressTokenUsdt = contractAddressTokenUsdt;
        _contractAddressTokenUsdc = contractAddressTokenUsdc;
        _seller = seller;
        _tokenUSDT = ERC20(contractAddressTokenUsdt);
        _tokenUSDC = ERC20(contractAddressTokenUsdc);

        require(contractAddressTokenUsdt != address(0), "Address cannot be zero.");
        require(contractAddressTokenUsdc != address(0), "Address cannot be zero.");
        require(seller != address(0), "Seller address cannot be zero.");
    }

    function buyUSDT(uint256 _amount, string memory _transactionId) public nonReentrant {
        _buy(_amount, _transactionId, _tokenUSDT);
    }

    function buyUSDC(uint256 _amount, string memory _transactionId) public nonReentrant {
        _buy(_amount, _transactionId, _tokenUSDC);
    }

    function _buy(uint256 _amount, string memory _transactionId, IERC20 _token) private {
        address _buyer = msg.sender;

        require(_amount <= _token.allowance(_buyer, address(this)), "Approve token before.");

        uint256 _buyerBalance = _token.balanceOf(_buyer);
        require(_buyerBalance >= _amount, "Insufficient tokens.");

        bool _success = _token.transferFrom(_buyer, _seller, _amount);
        require(_success, "Token transfer failed.");

        _transactionUser[_transactionId] = _buyer;
        _transactionAmount[_transactionId] = _amount;
        _transactionContract[_transactionId] = address(_token);
        _transactionTimestamp[_transactionId] = block.timestamp;

        emit Purchase(_buyer, _transactionId, _amount, address(_token));
    }

    function rechargeUSDT(uint256 _amount) public nonReentrant {
        _recharge(_amount, _tokenUSDT);
    }

    function rechargeUSDC(uint256 _amount) public nonReentrant {
        _recharge(_amount, _tokenUSDC);
    }

    function _recharge(uint256 _amount, IERC20 _token) private {
        address _buyer = msg.sender;

        require(_amount <= _token.allowance(_buyer, address(this)), "Approve token before.");

        uint256 _buyerBalance = _token.balanceOf(_buyer);
        require(_buyerBalance >= _amount, "Insufficient tokens.");

        bool _success = _token.transferFrom(_buyer, _seller, _amount);
        require(_success, "Token transfer failed.");

        emit Recharge(_buyer, _amount, address(_token));
    }

    function updateSeller(address _newSeller) public onlyOwner {
        require(_newSeller != address(0), "Address cannot be zero.");
        _seller = _newSeller;
    }

    function getTransactionDetails(string memory _transactionId) public view onlyOwner returns (address user, uint256 amount, address contractAddress, uint256 timestamp) {
        return (_transactionUser[_transactionId], _transactionAmount[_transactionId], _transactionContract[_transactionId], _transactionTimestamp[_transactionId]);
    }

    function getSeller() public view returns (address seller) {
        return _seller;
    }

}
"
