import '@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol';
import '@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol';

interface IExtoPlan {
    function getEnrollStartAt() external view returns (uint256);
}

contract ExtoTokenMiles is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {
    IERC20 public ExtoInterface;

    struct ExtoPlan {
        uint256 rewards;
        uint256 canRewardAt;
        uint256 sentAt;
        uint8 flag;
    }

    uint8 constant IS_INITED = 1 << 0;
    uint8 constant IS_PAUSED = 1 << 1;
    uint8 constant IS_START = 1 << 2;
    uint8 constant IS_CLOSED = 1 << 3;

    uint256 totalRewards;
    address public operator;

    mapping(address => ExtoPlan) public extoPlans;

    event InitTokenMile(uint256 tokenmiles, uint256 timestamp);
    event AddPlan(address indexed plan, uint256 canRewardAt, uint256 timestamp);
    event SetRewardAt(address indexed plan, uint256 canRewardAt, uint256 timestamp);
    event ReleaseMilesToHolder(address indexed plan, address indexed holder, address indexed creator, uint256 reward, uint32 feerate, uint256 sentAt);
    event ReleaseMilesToContract(address indexed plan, address indexed creator, uint256 reward, uint32 feerate, uint256 sentAt);

    function initialize(address _exto, address _operator) external initializer {
        ExtoInterface = IERC20(_exto);   

        operator = _operator;

        __Pausable_init_unchained();
        __Ownable_init_unchained(_msgSender());
        __ReentrancyGuard_init_unchained();        
    }

    function setOperator(address _operator) external onlyOwner {
        operator = _operator;
    }    

    modifier hasPermit() {      
        require(msg.sender == operator || msg.sender == owner(), "not permitted");
        _;
    }

    function setTokenMiles(uint256 _amount) external onlyOwner {
        require(_amount > 0, "Amount must be greater than 0");
        require(_amount <= ExtoInterface.allowance(_msgSender(), address(this)), "Make sure to add enough allowance");
        require(ExtoInterface.transferFrom(_msgSender(), address(this), _amount), "Transfer failed"); 
        
        emit InitTokenMile(_amount, block.timestamp);        
    }

    function addPlan(address _plan, uint256 _canRewardAt) external hasPermit {              
        require(_plan != address(0), 'Zero Plan address');
        require(extoPlans[_plan].flag & IS_INITED == 0, 'exists plan');
        require(IExtoPlan(_plan).getEnrollStartAt() > 0, 'not init plan');        

        extoPlans[_plan] = ExtoPlan({
            rewards: 0,
            canRewardAt: _canRewardAt,
            sentAt: 0,
            flag: IS_INITED
        });

        emit AddPlan(_plan, _canRewardAt, block.timestamp);
    }

    function setRewardAt(address _plan, uint256 _canRewardAt) external hasPermit {
      require(extoPlans[_plan].flag & IS_INITED != 0, 'not exists plan');
      extoPlans[_plan].canRewardAt = _canRewardAt;
      emit SetRewardAt(_plan, _canRewardAt, block.timestamp);
    }

    function getPlanInfo(address _plan) external view returns (ExtoPlan memory) {
        return extoPlans[_plan];
    }

    function pausePlan(address _plan) external hasPermit {
        require(extoPlans[_plan].flag & IS_INITED != 0, 'Uninitialized Plan');
        extoPlans[_plan].flag |= IS_PAUSED; 
    }

    function unpausePlan(address _plan) external hasPermit {
        require(extoPlans[_plan].flag & IS_INITED != 0, 'Uninitialized Plan');
        extoPlans[_plan].flag &= ~IS_PAUSED;
    }

    modifier isPaused(address _plan) {
        uint8 flag = extoPlans[_plan].flag;
        require(flag & IS_INITED != 0, 'Uninitialized Plan');
        require(flag & IS_PAUSED == 0, 'paused Plan');
        require(flag & IS_CLOSED == 0, 'closed Plan');
        _;
    }

    //feerate 10.00 % => 10 00 0000
    function releaseMilesToPlan(address _creator, uint256 _reward, uint32 _feerate) external isPaused(_msgSender()) nonReentrant {
        address _plan = _msgSender();
        ExtoPlan storage plan = extoPlans[_plan];        
        require(plan.canRewardAt < block.timestamp, "not a time to reward");
        require(_reward > 0, "invalid reward");
        require(_feerate <= 10000000, "invalid feerate");
        require(ExtoInterface.balanceOf(address(this)) >= _reward, "insufficient miles");      

        if (_feerate > 0) {
          uint256 fee = _reward * _feerate / 100000000;          
          require(ExtoInterface.transfer(_plan, _reward - fee), "Transfer failed");
          require(ExtoInterface.transfer(_creator, fee), "Transfer failed");
        } else {
          require(ExtoInterface.transfer(_plan, _reward), "Transfer failed");
        }

        plan.rewards += _reward;
        plan.sentAt = block.timestamp;

        totalRewards += _reward;

        emit ReleaseMilesToContract(_msgSender(), _creator, _reward, _feerate, block.timestamp);
    }

    function releaseMilesToHolder(address _holder, address _creator, uint256 _reward, uint32 _feerate) external isPaused(_msgSender()) nonReentrant {
        ExtoPlan storage plan = extoPlans[_msgSender()];        
        require(plan.canRewardAt < block.timestamp, "not a time to reward");
        require(_reward > 0, "invalid reward");
        require(_feerate <= 10000000, "invalid feerate");
        require(ExtoInterface.balanceOf(address(this)) >= _reward, "insufficient miles");      

        if (_feerate > 0) {
          uint256 fee = _reward * _feerate / 100000000;          
          require(ExtoInterface.transfer(_holder, _reward - fee), "Transfer failed");
          require(ExtoInterface.transfer(_creator, fee), "Transfer failed");
        } else {
          require(ExtoInterface.transfer(_holder, _reward), "Transfer failed");
        }

        plan.rewards += _reward;
        plan.sentAt = block.timestamp;

        totalRewards += _reward;

        emit ReleaseMilesToHolder(_msgSender(), _holder, _creator, _reward, _feerate, block.timestamp);
    }

