    "contracts/Presale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

interface ITokenVesting {
    function createVestingSchedule(
        address beneficiary,
        uint256 amount,
        bool revocable
    ) external;
}

contract Presale is Ownable {
    IERC20 public token;
    IERC20 public usdt;
    AggregatorV3Interface internal priceFeed;
    ITokenVesting public vestingContract;

    uint256 public constant TOKEN_PRICE_USDT = 0.05 * 1e18; 
    uint256 public constant MINIMUM_USDT_PURCHASE = 5 * 1e18; 
    uint256 public constant MINIMUM_ETH_PURCHASE = 0.0015 * 1e18; 

    bool public constant IS_VESTING_REVOCABLE = false;

    uint256 public startTime;
    uint256 public endTime;

    event TokensPurchased(address indexed buyer, uint256 amount);
    event FundsWithdrawn(address indexed owner, uint256 ethAmount, uint256 usdtAmount);
    event TokensDeposited(address indexed owner, uint256 amount);

    constructor(
        address _token,
        address _usdt,
        address _priceFeed,
        address _vestingContract,
        uint256 _startTime,
        uint256 _endTime
    ) Ownable(msg.sender) {
        require(_token != address(0), "Invalid token address");
        require(_usdt != address(0), "Invalid USDT address");
        require(_priceFeed != address(0), "Invalid price feed address");
        require(_vestingContract != address(0), "Invalid vesting contract address");
        require(_startTime < _endTime, "Start time must be before end time");

        token = IERC20(_token);
        usdt = IERC20(_usdt);
        priceFeed = AggregatorV3Interface(_priceFeed);
        vestingContract = ITokenVesting(_vestingContract);
        startTime = _startTime;
        endTime = _endTime;


    }

    modifier onlyDuringSale() {
        require(block.timestamp >= startTime, "Sale has not started yet");
        require(block.timestamp <= endTime, "Sale has ended");
        _;
    }

    function buyTokensWithUSDT(uint256 usdtAmount) external onlyDuringSale {
        require(usdtAmount * 1e12 >= MINIMUM_USDT_PURCHASE, "USDT amount too low");
        require(usdt.transferFrom(msg.sender, address(this), usdtAmount), "USDT transfer failed");

        uint256 tokenAmount = (usdtAmount * 1e12) / TOKEN_PRICE_USDT * 1e18;

        vestingContract.createVestingSchedule(
            msg.sender, 
            tokenAmount,
            IS_VESTING_REVOCABLE
        );

        emit TokensPurchased(msg.sender, tokenAmount);
    }

    function buyTokensWithETH() external payable onlyDuringSale {
        require(msg.value >= MINIMUM_ETH_PURCHASE, "ETH amount too low");

        uint256 ethPrice = getLatestETHPrice();
        uint256 usdtValue = msg.value * ethPrice / 1e18;

        uint256 tokenAmount = usdtValue / TOKEN_PRICE_USDT * 1e18;

        vestingContract.createVestingSchedule(
            msg.sender, 
            tokenAmount,
            IS_VESTING_REVOCABLE
        );

        emit TokensPurchased(msg.sender, tokenAmount);
    }

    function getLatestETHPrice() public view returns (uint256) {
        (, int price,,,) = priceFeed.latestRoundData();
        return uint256(price) * 1e10;
    }

    function getRemainingTokensAmount() public view returns (uint256) {
        return token.balanceOf(address(vestingContract));
    }

    function withdrawFunds() external onlyOwner {
        uint256 ethBalance = address(this).balance;
        uint256 usdtBalance = usdt.balanceOf(address(this));

        if (ethBalance > 0) {
            payable(owner()).transfer(ethBalance);
        }

        if (usdtBalance > 0) {
            usdt.transfer(owner(), usdtBalance);
        }

        emit FundsWithdrawn(owner(), ethBalance, usdtBalance);
    }
}
