pragma solidity ^0.8.19;

import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import {IXERC20} from 'xerc20/solidity/interfaces/IXERC20.sol';
import {IXERC20Lockbox} from 'xerc20/solidity/interfaces/IXERC20Lockbox.sol';

interface Messenger {
  function sendMessage(address _target, bytes memory _message, uint32 _minGasLimit) external;
  function xDomainMessageSender() external returns (address);
}

interface L2TokenMinter {
  function mint(address _to, uint256 _amount) external;
}

interface IXERC20Registry {
  function getXERC20(address erc20) external view returns (address xerc20);

  function getERC20(address xerc20) external view returns (address erc20);

  function getLockbox(address erc20) external view returns (address xerc20);
}

contract OPBridgeL1LockboxAdapter is Ownable {
  Messenger public immutable CROSS_DOMAIN_MESSENGER;
  L2TokenMinter public mirrorTokenMinter;
  IXERC20 public immutable XERC20;
  IXERC20Lockbox public immutable LOCKBOX;
  IERC20 public immutable ERC20;

  // ERRORS
  error NotMinter(address sender);
  error AmountLessThanZero();

  modifier onlyMinter() {
    if (
      msg.sender == address(CROSS_DOMAIN_MESSENGER)
        && CROSS_DOMAIN_MESSENGER.xDomainMessageSender() == address(mirrorTokenMinter)
    ) {
      revert NotMinter(msg.sender);
    }
    _;
  }

  constructor(address _crossDomainMessenger, address _xerc20, address _lockbox, address _erc20) Ownable() {
    CROSS_DOMAIN_MESSENGER = Messenger(_crossDomainMessenger);
    XERC20 = IXERC20(_xerc20);
    LOCKBOX = IXERC20Lockbox(_lockbox);
    ERC20 = IERC20(_erc20);
  }

  function setMirrorTokenMinter(address _mirrorTokenMinter) external onlyOwner {
    mirrorTokenMinter = L2TokenMinter(_mirrorTokenMinter);
  }

  /// @dev Combines Lockbox deposit and xcall using native asset as relayer fee.
  /// @param _to The recipient or contract address on destination.
  /// @param _amount The amount of asset to bridge.
  function sendToL2(address _to, uint256 _amount, bool _wrapERC20) external {
    if (_amount <= 0) {
      revert AmountLessThanZero();
    }

    if (_wrapERC20) {
      SafeERC20.safeTransferFrom(ERC20, msg.sender, address(this), _amount);
      ERC20.approve(address(LOCKBOX), _amount);
      LOCKBOX.deposit(_amount);
      XERC20.burn(address(this), _amount);
    } else {
      XERC20.burn(msg.sender, _amount);
    }

    CROSS_DOMAIN_MESSENGER.sendMessage(
      address(mirrorTokenMinter), abi.encodeCall(L2TokenMinter.mint, (_to, _amount)), 200_000
    );
  }

  /// @dev Receives xERC20s from L2 and optionally withdraws ERC20 from Lockbox.
  /// @param _to The address that will receive the ERC20 or XERC20.
  /// @param _amount The amount of funds that will be received.
  /// @param _wrapXERC20 Whether to wrap XERC20 into ERC20.
  function mint(address _to, uint256 _amount, bool _wrapXERC20) external onlyMinter {
    if (_wrapXERC20) {
      XERC20.mint(address(this), _amount);
      ERC20.approve(address(LOCKBOX), _amount);
      LOCKBOX.withdrawTo(_to, _amount);
    } else {
      XERC20.mint(_to, _amount);
    }
  }
}
