     */
    error ExcessNativeRewardBalance(uint256 balance);

    /**
     * @dev Revert with an error if the length of n arrays are not equal.
     */
    error ArrayLengthMismatch();

    /**
     * @dev Emitted when a reward extraction delay is invalid.
     */
    error InvalidRewardExtractionDelay(uint256 delay, uint256 minimumDelay, uint256 maximumDelay);

    /**
     * @dev Revert with an error if the owner attempts to renounce their ownership.
     */
    error OwnerCannotRenounceOwnership();

    /**
     * @dev Revert with an error when the caller is not an approved caller.
     */
    error UnapprovedCaller();

    /**
     * @dev Emitted when a quest is created with a batch of rewards.
     */
    event QuestCreatedWithRewards(
        uint256 indexed questId,
        uint256 startTime,
        uint256 endTime,
        address creator,
        address signer
    );

    /**
     * @dev Emitted when the quest creator extracts the remaining rewards.
     */
    event QuestRewardsExtracted(uint256 indexed questId, address creator);

    /**
     * @dev Emitted when a quest reward is claimed.
     */
    event QuestRewardClaimed(uint256 indexed questId, uint256 rewardId, uint256 claimUId, address claimer);

    /**
     * @dev Emitted when quest rewards are claimed.
     */
    event QuestRewardsClaimed(uint256 indexed questId, uint256[] rewardIds, uint256[] claimUIds, address claimer);

    /**
     * @dev Emitted when the reward extraction delay is updated.
     */
    event RewardExtractionDelayUpdated(uint256 _rewardExtractionDelay);
}
"
    },
    "contracts/lib/RewardManager.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {TokenTransferrer} from "./TokenTransferrer.sol";
import {Validators} from "./Validators.sol";
import {Reward, RewardDeposit, RewardDepositBatchERC1155} from "./Structs.sol";
import {RewardItemType} from "./Enums.sol";

contract RewardManager is TokenTransferrer, Validators {
    mapping(uint256 => Reward) public rewards;
    mapping(uint256 => bool) public claims;

    function _handleQuestRewardDeposit(RewardDeposit calldata deposit, uint256 questId) internal {
        _validateQuestRewardDeposit(deposit);

        Reward storage reward = rewards[deposit.rewardId];

        // amountPerReward can never become zero after being initialized
        if (reward.amountPerReward != 0) {
            revert QuestRewardExists(deposit.rewardId);
        }

        reward.tokenId = deposit.tokenId;
        reward.total = deposit.total;
        reward.amountPerReward = deposit.amountPerReward;
        reward.token = deposit.token;
        reward.itemType = deposit.itemType;
        reward.questId = questId;

        if (deposit.itemType != RewardItemType.NATIVE) {
            _transferQuestRewardFromCaller(deposit);
        }
    }

    function _handleBatchERC1155QuestRewardDeposit(
        RewardDepositBatchERC1155 calldata batchERC1155Deposit,
        uint256 questId
    ) internal {
        uint256 batchLength = batchERC1155Deposit.rewardIds.length;

        if (batchLength == 0) {
            revert BatchERC1155RewardDepositEmpty();
        }

        if (
            batchERC1155Deposit.tokenIds.length != batchLength ||
            batchERC1155Deposit.totals.length != batchLength ||
            batchERC1155Deposit.amountPerRewards.length != batchLength
        ) {
            revert ArrayLengthMismatch();
        }

        for (uint256 i = 0; i < batchLength; i++) {
            uint256 rewardId = batchERC1155Deposit.rewardIds[i];
            uint256 total = batchERC1155Deposit.totals[i];
            uint256 amountPerReward = batchERC1155Deposit.amountPerRewards[i];

            // Ensure that the amount per reward and the total are not zero
            if (amountPerReward == 0 || total == 0) {
                revert QuestRewardAmountInvalid(rewardId, total, amountPerReward);
            }

            // Ensure the total is an exact multiple of the amount per reward
            if (total % amountPerReward != 0) {
                revert QuestRewardPrecisionInvalid(rewardId, total, amountPerReward);
            }

            Reward storage reward = rewards[rewardId];

            // amountPerReward can never become zero after being initialized
            if (reward.amountPerReward != 0) {
                revert QuestRewardExists(rewardId);
            }

            reward.tokenId = batchERC1155Deposit.tokenIds[i];
            reward.total = total;
            reward.amountPerReward = amountPerReward;
            reward.token = batchERC1155Deposit.token;
            reward.itemType = RewardItemType.ERC1155;
            reward.questId = questId;
        }

        _performERC1155BatchTransfer(
            batchERC1155Deposit.token,
            msg.sender,
            address(this),
            batchERC1155Deposit.tokenIds,
            batchERC1155Deposit.totals
        );
    }

    function _handleQuestRewardClaim(uint256 questId, uint256 rewardId, uint256 claimUId) internal {
        Reward storage reward = rewards[rewardId];

        uint256 rewardTotal = reward.total;
        uint256 amountPerReward = reward.amountPerReward;

        if (reward.questId != questId) {
            revert QuestRewardNotPartOfQuest(questId, rewardId);
        }

        if (rewardTotal < amountPerReward) {
            revert QuestRewardDepleted(rewardId);
        }

        if (claims[claimUId]) {
            revert QuestRewardAlreadyClaimed(rewardId, claimUId);
        }

        reward.total = rewardTotal - amountPerReward;
        claims[claimUId] = true;

        _transferQuestRewardToCaller(reward.token, reward.tokenId, amountPerReward, reward.itemType);
    }

    function _handleQuestRewardExtraction(uint256 questId, uint256[] calldata rewardIds) internal {
        uint256 rewardIdsLength = rewardIds.length;

        if (rewardIdsLength == 0) {
            revert RewardsArrayEmpty();
        }

        for (uint256 i = 0; i < rewardIdsLength; ) {
            Reward storage reward = rewards[rewardIds[i]];

            if (reward.questId != questId) {
                revert QuestRewardNotPartOfQuest(questId, rewardIds[i]);
            }

            uint256 amountToTransfer = reward.total;

            if (amountToTransfer > 0) {
                reward.total = 0;
                _transferQuestRewardToCaller(reward.token, reward.tokenId, amountToTransfer, reward.itemType);
            }

            unchecked {
                ++i;
            }
        }

        emit QuestRewardsExtracted(questId, msg.sender);
    }
}
"
    },
    "contracts/lib/Structs.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {RewardItemType} from "./Enums.sol";

struct Quest {
