}

struct Reward {
    uint256 tokenId;
    uint256 total;
    uint256 amountPerReward;
    uint256 questId;
    address token;
    RewardItemType itemType;
}

struct RewardDeposit {
    uint256 rewardId;
    uint256 tokenId;
    uint256 total;
    uint256 amountPerReward;
