    "contracts/lib/TokenTransferrer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC1155} from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import {IErrorsAndEvents} from "./interfaces/IErrorsAndEvents.sol";

import {RewardItemType} from "./Enums.sol";
import {RewardDeposit} from "./Structs.sol";

contract TokenTransferrer is IErrorsAndEvents {
    function _transferQuestRewardToCaller(
        address token,
        uint256 tokenId,
        uint256 amount,
        RewardItemType itemType
    ) internal {
        if (itemType == RewardItemType.NATIVE) {
            _performNativeTransfer(msg.sender, amount);
        } else if (itemType == RewardItemType.ERC20) {
            _performERC20Transfer(token, msg.sender, amount);
        } else if (itemType == RewardItemType.ERC721) {
            _performERC721Transfer(token, address(this), msg.sender, tokenId);
        } else if (itemType == RewardItemType.ERC1155) {
            _performERC1155Transfer(token, address(this), msg.sender, tokenId, amount);
        } else {
            revert InvalidRewardItemType(itemType);
        }
    }

    function _transferQuestRewardFromCaller(RewardDeposit calldata deposit) internal {
        if (deposit.itemType == RewardItemType.ERC20) {
            _performERC20TransferFrom(deposit.token, msg.sender, address(this), deposit.total);
        } else if (deposit.itemType == RewardItemType.ERC721) {
            _performERC721Transfer(deposit.token, msg.sender, address(this), deposit.tokenId);
        } else if (deposit.itemType == RewardItemType.ERC1155) {
            _performERC1155Transfer(deposit.token, msg.sender, address(this), deposit.tokenId, deposit.total);
        } else {
            revert InvalidRewardItemType(deposit.itemType);
        }
    }

    function _performNativeTransfer(address to, uint256 amount) private {
        (bool success, ) = payable(to).call{value: amount}("");
        if (!success) {
            revert TokenTransferFailed(address(0), address(this), to, 0, amount);
        }
    }

    function _performERC20Transfer(address token, address to, uint256 amount) internal {
        SafeERC20.safeTransfer(IERC20(token), to, amount);
    }

    function _performERC20TransferFrom(address token, address from, address to, uint256 amount) internal {
        SafeERC20.safeTransferFrom(IERC20(token), from, to, amount);
    }

    function _performERC721Transfer(address token, address from, address to, uint256 tokenId) private {
        if (token.code.length == 0) {
            revert InvalidContract(token);
        }

        IERC721(token).transferFrom(from, to, tokenId);
    }

    function _performERC1155Transfer(address token, address from, address to, uint256 tokenId, uint256 amount) private {
        if (token.code.length == 0) {
            revert InvalidContract(token);
        }

        IERC1155(token).safeTransferFrom(from, to, tokenId, amount, "");
    }

    function _performERC1155BatchTransfer(
        address token,
        address from,
        address to,
        uint256[] memory tokenIds,
        uint256[] memory amounts
    ) internal {
        if (token.code.length == 0) {
            revert InvalidContract(token);
        }

        IERC1155(token).safeBatchTransferFrom(from, to, tokenIds, amounts, "");
    }
}
"
    },
    "contracts/lib/Validators.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {RewardDeposit} from "./Structs.sol";
import {RewardItemType} from "./Enums.sol";
import {IErrorsAndEvents} from "./interfaces/IErrorsAndEvents.sol";

contract Validators is IErrorsAndEvents {
    uint256 public rewardExtractionDelay;

    using ECDSA for bytes32;

    function _validateQuestCreation(address creator, uint256 startTime, uint256 endTime) internal view {
        if (creator != address(0)) {
            revert QuestExists();
        }

        if (startTime >= endTime) {
            revert InvalidQuestStartTime(startTime, endTime);
        }

        if (endTime <= block.timestamp) {
            revert InvalidQuestEndTime(endTime, block.timestamp);
        }
    }

    function _validateQuestRewardDeposit(RewardDeposit calldata deposit) internal pure {
        // Ensure that the amount per reward and the total are not zero
        if (deposit.amountPerReward == 0 || deposit.total == 0) {
            revert QuestRewardAmountInvalid(deposit.rewardId, deposit.total, deposit.amountPerReward);
        }

        // Ensure the total is an exact multiple of the amount per reward
        if (deposit.total % deposit.amountPerReward != 0) {
            revert QuestRewardPrecisionInvalid(deposit.rewardId, deposit.total, deposit.amountPerReward);
        }

        if (deposit.itemType == RewardItemType.ERC721 && (deposit.total != 1 || deposit.amountPerReward != 1)) {
            revert QuestRewardAmountInvalid(deposit.rewardId, deposit.total, deposit.amountPerReward);
        }
    }

    function _validateQuestRewardClaim(uint256 startTime, uint256 endTime) internal view {
        if (startTime > block.timestamp) {
            revert QuestInactive();
        }

        if (block.timestamp >= (endTime + rewardExtractionDelay)) {
            revert QuestExpired();
        }
    }

    function _validateQuestRewardExtraction(uint256 startTime, uint256 endTime, address creator) internal view {
        if (creator != msg.sender) {
            revert QuestCreatorInvalid(creator, msg.sender);
        }

        if (block.timestamp >= startTime && block.timestamp <= (endTime + rewardExtractionDelay)) {
            revert QuestActive();
        }
    }

    function _validateQuestRewardSignature(
        uint256 questId,
        uint256 rewardId,
        uint256 uid,
        address signer,
        bytes calldata signature
    ) internal view {
        bytes32 hash = keccak256(abi.encodePacked(questId, rewardId, uid, msg.sender));
        address recoveredSigner = hash.toEthSignedMessageHash().recover(signature);
        if (signer != recoveredSigner) {
            revert QuestSignerInvalid(signer, recoveredSigner);
        }
    }

    function _validateQuestRewardsBatchSignature(
        uint256 questId,
        uint256[] calldata rewardIds,
        uint256[] calldata uids,
        address signer,
        bytes calldata signature
    ) internal view {
        bytes32 hash = keccak256(abi.encodePacked(questId, rewardIds, uids, msg.sender));
        address recoveredSigner = hash.toEthSignedMessageHash().recover(signature);
        if (signer != recoveredSigner) {
            revert QuestSignerInvalid(signer, recoveredSigner);
        }
    }
}
"
