    "contracts/NiftyIslandQuest.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import {ERC1155Holder} from "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";

import {RewardManager} from "./lib/RewardManager.sol";
import {Quest, RewardDeposit, RewardDepositBatchERC1155} from "./lib/Structs.sol";
import {RewardItemType} from "./lib/Enums.sol";

/**
 *  @title   Nifty Island Quest
 *  @notice  Allows creators to create quests with rewards, and participants to claim rewards from quests.
 *  @custom:version  v1.0.0
 */
contract NiftyIslandQuest is RewardManager, ReentrancyGuard, Pausable, Ownable2Step, ERC721Holder, ERC1155Holder {
    mapping(uint256 => Quest) public quests;
    mapping(address => bool) public approvedCallers;

    uint256 public constant MINIMUM_REWARD_EXTRACTION_DELAY = 1 days;
    uint256 public constant MAXIMUM_REWARD_EXTRACTION_DELAY = 180 days;

    modifier onlyApprovedCallers() {
        if (!approvedCallers[msg.sender]) {
            revert UnapprovedCaller();
        }
        _;
    }

    constructor(uint256 _rewardExtractionDelay) {
        rewardExtractionDelay = _rewardExtractionDelay;
    }

    /**
     * @notice  Invoked by quest creator, who provides a trusted 'signer' address.
     * @dev     Responsible for creating a Quest struct and storing it in the quests mapping.
     *          Responsible for transferring rewards from `msg.sender` into the contract.
     *          Responsible for emitting the QuestCreatedWithRewards event.
     * @param   questId             id for the created quest
     * @param   startTime           timestamp after which claiming will be enabled
     * @param   endTime             timestamp marking the end of the quest
     * @param   signer              trusted 'signer' address for this quest
     * @param   rewardDeposits             array of RewardDeposit structs specifying the rewards for this quest
     * @param   batchERC1155RewardDeposits array of RewardDepositBatchERC1155 structs specifying the ERC1155 rewards for this quest
     */
    function createQuestWithRewards(
        uint256 questId,
        uint256 startTime,
        uint256 endTime,
        address signer,
        RewardDeposit[] calldata rewardDeposits,
        RewardDepositBatchERC1155[] calldata batchERC1155RewardDeposits
    ) external payable nonReentrant whenNotPaused onlyApprovedCallers {
        uint256 rewardsLength = rewardDeposits.length;
        uint256 batchERC1155RewardsLength = batchERC1155RewardDeposits.length;

        if (rewardsLength == 0 && batchERC1155RewardsLength == 0) {
            revert RewardDepositArraysEmpty();
        }

        Quest storage quest = quests[questId];

        _validateQuestCreation(quest.creator, startTime, endTime);

        quest.startTime = startTime;
        quest.endTime = endTime;
        quest.creator = msg.sender;
        quest.signer = signer;

        uint256 nativeRewardsBalance = msg.value;

        // Transfer rewards from `msg.sender` into the contract
        for (uint256 i = 0; i < rewardsLength; ) {
            RewardDeposit calldata deposit = rewardDeposits[i];

            // Special case for native rewards
            if (deposit.itemType == RewardItemType.NATIVE) {
                if (nativeRewardsBalance < deposit.total) {
                    revert InsufficientNativeRewardBalance(nativeRewardsBalance, deposit.total);
                }

                unchecked {
                    nativeRewardsBalance -= deposit.total;
                }
            }

            _handleQuestRewardDeposit(deposit, questId);

            unchecked {
                ++i;
            }
        }

        // Transfer batch Rewards from `msg.sender` into the contract.
        // Used for gas efficiency with ERC1155 reawards.
        for (uint256 i = 0; i < batchERC1155RewardsLength; ) {
            _handleBatchERC1155QuestRewardDeposit(batchERC1155RewardDeposits[i], questId);

            unchecked {
                ++i;
            }
        }

        if (nativeRewardsBalance > 0) {
            revert ExcessNativeRewardBalance(nativeRewardsBalance);
        }

        emit QuestCreatedWithRewards(questId, startTime, endTime, msg.sender, signer);
    }

    /**
     * @notice  Invoked by quest participant, who provides a signature from the trusted 'signer' address.
     *          Can be invoked from the quest start time until `rewardExtractionDelay` seconds after the quest end time.
     * @dev     Responsible for validating the quest reward claim.
     *          Responsible for validating the signature.
     *          Responsible for transfering the quest reward to the participant.
     *          Responsible for emitting the QuestRewardClaimed event.
     * @param   questId     id of quest to claim reward from
     * @param   rewardId    id of reward to claim
     * @param   claimUId    unique identifier for this claim to avoid double-spend using the same siganture
     * @param   signature   signature from the trusted 'signer' address
     */
    function claimQuestReward(
        uint256 questId,
        uint256 rewardId,
        uint256 claimUId,
        bytes calldata signature
    ) external nonReentrant whenNotPaused {
        Quest storage quest = quests[questId];

        _validateQuestRewardClaim(quest.startTime, quest.endTime);

        _validateQuestRewardSignature(questId, rewardId, claimUId, quest.signer, signature);

        _handleQuestRewardClaim(questId, rewardId, claimUId);

        emit QuestRewardClaimed(questId, rewardId, claimUId, msg.sender);
    }

    /**
     * @notice  Invoked by quest participant, who provides a signature from the trusted 'signer' address.
     *          Can be invoked from the quest start time until `rewardExtractionDelay` seconds after the quest end time.
     *          Used to claim multiple rewards from a quest simultaneously.
     * @param   questId     id of quest to claim reward from
     * @param   rewardIds   id of rewards to claim
     * @param   claimUIds   unique identifiers for these claims to avoid double-spend using the same siganture
     * @param   signature   signature from the trusted 'signer' address
     */
    function claimQuestRewardsBatch(
        uint256 questId,
        uint256[] calldata rewardIds,
        uint256[] calldata claimUIds,
        bytes calldata signature
    ) external nonReentrant whenNotPaused {
        uint256 totalRewards = rewardIds.length;

        if (totalRewards != claimUIds.length) {
            revert ArrayLengthMismatch();
        }

        Quest storage quest = quests[questId];

        _validateQuestRewardClaim(quest.startTime, quest.endTime);

        _validateQuestRewardsBatchSignature(questId, rewardIds, claimUIds, quest.signer, signature);

        for (uint256 i = 0; i < totalRewards; ) {
            _handleQuestRewardClaim(questId, rewardIds[i], claimUIds[i]);

            unchecked {
                ++i;
            }
        }

        emit QuestRewardsClaimed(questId, rewardIds, claimUIds, msg.sender);
    }

    /**
     * @notice  Invoked by quest creator.
     *          Can be invoked only before the quest start time or after `rewardExtractionDelay` seconds after the quest end time.
     *          Used to extract quest rewards that were not claimed by quest participants, and return them to the creator.
     * @dev     Responsible for validating the quest reward extraction.
     *          Responsible for transfering remaining quest rewards to the creator.
     * @param   questId     id of the quest to extract rewards from
     * @param   rewardIds   ids of rewards to extract
     */
    function extractQuestRewards(uint256 questId, uint256[] calldata rewardIds) external nonReentrant {
        Quest storage quest = quests[questId];

        _validateQuestRewardExtraction(quest.startTime, quest.endTime, quest.creator);

        _handleQuestRewardExtraction(questId, rewardIds);
    }

    /**
     * @notice  Invoked by quest creator.
     *          Can be invoked only before the quest start time or after `rewardExtractionDelay` seconds after the quest end time.
     *          Used to extract unclaimed quest rewards from multiple quests simultaneously.
     * @param   questIds        ids of quests to extract rewards from
     * @param   rewardsBatched  array of arrays of reward ids to extract
     */
    function extractQuestRewardsBatch(
        uint256[] calldata questIds,
        uint256[][] calldata rewardsBatched
    ) external nonReentrant {
        uint256 questIdsLength = questIds.length;

        if (questIdsLength != rewardsBatched.length) {
            revert ArrayLengthMismatch();
        }

        for (uint256 i = 0; i < questIdsLength; ) {
            uint256 questId = questIds[i];

            Quest storage quest = quests[questId];

            _validateQuestRewardExtraction(quest.startTime, quest.endTime, quest.creator);

            _handleQuestRewardExtraction(questId, rewardsBatched[i]);

            unchecked {
                ++i;
            }
        }
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function renounceOwnership() public view override onlyOwner {
        revert OwnerCannotRenounceOwnership();
    }

    /**
     * @dev Allows owner to modify the reward extraction delay.
     */
    function setRewardExtractionDelay(uint256 _newDelay) external onlyOwner {
        if (_newDelay < MINIMUM_REWARD_EXTRACTION_DELAY || _newDelay > MAXIMUM_REWARD_EXTRACTION_DELAY) {
            revert InvalidRewardExtractionDelay(
                _newDelay,
                MINIMUM_REWARD_EXTRACTION_DELAY,
                MAXIMUM_REWARD_EXTRACTION_DELAY
            );
        }

        rewardExtractionDelay = _newDelay;

        emit RewardExtractionDelayUpdated(_newDelay);
    }

    /**
     * @dev Allows owner to modify the state of approved callers.
     */
    function setApprovedCallers(address[] calldata _addresses, bool _enabled) external onlyOwner {
        uint256 addressesLength = _addresses.length;

        for (uint256 i = 0; i < addressesLength; ) {
            approvedCallers[_addresses[i]] = _enabled;

            unchecked {
                ++i;
            }
        }
    }
}
"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": false,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
