    "contracts/BuyBackTAO.sol": {
      "content": "// SPDX-License-Identifier: MIT
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

pragma solidity 0.8.23;

contract BuyBackTAO is Ownable {
    address public weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address public wtao = 0x77E06c9eCCf2E797fd462A92B6D7642EF85b0A44;

    ISwapRouter public swaprouter =
        ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);
    bool public openSwapPublic;
    uint256 public swapAmount;
    uint256 public latestBuyBack;
    uint256 public buyBackInterval;

    event BuyBackWTAO(uint256 amount);

    constructor() {
        swapAmount = 1 ether;
        openSwapPublic = true;
        buyBackInterval = 1 days;
    }

    modifier canSwap() {
        require(openSwapPublic, "Swap is not open");
        require(
            block.timestamp - latestBuyBack > buyBackInterval,
            "Buyback interval not reached"
        );
        _;
    }

    function buyBackWTAO() external canSwap {
        uint256 _wtao = ISwapRouter(swaprouter).exactInputSingle{
            value: swapAmount
        }(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: weth,
                tokenOut: wtao,
                fee: 10000,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: swapAmount,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
        latestBuyBack = block.timestamp;
        emit BuyBackWTAO(_wtao);
    }

    function buyBackWTAOAdmin(uint256 _amount) external onlyOwner {
        uint256 _wtao = ISwapRouter(swaprouter).exactInputSingle{
            value: _amount
        }(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: weth,
                tokenOut: wtao,
                fee: 10000,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: _amount,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
        emit BuyBackWTAO(_wtao);
    }

    function setOpenSwapPublic(bool _openSwapPublic) external onlyOwner {
        openSwapPublic = _openSwapPublic;
    }

    function setSwapAmount(uint256 _swapAmount) external onlyOwner {
        swapAmount = _swapAmount;
    }

    function setBuyBackInterval(uint256 _buyBackInterval) external onlyOwner {
        buyBackInterval = _buyBackInterval;
    }

    function rescueETH() external onlyOwner {
        (bool _sent, ) = payable(_msgSender()).call{
            value: address(this).balance
        }("");
        require(_sent);
    }

    function rescueToken(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(_msgSender(), _amount);
    }

    receive() external payable {}
}
