contract Presale is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    enum PresaleStatus {
        Started,
        Finished
    }

    struct PresaleConfig {
        address usdc;
        address presaleToken;
        uint256 startTime;
        uint256 endTime;
        uint256 hardcap;
        uint256 minContribution;
    }

    struct PoolInfo {
        uint presaleLevel;
        uint256 lastRewardTime; // Last time number that Wonka distribution occurs.
        uint256 accWonkaPerShare; // Accumulated Wonka per share
    }

    struct Funder {
        uint256 contributedAmount;
        uint256 claimableAmount;
        uint256 rewardAmount;
        uint256 rewardDebt;
        bool isClaimed;
    }

    // Presale Info Variables
    uint256 public constant PRECISION_FACTOR = uint256(10 ** (40 - 18));
    bool public initialized = false;
    PresaleConfig public presaleConfig;
    PresaleStatus public presaleStatus;
    // Presale Level will rise up once 1 week passed or capLevel reached.
    uint256[] public wonkaPrice;
    uint256[] public capPerLevel;

    uint256 public totalContributed;
    uint256[7] public contributedPerLevel;

    // Reward Distribution Variables
    PoolInfo public poolInfo;
    uint256 public wonkaPerSecond;
    uint256 public bonusEndTime;

    // User Info Variables
    mapping(address => Funder) public funders;
    uint256 public funderCounter;

    address public admin;
    address public keeper;

     // Affiliate Variable
    mapping(string => uint256) public affiliate;

    event Contribute(address funder, uint256 amount);
    event Claimed(address funder, uint256 amount);

    constructor() {}

    function initialize(
        PresaleConfig memory _config,
        uint256[] memory _price,
        uint256[] memory _capPerLevel
    ) external onlyOwner {
        require(!initialized, "already initialized");
        require(owner() == address(0x0) || _msgSender() == owner(), "not allowed");

        initialized = true;
        presaleConfig = _config;
        wonkaPrice = _price;
        capPerLevel = _capPerLevel;
        poolInfo.lastRewardTime = 1715295600;
        wonkaPerSecond = 3674 ether;
        bonusEndTime = 1720738800;
    }

    modifier adminAuth() {
        require(_msgSender() == keeper || _msgSender() == owner(), "Caller is not registered admin");
        _;
    }



    /**** Presale Functions ****/

    function contribute(uint256 _amount, string calldata code) external nonReentrant {
        require(presaleStatus == PresaleStatus.Started, "Presale is over");
        require(_amount >= presaleConfig.minContribution, "Contribution Amount is too low");
        require(block.timestamp > presaleConfig.startTime, "Presale is not started yet");
        require(block.timestamp < presaleConfig.endTime, "Presale is over");
        require(totalContributed + _amount <= presaleConfig.hardcap, "Amount entered goes over hardcap");


        Funder storage funder = funders[_msgSender()];

        if (funder.contributedAmount == 0) {
            funderCounter++;
        }

        updatePool();

        if (funder.contributedAmount > 0) {
            uint256 pending = funder.contributedAmount.mul(poolInfo.accWonkaPerShare).div(PRECISION_FACTOR).sub(
                funder.rewardDebt
            );
            funder.rewardAmount += pending;
        }

        IERC20(presaleConfig.usdc).transferFrom(msg.sender, address(this), _amount);

        totalContributed += _amount;

        funder.contributedAmount += _amount;


        if(contributedPerLevel[poolInfo.presaleLevel] + _amount >= capPerLevel[poolInfo.presaleLevel]){
            
            uint256 remLvlAmnt = capPerLevel[poolInfo.presaleLevel].sub(contributedPerLevel[poolInfo.presaleLevel]);

            contributedPerLevel[poolInfo.presaleLevel] += remLvlAmnt;

            uint256 curPrice = remLvlAmnt.mul(wonkaPrice[poolInfo.presaleLevel]);

            poolInfo.presaleLevel++;

            uint256 newPrice = _amount.sub(remLvlAmnt).mul(wonkaPrice[poolInfo.presaleLevel]);

            funder.claimableAmount += curPrice + newPrice;

            contributedPerLevel[poolInfo.presaleLevel] += _amount.sub(remLvlAmnt);

            affiliate[code] += curPrice + newPrice;

        }else{

            contributedPerLevel[poolInfo.presaleLevel] += _amount;

            affiliate[code] += (_amount * wonkaPrice[poolInfo.presaleLevel]);

            funder.claimableAmount += (_amount * wonkaPrice[poolInfo.presaleLevel]);

        }

        funder.rewardDebt = funder.contributedAmount.mul(poolInfo.accWonkaPerShare).div(PRECISION_FACTOR);

        if (totalContributed >= presaleConfig.hardcap) presaleStatus = PresaleStatus.Finished;

        emit Contribute(_msgSender(), _amount);
    }

    function claim() external nonReentrant {
        require(presaleStatus == PresaleStatus.Finished , "Presale is not finished");

        Funder storage funder = funders[_msgSender()];
        require(funder.contributedAmount >= presaleConfig.minContribution && funder.isClaimed == false, "You are not a funder");

        updatePool();

        funder.isClaimed = true;

        uint256 pending = funder.contributedAmount.mul(poolInfo.accWonkaPerShare).div(PRECISION_FACTOR).sub(
            funder.rewardDebt
        );
        funder.rewardAmount += pending;

        _safeTransfer(IERC20(presaleConfig.presaleToken), _msgSender(), funder.claimableAmount + funder.rewardAmount);
        emit Claimed(_msgSender(), funder.claimableAmount + funder.rewardAmount);
    }

    //**** Reward Distribution functions ****/

    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {

        if(block.timestamp >= bonusEndTime){
            return bonusEndTime.sub(_from);
        }else{
            return _to.sub(_from);
        }
      
    }

    function pendingWonka(address _user) external view returns (uint256) {
        Funder storage user = funders[_user];
        uint256 accWonkaPerShare = poolInfo.accWonkaPerShare;
        if (block.timestamp > poolInfo.lastRewardTime && totalContributed != 0 && poolInfo.lastRewardTime > 0) {
            uint256 multiplier = getMultiplier(poolInfo.lastRewardTime, block.timestamp);
            uint256 wonkaReward = multiplier.mul(wonkaPerSecond);

            accWonkaPerShare = accWonkaPerShare.add(wonkaReward.mul(PRECISION_FACTOR).div(totalContributed));
        }
        return user.contributedAmount.mul(accWonkaPerShare).div(PRECISION_FACTOR).sub(user.rewardDebt);
    }

    function updatePool() public {
        if (block.timestamp <= poolInfo.lastRewardTime || poolInfo.lastRewardTime == 0) return;

        uint256 multiplier = getMultiplier(poolInfo.lastRewardTime, block.timestamp);
        uint256 _reward = multiplier * wonkaPerSecond;
        poolInfo.accWonkaPerShare += (_reward * PRECISION_FACTOR) / totalContributed;

        if(block.timestamp >= bonusEndTime){
            poolInfo.lastRewardTime = bonusEndTime;
        }else{
            poolInfo.lastRewardTime = block.timestamp;
        }
            
    }

    //**** Internal Functions ****/
    function _safeTransfer(IERC20 _token, address _to, uint256 _amount) internal {
        _token.safeTransfer(_to, _amount);
    }
    //**** Admin Function ****/
    function setPresaleStatus(PresaleStatus _status) public adminAuth {
        presaleStatus = _status;
    }

    function setPresaleToken(address _token) public onlyOwner {
        presaleConfig.presaleToken = _token;
    }

    function setSalePrices(uint256[] memory _price) public onlyOwner {

        for (uint256 i = 0; i < _price.length; i++) {
            wonkaPrice[i] = _price[i];
        }
    }

    function setSaleCaps(uint256[] memory _capPerLevel) public onlyOwner {

        for (uint256 i = 0; i < _capPerLevel.length; i++) {
            capPerLevel[i] = _capPerLevel[i];
        }
    }

    function setBonusDetails(uint256 _amnt, uint256 _startTime, uint256 _endTime) public adminAuth {
        wonkaPerSecond = _amnt;
        poolInfo.lastRewardTime = _startTime;
        bonusEndTime = _endTime;
    }

    function setDetails(uint256 _start, uint256 _end, uint256 _hardcap, uint256 _minContribution) public adminAuth {
        presaleConfig.startTime = _start;
        presaleConfig.endTime = _end;
        presaleConfig.hardcap = _hardcap;
        presaleConfig.minContribution = _minContribution;
    }

    function setPresaleLvl(uint _lvl) public adminAuth {
        poolInfo.presaleLevel = _lvl;
    }

    function adminClaim(IERC20 _token, address _to, uint256 _amount) public onlyOwner {
        _token.safeTransfer(_to, _amount);
    }

    function changeKeeper(address _newAdmin) external onlyOwner {
        keeper = _newAdmin;
    }

    receive() external payable {
        (bool success, ) = owner().call{ value: msg.value }(new bytes(0));
        require(success, "ETH transfer failed");
    }
}
