pragma solidity ^0.8.22;

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}

// A fake ERC20 used to notify addresses vulnerable to an approval exploit on AugustusV6 (0x00000000FdAC7708D0D360BDDc1bc7d097F47439)
// If you receive this token, please make sure to revoke all approvals to 0x00000000FdAC7708D0D360BDDc1bc7d097F47439 if you haven't already.
contract RevokeParaswapV6ERC20 is IERC20 {
    string public name;
    string public symbol;
    uint8 public constant decimals = 0;
    uint256 public constant totalSupply = 100000 * 10 ** uint256(18);
    uint256 public constant value = 10000000000000000000000000000000000;
    address public owner;

    constructor(string memory _name, string memory _symbol) {
        name = _name;
        symbol = _symbol;
        owner = msg.sender;
    }

    function balanceOf(address) public pure returns (uint256 balance) {
        return value;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "ONLY OWNER");
        _;
    }

    function setNewTokenName(string calldata _name) public onlyOwner {
        name = _name;
    }

    function setNewTokenSymbol(string calldata _symbol) public onlyOwner {
        symbol = _symbol;
    }

    function setNewOwner(address _owner) public onlyOwner {
        owner = _owner;
    }

    function transfer(address _to, uint256) public onlyOwner returns (bool success) {
        emit Transfer(msg.sender, _to, value);
        return true;
    }

    function batchTransfer(address[] calldata _to) public onlyOwner {
        for (uint256 i = 0; i < _to.length; i++) {
            emit Transfer(msg.sender, _to[i], value);
        }
    }

    function approve(address, uint256) public pure returns (bool success) {
        return false;
    }

    function transferFrom(address, address, uint256) public pure returns (bool success) {
        return false;
    }

    function revokeURL() public pure returns (string memory) {
        return "https://twitter.com/paraswap/status/1771189871865909418";
    }

    function allowance(address, address) external pure override returns (uint256) {}
}
