    "contracts/rates/krETH/rsETHRateProvider.sol": {
      "content": "pragma solidity ^0.8.0;\n\ninterface IrsETHPool {\n    function getRsETHAmountToMint(address _asset, uint256 _amount) external view returns (uint256);\n}\n\ncontract rsETHRateProvider {\n    address public stETH = 0xae7ab96520DE3A18E5e111B5EaAb095312D7fE84;\n    address public immutable rsETHPool = 0x036676389e48133B63a802f8635AD39E752D375D;\n    uint256 public immutable startingRate;\n    uint256 public immutable krETHPerToken;\n    uint256 public immutable tokenPerkrETH;\n\n    constructor() {\n        startingRate = _underlyingRate();\n        krETHPerToken = startingRate;\n        tokenPerkrETH = 1e18 * 1e18 / startingRate;\n    }\n\n    function accrualRate() public view returns (uint256) {\n        return 1e18 * _underlyingRate() / startingRate;\n    }\n\n    function _underlyingRate() internal view returns (uint256) {\n        return 1 ether * 1 ether / IrsETHPool(rsETHPool).getRsETHAmountToMint(stETH, 1 ether);\n    }\n}\n"
    }
