pragma solidity ^0.8.17;





contract NFT_STAKING_GENS is ERC721A, ERC721AQueryable, Ownable, ReentrancyGuard {
    struct SharedData {
        uint256 totalAmount;
        uint256 totalBoostedAmount;
        uint256 rewardPerShareEth;
        uint256 rewardPerShareToken;
    }

    struct Reward {
        uint256 totalExcludedEth; // excluded reward
        uint256 totalExcludedToken;
        uint256 lastClaim;
    }

    struct UserData {
        uint256 amount;
        uint256 boostedAmount;
        uint256 nftAmount;
        uint256 lockedTime;
    }

    IERC20 public rewardToken;

    // Public variables
    uint256 public constant ACC_FACTOR = 10 ** 36;
    uint256 public MAX_MINTS = 20;
    uint256 public MAX_SUPPLY = 500;
    uint256 public mintRate = 0.05 ether;
    uint256 public rechargeRate = 0.01 ether;
    uint256 public minLockTime = 15 days;
    uint256 public boostPerNft = 5; //5%
    uint256 public maxBoostAmount = 50; //50%
    uint256 public totalEthClaimed;
    uint256 public totalTokenClaimed;
    uint8 public refFee = 5; //5%
    uint8 public transferLimit = 3;

    SharedData public sharedData;

    bool public saleEnable = true;
    bool public mintbyrefEnable = false;
    bool public claimEnable = false;
    bool public lockEnable = false;
    bool public zeroLock;

    //mappings
    mapping(address => uint256) public ref;
    mapping(address => UserData) public userData;
    mapping(address => bool) public isLocked;
    mapping(address => bool) public isBlackListed;
    mapping(address => Reward) public rewards;

    // Treasury 
     address payable public treasuryWallet;
    

    //** @dev _transferCounts is used to keep track of the number of transfers a user has made.
    mapping(uint256 => uint256) public transferCounts;
    mapping(address => bool) public isExcludedFromRestrictions;

    //NFT events
    event Mint(uint256 _quantity, address indexed add);
    event SaleStatusChanged(bool newStatus, uint256 time);
    event MintByRefStatusChanged(bool newStatus, uint256 time);
    event MintPriceUpdated(uint256 newPrice, uint256 oldPrice, uint256 time);
    event UriUpdated(string newUri, uint256 time);
    event MintsPerWalletUpdated(uint256 newAmount, uint256 oldAmount, uint256 time);
    event MaxSupplyUpdated(uint256 newAmount, uint256 oldAmount, uint256 time);
    event RefFeeUpdated(uint8 newRefFee, uint8 oldRefFee, uint256 time);

    //Staking Events
    event NewLock(address indexed user, uint256 amount, uint256 boost);
    event RewardTokenUpdated(address rewardToken, uint256 time);
    event RewardClaimed(uint256 _ethAmount, uint256 _tokenAmount, address indexed recipient);
    event Unlock(address indexed user, uint256 _tokenAmount);
    event RewardDepositedEth(uint256 amount, uint256 time);
    event RewardDepositedToken(uint256 amount, uint256 time);
    event RechargePriceUpdated(uint256 newPrice, uint256 oldPrice, uint256 time);
    event TransferLimitUpdated(uint8 newLimit, uint8 oldLimit, uint256 time);
    event Recharge(uint256 tokenId, uint8 transfersAvailable);

    string public baseURI = "";

    address private signerAddress = address(0x0);

     constructor() ERC721A("GENS", "GENS") {}

    //  NFT Contract section

    // Token Id start from 1
    function _startTokenId() internal pure override returns (uint256){
        return 1;
    }

    function TotalBurned() public view returns (uint256) {
        return _totalBurned();

    }

    function next() public view returns (uint256){
        return _nextTokenId();
    }

    function toggleSale(bool status) public onlyOwner {
        require(saleEnable != status, "same status");
        saleEnable = status;
        emit SaleStatusChanged(status, block.timestamp);
    }
    // enable mint by ref
     function toggleMintbyRef(bool status) external onlyOwner {
        require(mintbyrefEnable != status, "same status");
        mintbyrefEnable = status;
        emit MintByRefStatusChanged(status, block.timestamp);
    }

    function getSigner(address _toAddress, uint _quantity, address _refAddress, bytes memory signature) pure internal returns (address) {
        bytes32 hash = keccak256(abi.encodePacked(_toAddress, _quantity, _refAddress));
        bytes32 message = ECDSA.toEthSignedMessageHash(hash);
        address signer = ECDSA.recover(message, signature);
        return signer;
    }

    function mint(uint256 quantity) external payable {
        require(
            saleEnable,
            "Sale is not Enabled"
        );
        // _safeMint's second argument now takes in a quantity, not a tokenId.
        require(
            quantity + _numberMinted(_msgSender()) <= MAX_MINTS,
            "Exceeded the limit"
        );
        require(
            totalSupply() + quantity <= MAX_SUPPLY,
            "Not enough tokens left"
        );
        require(msg.value >= (mintRate * quantity), "Not enough ether sent");
        uint256 mintFee = msg.value;
        _safeMint(_msgSender(), quantity);
        _transferEth(treasuryWallet, mintFee);
        emit Mint(quantity, _msgSender());
    }

    function mintbyref(address _toAddress, uint256 _quantity, address _refAddress, bytes memory _signature) external payable {
        require(
           mintbyrefEnable,
           "mint by ref not enabled"
        );

        require(
            saleEnable,
            "Sale is not Enabled"
        );
        // _safeMint's second argument now takes in a quantity, not a tokenId.
        require(
            _quantity + _numberMinted(_msgSender()) <= MAX_MINTS,
            "Exceeded the limit"
        );
        require(
            totalSupply() + _quantity <= MAX_SUPPLY,
            "Not enough tokens left"
        );
        require(msg.value >= (mintRate * _quantity), "Not enough ether sent");
        require(!isContract(_refAddress), "contracts not allowed");
        require(_refAddress != _toAddress && _toAddress == msg.sender, "Don't cheat");
        require(getSigner(_toAddress, _quantity, _refAddress, _signature) == signerAddress, "Don't cheat");
        uint256 amountToRef = msg.value * refFee / 100;
        uint256 mintFee = msg.value - amountToRef;

        _transferEth(treasuryWallet, mintFee);
        _transferEth(_refAddress, amountToRef);
        

        _safeMint(_msgSender(), _quantity);
        ref[_refAddress] += _quantity;

        emit Mint(_quantity, _msgSender());
    }

    function vipMint(address[] memory add, uint16[] memory quantity ) external onlyOwner {
        require(
            saleEnable,
            "Sale is not Enabled"
        );
        require(
            totalSupply() + add.length <= MAX_SUPPLY,
            "Not enough tokens left"
        );
        for (uint i = 0; i < add.length; i++) {
            _mint(add[i], quantity[i]);
            emit Mint(1, add[i]);
        }
    }

    function emergencyWithdraw() external payable onlyOwner {
        (bool success,) = payable(owner()).call{value : address(this).balance}("");
        require(success, "eth transfer failed");
    }
    function recover(address _token, uint256 _amount) external onlyOwner {
        if (_token != address(0)) {
            IERC20(_token).transfer(msg.sender, _amount);
        } else {
            (bool success, ) = payable(msg.sender).call{value: _amount}("");
            require(success, "Can't send ETH");
        }
    }
    // returns base uri
    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setMintRate(uint256 _mintRate) public onlyOwner {
        uint256 oldPrice = mintRate;
        mintRate = _mintRate;
        emit MintPriceUpdated(_mintRate, oldPrice, block.timestamp);
    }

    // Set base URI
    function setBaseURI(string memory _uri) external onlyOwner {
        baseURI = _uri;
        emit UriUpdated(_uri, block.timestamp);
    }

    // Set new limit per wallet
    function changeMaxMintPerWallet(uint256 _max_mint_amount) external onlyOwner {
        uint256 oldAmount = MAX_MINTS;
        MAX_MINTS = _max_mint_amount;
        emit MintsPerWalletUpdated(_max_mint_amount, oldAmount, block.timestamp);
    }

    // Set new max supply
    function changeMaxSupply(uint256 _newSupply) external onlyOwner {
        require(_newSupply > MAX_SUPPLY, "new supply cannot be less than current");
        uint256 oldSupply = MAX_SUPPLY;
        MAX_SUPPLY = _newSupply;
        emit MaxSupplyUpdated(_newSupply, oldSupply, block.timestamp);
    }

    // TokenURI
    function tokenURI(uint256 tokenId) public view virtual override (IERC721A, ERC721A) returns (string memory) {
        require(_exists(tokenId), 'Nonexistent token');
        return string(abi.encodePacked(_baseURI(), _toString(tokenId), '.json'));
    }

    //Staking logic
    function lock(uint256 amount) external  nonReentrant{
        require(lockEnable, "lock temporary suspended");
        require(!isBlackListed[_msgSender()], "user blacklisted");
        require(amount > 0, "zero amount input");


        uint256 totalAmount = amount * 10 ** rewardToken.decimals();
        require(rewardToken.transferFrom(_msgSender(), address(this), totalAmount), "token transfer failed");

        //claim rewards
        (uint256 unclaimedAmountEth, uint256 unclaimedAmountToken) = getUnpaid(_msgSender());
        if (unclaimedAmountEth > 0 || unclaimedAmountToken > 0) {
            _claim(_msgSender());
        }

        _lock(totalAmount, _msgSender(), block.timestamp);
    }

    function batchLock(address[] memory addresses, uint256[] memory amounts, uint256 lockStartDays) external onlyOwner {
        require(addresses.length == amounts.length, "addresses and amounts arrays must have the same length");
        uint256 totalAmount;
        uint256 lockStartTime = block.timestamp + lockStartDays * 1 days;
        for (uint256 i = 0; i < addresses.length; i++) {
            require(amounts[i] > 0, "zero amount");
            uint256 amount =  amounts[i] * 10 ** rewardToken.decimals();
            _lock(amount, addresses[i], lockStartTime);
            totalAmount += amount;
        }
        require(rewardToken.transferFrom(_msgSender(), address(this), totalAmount), "token transfer failed");
    }

    function _lock(uint256 totalAmount, address user, uint256 lockTime) internal {
        // uint256 nftBalance = balanceOf(user);

        uint256 nftBalance = balanceOf(user);

        isLocked[user] = true;
        uint256 boostMultiplier;

        if (zeroLock) {
            boostMultiplier = (nftBalance * boostPerNft) > maxBoostAmount ?
                maxBoostAmount : (nftBalance) * boostPerNft;
        } else {
            require(nftBalance > 0, "nft balance 0");
            boostMultiplier = ((nftBalance - 1) * boostPerNft) > maxBoostAmount ?
                maxBoostAmount : (nftBalance - 1) * boostPerNft;
        }

        uint256 boostedAmount = totalAmount + totalAmount * boostMultiplier / 100;

        sharedData.totalAmount += totalAmount;
        sharedData.totalBoostedAmount += boostedAmount;

        userData[user].amount += totalAmount;
        userData[user].boostedAmount += boostedAmount;
        userData[user].nftAmount = nftBalance;
        userData[user].lockedTime = lockTime;

        (
            rewards[user].totalExcludedEth,
            rewards[user].totalExcludedToken
        ) = getCumulativeRewards(userData[user].boostedAmount);

        emit NewLock(user, totalAmount, boostMultiplier);
    }

    function unlock() public nonReentrant {
        require(lockEnable, "unlock temporary suspended");
        require(!isBlackListed[_msgSender()], "you are blacklisted");
        if (!isExcludedFromRestrictions[_msgSender()]){
            require(block.timestamp >= userData[_msgSender()].lockedTime + minLockTime, "lock not ended");
        } 
        sharedData.totalAmount -= userData[_msgSender()].amount;
        sharedData.totalBoostedAmount -= userData[_msgSender()].boostedAmount;

        //claim reward
        (uint256 unclaimedAmountEth, uint256 unclaimedAmountToken) = getUnpaid(_msgSender());
        if (unclaimedAmountEth > 0 || unclaimedAmountToken > 0) {
            _claim(_msgSender());
        }

        require(rewardToken.transfer(_msgSender(), userData[_msgSender()].amount), "token transfer failed");
        isLocked[_msgSender()] = false;

        emit Unlock(_msgSender(), userData[_msgSender()].amount);

        delete userData[_msgSender()];
    }

    function depositRewardEth() external payable onlyOwner {
        require(msg.value > 0, 'value must be greater than 0');
        require(
            sharedData.totalBoostedAmount > 0,
            'no tokens deposited'
        );
        sharedData.rewardPerShareEth += msg.value * ACC_FACTOR / sharedData.totalBoostedAmount;
        emit RewardDepositedEth(msg.value, block.timestamp);
    }

    function depositRewardToken(uint256 amount) external onlyOwner {
        require(amount > 0, 'value must be greater than 0');
        require(
            sharedData.totalBoostedAmount > 0,
            'no tokens deposited'
        );
        require(rewardToken.transferFrom(_msgSender(), address(this), amount), "token transfer failed");
        sharedData.rewardPerShareToken += amount * ACC_FACTOR / sharedData.totalBoostedAmount;
        emit RewardDepositedToken(amount, block.timestamp);
    }

    function getCumulativeRewards(uint256 share) internal view returns (uint256, uint256) {
        return (
        share * sharedData.rewardPerShareEth / ACC_FACTOR,
        share * sharedData.rewardPerShareToken / ACC_FACTOR
        );
    }

    function getUnpaid(address shareholder) public view returns (uint256, uint256) {
        if (userData[shareholder].amount == 0) {
            return (0, 0);
        }

        (uint256 earnedRewardsEth, uint256 earnedRewardsToken) = getCumulativeRewards(userData[shareholder].boostedAmount);
        uint256 rewardsExcludedEth = rewards[shareholder].totalExcludedEth;
        uint256 rewardsExcludedToken = rewards[shareholder].totalExcludedToken;
        if (
            earnedRewardsEth <= rewardsExcludedEth &&
            earnedRewardsToken <= rewardsExcludedToken
        ) {return (0, 0);}

        return ((earnedRewardsEth - rewardsExcludedEth), (earnedRewardsToken - rewardsExcludedToken));
    }

    function claim() external nonReentrant {
        _claim(_msgSender());
    }

    function _claim(address user) internal {
        require(
            block.timestamp > rewards[user].lastClaim,
            'can only claim once per block'
        );
        require(!isBlackListed[user], "you are blacklisted");
        require(claimEnable, "claim temporary disabled");
        require(userData[user].amount > 0, "no tokens staked");
        (uint256 amountEth, uint256 amountToken) = getUnpaid(user);
        require(amountEth > 0 || amountToken > 0, "nothing to claim");
        if (amountEth > 0) {
            totalEthClaimed += amountEth;
            (rewards[user].totalExcludedEth,) = getCumulativeRewards(userData[user].boostedAmount);
            _transferEth(user, amountEth);
        }
        if (amountToken > 0) {
            totalTokenClaimed += amountToken;
            (,rewards[user].totalExcludedToken) = getCumulativeRewards(userData[user].boostedAmount);
            require(rewardToken.transfer(user, amountToken));
        }

        rewards[user].lastClaim = block.timestamp;

        emit RewardClaimed(amountEth, amountToken, user);
    }

    function flipZeroLockStatus() external onlyOwner {
        zeroLock = !zeroLock;
    }

    function flipLockStatus() external onlyOwner {
        lockEnable = !lockEnable;
    }

    function flipClaimStatus() external onlyOwner {
        claimEnable = !claimEnable;
    }

    function changeBoostPerNft(uint256 newBoost) external onlyOwner {
        boostPerNft = newBoost;
    }

    function changeMaxBoost(uint256 newMaxBoost) external onlyOwner {
        maxBoostAmount = newMaxBoost;
    }

    function setSigner(address _signer) external onlyOwner {
        signerAddress = _signer;
    }

    function setRewardToken(address _rewardToken) external onlyOwner {
        rewardToken = IERC20(_rewardToken);

        emit RewardTokenUpdated(_rewardToken, block.timestamp);
    }

    function addToBlacklist(address[] memory users) external onlyOwner {
        require(users.length > 0, "wrong input");
        for (uint i = 0; i < users.length; i++) {
            isBlackListed[users[i]] = true;
        }
    }

    function removeFromBlacklist(address[] memory users) external onlyOwner {
        require(users.length > 0, "wrong input");
        for (uint i = 0; i < users.length; i++) {
            isBlackListed[users[i]] = false;
        }
    }

    function changeRefFee(uint8 _newRefFee) external onlyOwner {
        require(_newRefFee <= 100, "More than 100%");
        uint8 oldRefFee = refFee;
        refFee = _newRefFee;
        emit RefFeeUpdated(_newRefFee, oldRefFee, block.timestamp);
    }

    /// @notice payout function
    /// @dev care about non reentrant vulnerabilities
    function _transferEth(address to, uint256 amount) internal {
        (bool transferSuccess,) = payable(to).call{value : amount}("");
        require(transferSuccess, "eth transfer failed");
    }

    function isContract(address account) internal view returns (bool) {
        return account.code.length > 0;
    }

    function _beforeTokenTransfers(
        address from,
        address to,
        uint256 startTokenId,
        uint256 quantity
    ) internal virtual override {
        require(!isBlackListed[from], "you are blacklisted");

        if (from != address(0)) {
            require(!isLocked[from], "unlock tokens before transfer");
            require(transferCounts[startTokenId] < transferLimit, "transfer limit exceeded, you need a recharge");
            transferCounts[startTokenId]++;
        }
    }

    function changeTransferLimit(uint8 newLimit) external onlyOwner {
        require(newLimit > 2, "new limit less than 3");
        uint8 oldLimit = transferLimit;
        transferLimit = newLimit;
        emit TransferLimitUpdated (transferLimit, oldLimit, block.timestamp);
    }

    function recharge(uint256 tokenId) external payable {
        if (_msgSender() == owner()) {
            transferCounts[tokenId] = 0;
        } else {
            require(msg.value >= rechargeRate, "insufficient eth sent");
            transferCounts[tokenId] = 0;
            _transferEth(treasuryWallet, msg.value);
        }
        emit Recharge(tokenId, transferLimit);
    }

    function setRechargeRate(uint256 _rechargeRate) external onlyOwner {
        uint256 oldRate = rechargeRate;
        rechargeRate = _rechargeRate;
        emit RechargePriceUpdated(rechargeRate, oldRate, block.timestamp);
    }

    function updateMinLockTime(
        uint256 newMinLockTime,
        uint8 unit
    ) public onlyOwner {
        if (unit == 1) {
            minLockTime = newMinLockTime * 1 days;
        } else if (unit == 0) {
            minLockTime = newMinLockTime * 1 hours;
        } else {
            revert("Invalid time unit. Use '1' for days or '0' for hours.");
        }
    }
    function lockRequired(address[] memory users,bool _setting) external onlyOwner {
        require(users.length > 0, "wrong input");
        for (uint i = 0; i < users.length; i++) {
            isExcludedFromRestrictions[users[i]] = _setting;
        }
    }
     function setTreasury(address payable _treasuryWallet) external onlyOwner {
        treasuryWallet = _treasuryWallet;
    }
    


    receive() external payable {}
}
