    "contracts/krETH.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IRateProvider {
    function krETHPerToken() external view returns (uint256);
    function tokenPerkrETH() external view returns (uint256);
}

interface IVaultSupervisor {
    function deposit(address vault, uint256 amount, uint256 minSharesOut) external returns (uint256 shares);
    function gimmieShares(address vault, uint256 shares) external;
}

contract krETH is ERC20, Ownable2Step {
    /// DEPENDENCIES ///

    using SafeERC20 for IERC20;

    /// STRUCTS ///

    struct Token {
        address vaultToken;
        address rateProvider;
        uint256 cap;
        uint256 deposited;
        bool paused;
    }

    /// STATE VARIABLES ///

    /// @notice Address of Karak vault supervisor
    address public immutable vaultSupervisor = 0x54e44DbB92dBA848ACe27F44c0CB4268981eF1CC;
    /// @notice Address of fee address
    address public feeAddress;

    /// @notice Array of approved tokens
    address[] public approvedTokens;

    /// @notice Bool if address is approved token
    mapping(address => bool) public approvedToken;
    /// @notice Bool if address is vault token
    mapping(address => bool) public vaultToken;
    /// @notice Bool is address is approved protector
    mapping(address => bool) public approvedProtector;
    /// @notice Token struct for token
    mapping(address => Token) public tokens;

    /// @notice Bool if deposits are open
    bool public depositsOpen;

    /// @notice Number of approved tokens
    uint256 public numApprovedTokens;
    /// @notice Mint fee percent - 100 = 1%
    uint256 public mintFee;
    /// @notice Redeem fee percent - 100 = 1%
    uint256 public redeemFee;
    /// @notice Fee denom
    uint256 public constant FEE_DENOM = 10_000;
    /// @notice Mint amount out
    uint256 public constant MIN_MINT = 0.001 ether;
    /// @notice Mint amount to redeem
    uint256 public constant MIN_REDEEM = 0.001 ether;

    /// EVENTS ///

    event Deposit(address indexed staker, uint256 krETHReceived);
    event Redeemed(address indexed staker, uint256 krETHBurned);
    event TokenBalanceUpdated(address indexed token, uint256 newBalance);
    event TokenAdded(address tokenAdded);
    event RateProviderUpdated(address indexed token, address indexed newProvider);
    event TokenRemoved(address tokenRemoved);
    event ProtectorAdded(address indexed protectorAdded);
    event ProtectorRemoved(address indexed protectorRemoved);
    event DepositsOpened();
    event DepositsClosed();
    event DepositPaused(address indexed token);
    event DepositUnpaused(address indexed token);
    event CapIncrease(address indexed token, uint256 oldCap, uint256 newCap);
    event MintFeeSet(uint256 oldFee, uint256 newFee);
    event RedeemFeeSet(uint256 oldFee, uint256 newFee);
    event FeeAddressSet(address oldAddress, address newAddress);

    /// CONSTRUCTOR ///

    constructor() ERC20("Kernel Restaked ETH", "krETH") Ownable2Step() {
        feeAddress = owner();
        mintFee = 10;
        redeemFee = 25;
    }

    /// MUTATIVE FUNCTIONS ///

    /// @notice                Deposit `_approvedToken` to receive krETH
    /// @param _approvedToken  Token to deposit
    /// @param _to             Address to send minted krETH to
    /// @param _amount         Amount of `_approvedToken` to deposit
    /// @param _receipt        Bool if to deposit receipt token
    function deposit(address _approvedToken, address _to, uint256 _amount, bool _receipt) external {
        if (!depositsOpen) require(msg.sender == owner(), "Deposits not open");

        Token memory token = tokens[_approvedToken];
        require(approvedToken[_approvedToken] && !token.paused, "Deposits not open for token");
        require(_amount > 0, "Can not deposit 0");
        require(token.deposited + _amount <= token.cap, "Exceeds cap");
        uint256 _rate = IRateProvider(token.rateProvider).krETHPerToken();

        address _vaultToken = token.vaultToken;

        if (!_receipt) {
            IERC20(_approvedToken).safeTransferFrom(msg.sender, address(this), _amount);
            IERC20(_approvedToken).forceApprove(_vaultToken, _amount);
            uint256 _min = _amount * 99 / 100;

            _amount = IVaultSupervisor(vaultSupervisor).deposit(_vaultToken, _amount, _min);
            IVaultSupervisor(vaultSupervisor).gimmieShares(_vaultToken, _amount);
        } else {
            IERC20(_vaultToken).safeTransferFrom(msg.sender, address(this), _amount);
        }

        uint256 _amountToMint = (_rate * _amount) / (10 ** IERC20Metadata(_vaultToken).decimals());
        require(_amountToMint >= MIN_MINT, "Less than min mint");

        if (msg.sender != owner() && msg.sender != feeAddress && mintFee > 0) {
            uint256 _fee = _amountToMint * mintFee / FEE_DENOM;
            _amountToMint -= _fee;
            _mint(feeAddress, _fee);
        }

        tokens[_approvedToken].deposited += _amount;

        _mint(_to, _amountToMint);
        emit Deposit(_to, _amountToMint);
    }

    /// @notice                         Redeem krETH to receive `_approvedTokenToReceive`
    /// @param _approvedTokenToReceive  Token to receieve receipt for
    /// @param _to                      Address to send receive redeemed `_approvedTokenToReceive`
    /// @param _krETHToRedeem           Amount of krETH to redeem
    function redeem(address _approvedTokenToReceive, address _to, uint256 _krETHToRedeem) external {
        require(approvedToken[_approvedTokenToReceive], "Not token");
        require(_krETHToRedeem >= MIN_REDEEM, "Less than min redeem");

        Token memory token = tokens[_approvedTokenToReceive];

        uint256 _rate = IRateProvider(token.rateProvider).tokenPerkrETH();

        if (msg.sender != owner() && msg.sender != feeAddress && redeemFee > 0) {
            uint256 _fee = _krETHToRedeem * redeemFee / FEE_DENOM;
            _krETHToRedeem -= _fee;
            _transfer(msg.sender, feeAddress, _fee);
        }

        uint256 _toSend = _rate * _krETHToRedeem / (10 ** decimals());

        require(token.deposited >= _toSend, "Not enough funds to redeem token");

        tokens[_approvedTokenToReceive].deposited -= _toSend;
        _burn(msg.sender, _krETHToRedeem);

        address _vaultToken = token.vaultToken;

        IERC20(_vaultToken).safeTransfer(_to, _toSend);

        emit Redeemed(_to, _krETHToRedeem);
    }

    /// OWNER FUNCTION ///

    /// @notice Set mint fee to `_fee`
    function setMintFee(uint256 _fee) external onlyOwner {
        require(_fee <= 50, "Mint fee > 0.50%");
        uint256 _oldFee = mintFee;
        mintFee = _fee;

        emit MintFeeSet(_oldFee, _fee);
    }

    /// @notice Set redeem fee to `_fee`
    function setRedeemFee(uint256 _fee) external onlyOwner {
        require(_fee <= 50, "Redeem fee > 0.50%");
        uint256 _oldFee = redeemFee;
        redeemFee = _fee;

        emit RedeemFeeSet(_oldFee, _fee);
    }

    /// @notice Set fee address to `_address`
    function setFeeAddress(address _address) external onlyOwner {
        require(_address != address(0), "Can not be zero address");
        address _oldAddress = feeAddress;
        feeAddress = _address;

        emit FeeAddressSet(_oldAddress, _address);
    }

    /// @notice                Add approved token
    /// @param _approvedToken  Address of approved token to add
    /// @param _vaultToken     Address of karak vault
    /// @param _rateProvider   Address of rate provider
    /// @param _cap            Cap of `_approvedToken`
    function addToken(address _approvedToken, address _vaultToken, address _rateProvider, uint256 _cap)
        external
        onlyOwner
    {
        require(!approvedToken[_approvedToken], "Already added");

        Token storage token = tokens[_approvedToken];

        approvedToken[_approvedToken] = true;
        vaultToken[_vaultToken] = true;

        token.vaultToken = _vaultToken;
        token.rateProvider = _rateProvider;
        setCap(_approvedToken, _cap);

        approvedTokens.push(_approvedToken);
        ++numApprovedTokens;

        emit TokenAdded(_approvedToken);
        emit RateProviderUpdated(_approvedToken, _rateProvider);
    }

    /// @notice              Remove `_approvedToken`
    /// @param _approvedToken  Address of approved token to remove
    function removeToken(address _approvedToken) external onlyOwner {
        require(approvedToken[_approvedToken], "Not approved token");

        Token memory token = tokens[_approvedToken];

        approvedToken[_approvedToken] = false;
        vaultToken[token.vaultToken] = false;
        tokens[_approvedToken].rateProvider = address(0);
        tokens[_approvedToken].vaultToken = address(0);

        uint256 _arrLength = approvedTokens.length;
        for (uint256 i; i < _arrLength; ++i) {
            if (approvedTokens[i] == _approvedToken) {
                approvedTokens[i] = approvedTokens[_arrLength - 1];
                approvedTokens.pop();
                break;
            }
        }
        --numApprovedTokens;

        emit TokenRemoved(_approvedToken);
        emit RateProviderUpdated(_approvedToken, address(0));
    }

    /// @notice Update rate provider to `_rateProvider` for `_token`
    function updateRateProvider(address _token, address _rateProvider) external onlyOwner {
        require(approvedToken[_token], "Not approved token");
        tokens[_token].rateProvider = _rateProvider;

        emit RateProviderUpdated(_token, _rateProvider);
    }

    /// @notice Unpause deposits of `_token`
    function unpauseDeposits(address _token) external onlyOwner {
        require(approvedToken[_token], "Not approved token");
        require(tokens[_token].paused, "Token not paused");

        tokens[_token].paused = false;

        emit DepositUnpaused(_token);
    }

    /// @notice Set `_newCap` for `_token`
    function setCap(address _token, uint256 _newCap) public onlyOwner {
        require(approvedToken[_token], "Not approved token");
        uint256 _oldCap = tokens[_token].cap;
        require(_newCap > _oldCap, "Cannot lower cap");

        tokens[_token].cap = _newCap;

        emit CapIncrease(_token, _oldCap, _newCap);
    }

    /// @notice         Recover tokens
    /// @param _to      Address to send recovered tokens
    /// @param _token   Address of token to recover
    /// @param _amount  Amount of token to recover
    function recoverTokens(address _to, address _token, uint256 _amount) external onlyOwner {
        require(!vaultToken[_token], "Can Not transfer vault token");
        IERC20(_token).safeTransfer(_to, _amount);
    }

    /// @notice Add protector
    function addProtector(address _protector) external onlyOwner {
        require(!approvedProtector[_protector], "Already Protector");
        approvedProtector[_protector] = true;
        emit ProtectorAdded(_protector);
    }

    /// @notice Remove protector
    function removeProtector(address _protector) external onlyOwner {
        require(approvedProtector[_protector], "Not Protector");
        approvedProtector[_protector] = false;
        emit ProtectorRemoved(_protector);
    }

    /// @notice Open deposits
    function openDeposits() external onlyOwner {
        require(!depositsOpen, "Deposits already opened");
        depositsOpen = true;
        emit DepositsOpened();
    }

    /// PROTECTOR FUNCTIONS ///

    /// @notice Pause deposits of `_token`
    function pauseDeposits(address _token) external {
        require(approvedProtector[msg.sender] || msg.sender == owner(), "Not approved protector");
        require(approvedToken[_token], "Not approved token");
        require(!tokens[_token].paused, "Token already paused");

        tokens[_token].paused = true;

        emit DepositPaused(_token);
    }

    /// @notice Close deposits
    function closeDeposits() external {
        require(approvedProtector[msg.sender] || msg.sender == owner(), "Not approved protector");
        require(depositsOpen, "Deposits already closed");
        depositsOpen = false;
        emit DepositsClosed();
    }
}
