// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "erc721a/contracts/ERC721A.sol";
import "erc721a/contracts/extensions/ERC721AQueryable.sol";
import "erc721a/contracts/extensions/ERC721ABurnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";

/**
 * @dev Minting contract for Little Darlings
 */
contract LittleDarlings is
    ERC721AQueryable,
    ERC721ABurnable,
    ReentrancyGuard,
    Ownable,
    ERC2981
{
    // Variables
    // ---------------------------------------------------------------

    uint256 private numUBAllowlistMint;
    uint256 private numAllowlistMint;
    bytes32 private merkleRootUBAllowlist;
    bytes32 private merkleRootAllowlist;

    bool private isUBAllowlistMintActive = false;
    bool private isAllowlistMintActive = false;
    bool private isMintActive = false;

    uint256 private collectionSize;
    uint256 private maxPerWalletUBAllowlist;
    uint256 private maxPerWalletAllowlist;
    uint256 private maxPerWallet;
    uint256 private allowlistUBMintPrice;
    uint256 private allowlistMintPrice;
    uint256 private publicMintPrice;
    address private devAddress;
    string private _baseTokenURI;

    // Helper functions
    // ---------------------------------------------------------------

    /**
     * @dev This function packs three uint16 values into a single uint64 value.
     * @param a: first uint16
     * @param b: second uint16
     * @param c: third uint16
     */
    function pack(uint16 a, uint16 b, uint16 c) internal pure returns (uint64) {
        return (uint64(a) << 32) | (uint64(b) << 16) | uint64(c);
    }

    /**
     * @dev This function unpacks a uint64 value into three uint16 values.
     * @param a: uint64 value
     */
    function unpack(uint64 a) internal pure returns (uint16, uint16, uint16) {
        return (uint16(a >> 32), uint16(a >> 16), uint16(a));
    }

    /**
     * @dev This function increases the number of UB allowlist mints for an address
     * @param account: address to increase UB allowlist mints for
     * @param quantity: number of UB allowlist mints to increase by
     */

    function increaseUBAllowlistMints(
        address account,
        uint256 quantity
    ) internal {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(account));
        _setAux(
            account,
            pack(
                senderAllowlistUBMints + uint16(quantity),
                senderAllowlistMints,
                senderGifts
            )
        );
    }

    /**
     * @dev This function increases the number of allowlist mints for an address
     * @param account: address to increase allowlist mints for
     * @param quantity: number of allowlist mints to increase by
     */

    function increaseAllowlistMints(
        address account,
        uint256 quantity
    ) internal {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(account));
        _setAux(
            account,
            pack(
                senderAllowlistUBMints,
                senderAllowlistMints + uint16(quantity),
                senderGifts
            )
        );
    }

    /**
     * @dev This function increases the number of gifts for an address
     * @param account: address to increase gifts for
     * @param quantity: number of gifts to increase by
     */

    function increaseGifts(address account, uint256 quantity) internal {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(account));
        _setAux(
            account,
            pack(
                senderAllowlistUBMints,
                senderAllowlistMints,
                senderGifts + uint16(quantity)
            )
        );
    }

    // Modifiers
    // ---------------------------------------------------------------

    /**
     * @dev This modifier ensures that the caller is a user and not a contract.
     */
    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract.");
        _;
    }

    /**
     * @dev This modifier ensures that UB allowlist mint is open.
     */
    modifier UBAllowlistMintActive() {
        require(isUBAllowlistMintActive, "UB Allowlist mint is not open.");
        _;
    }

    /**
     * @dev This modifier ensures that allowlist mint is open.
     */
    modifier allowlistMintActive() {
        require(isAllowlistMintActive, "Allowlist mint is not open.");
        _;
    }

    /**
     * @dev This modifier ensures that public mint is open.
     */
    modifier publicMintActive() {
        require(isMintActive, "Mint is not open.");
        _;
    }

    /**
     * @dev This modifier checks that the merkle proof is valid.
     * @param merkleProof The merkle proof bytes32 array.
     */
    modifier isValidMerkleProof(bytes32[] calldata merkleProof, bytes32 root) {
        require(
            MerkleProof.verify(
                merkleProof,
                root,
                keccak256(abi.encodePacked(msg.sender))
            ),
            "Address does not exist in this allowlist."
        );
        _;
    }

    /**
     * @dev This modifier ensures that there are public mint tokens left.
     * @param quantity The number of tokens to be minted.
     */
    modifier mintLeft(uint256 quantity) {
        require(
            totalSupply() + quantity <= collectionSize,
            "There are no tokens left to mint."
        );
        _;
    }

    /**
     * @dev This modifier ensures that the caller does not mint more than the max number of allowlist tokens per wallet in the UB allowlist.
     * @param quantity The number of tokens to be minted.
     */
    modifier lessThanMaxPerWalletUBAllowlist(uint256 quantity) {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(msg.sender));
        require(
            senderAllowlistUBMints + quantity <= maxPerWalletUBAllowlist,
            "This wallet has reached its maximum allocation of UB allowlist tokens."
        );
        _;
    }

    /**
     * @dev This modifier ensures that the caller does not mint more than the max number of allowlist tokens per wallet.
     * @param quantity The number of tokens to be minted.
     */
    modifier lessThanMaxPerWalletAllowlist(uint256 quantity) {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(msg.sender));
        require(
            senderAllowlistUBMints + senderAllowlistMints + quantity <=
                maxPerWalletAllowlist,
            "This wallet has reached its maximum allocation of allowlist tokens."
        );
        _;
    }

    /**
     * @dev This modifier ensures that the caller does not mint more than the max number of tokens per wallet.
     * @param quantity The number of tokens to be minted.
     */
    modifier lessThanMaxPerWallet(uint256 quantity) {
        (
            uint16 senderAllowlistUBMints,
            uint16 senderAllowlistMints,
            uint16 senderGifts
        ) = unpack(_getAux(msg.sender));
        require(
            _numberMinted(msg.sender) + quantity <=
                maxPerWallet +
                    senderAllowlistUBMints +
                    senderAllowlistMints +
                    senderGifts,
            "This wallet has reached its maximum allocation of tokens."
        );
        _;
    }

    /**
     * @dev This modifier ensures that the caller has sent the correct amount of ETH.
     * @param price The price of the token.
     * @param quantity The number of tokens to be minted.
     */
    modifier isCorrectPayment(uint256 price, uint256 quantity) {
        require(price * quantity == msg.value, "Incorrect amount of ETH sent.");
        _;
    }

    // Constructor
    // ---------------------------------------------------------------

    /**
     * @dev This function is the constructor for the contract.
     * @param collectionSize_ The number of tokens in the collection.
     * @param maxPerWalletUBAllowlist_ The maximum number of tokens that can be minted per wallet in the UB allowlist phase.
     * @param maxPerWalletAllowlist_ The maximum number of tokens that can be minted per wallet.
     * @param maxPerWallet_ The maximum number of tokens that can be minted per wallet.
     * @param allowlistUBMintPrice_ The price of UB allowlist mint.
     * @param allowlistMintPrice_ The price of allowlist mint.
     * @param publicMintPrice_ The price of public mint.
     * @param devAddress_ The address of the dev that receives eth in the whithdraw() function.
     */
    constructor(
        uint256 collectionSize_,
        uint256 maxPerWalletUBAllowlist_,
        uint256 maxPerWalletAllowlist_,
        uint256 maxPerWallet_,
        uint256 allowlistUBMintPrice_,
        uint256 allowlistMintPrice_,
        uint256 publicMintPrice_,
        address devAddress_
    ) Ownable(msg.sender) ERC721A("Little Darlings", "LD") {
        collectionSize = collectionSize_;
        maxPerWalletUBAllowlist = maxPerWalletUBAllowlist_;
        maxPerWalletAllowlist = maxPerWalletAllowlist_;
        maxPerWallet = maxPerWallet_;
        allowlistUBMintPrice = allowlistUBMintPrice_;
        allowlistMintPrice = allowlistMintPrice_;
        publicMintPrice = publicMintPrice_;
        devAddress = devAddress_;

        // Set royalty receiver to the 0xSplits contract,
        // at 5% (default denominator is 10000).
        _setDefaultRoyalty(0x2717f3449736949c9472ff05FB46b833Cf4c32eC, 500);
    }

    // Public minting functions
    // ---------------------------------------------------------------

    /**
     * @notice Mint multiple tokens from UB allowlist paid mint.
     * @param quantity The number of tokens to be minted.
     * @param merkleProof The merkle proof bytes32 array.
     */
    function UBAllowlistMint(
        uint256 quantity,
        bytes32[] calldata merkleProof
    )
        external
        payable
        nonReentrant
        callerIsUser
        UBAllowlistMintActive
        isValidMerkleProof(merkleProof, merkleRootUBAllowlist)
        mintLeft(quantity)
        lessThanMaxPerWalletUBAllowlist(quantity)
        isCorrectPayment(allowlistUBMintPrice, quantity)
    {
        numUBAllowlistMint += quantity;
        increaseUBAllowlistMints(msg.sender, quantity);
        _safeMint(msg.sender, quantity);
    }

    /**
     * @notice Mint multiple tokens from allowlist paid mint.
     * @param quantity The number of tokens to be minted.
     * @param merkleProof The merkle proof bytes32 array.
     */
    function allowlistMint(
        uint256 quantity,
        bytes32[] calldata merkleProof
    )
        external
        payable
        nonReentrant
        callerIsUser
        allowlistMintActive
        isValidMerkleProof(merkleProof, merkleRootAllowlist)
        mintLeft(quantity)
        lessThanMaxPerWalletAllowlist(quantity)
        isCorrectPayment(allowlistMintPrice, quantity)
    {
        numAllowlistMint += quantity;
        increaseAllowlistMints(msg.sender, quantity);
        _safeMint(msg.sender, quantity);
    }

    /**
     * @notice Mint multiple tokens from public paid mint.
     * @param quantity The number of tokens to be minted.
     */
    function mint(
        uint256 quantity
    )
        external
        payable
        nonReentrant
        callerIsUser
        publicMintActive
        mintLeft(quantity)
        lessThanMaxPerWallet(quantity)
        isCorrectPayment(publicMintPrice, quantity)
    {
        _safeMint(msg.sender, quantity);
    }

    /**
     * @notice Mint a token to each address in an array.
     * @param addresses An array of addresses to mint to.
     */
    function gift(
        address[] calldata addresses
    ) external nonReentrant onlyOwner mintLeft(addresses.length) {
        uint256 numToGift = addresses.length;
        for (uint256 i = 0; i < numToGift; i++) {
            increaseGifts(addresses[i], 1);
            _safeMint(addresses[i], 1);
        }
    }

    /**
     * @notice Mint multiple tokens to each address in an array.
     * @param addresses An n-sized array of addresses to mint to.
     * @param quantities An n-sized array  quantities to mint to each corresponding address.
     */
    function giftMultiple(
        address[] calldata addresses,
        uint256[] calldata quantities
    ) external nonReentrant onlyOwner {
        require(
            addresses.length == quantities.length,
            "The number of recipients and quantities must be the same."
        );
        uint256 totalGifts = 0;
        for (uint256 i = 0; i < quantities.length; i++) {
            totalGifts += quantities[i];
        }
        require(
            totalSupply() + totalGifts <= collectionSize,
            "There are no tokens left to mint."
        );
        for (uint256 i = 0; i < addresses.length; i++) {
            increaseGifts(addresses[i], quantities[i]);
            _safeMint(addresses[i], quantities[i]);
        }
    }

    // Public read-only functions
    // ---------------------------------------------------------------

    /**
     * @notice Get the number of tokens minted by an address.
     * @param owner The address to check.
     * @return The number of tokens minted by the address.
     */
    function numberMinted(address owner) public view returns (uint256) {
        return _numberMinted(owner);
    }

    /**
     * @notice Get the number of tokens minted in the UB allowlist tier.
     * @return The number of tokens minted in the UB allowlist tier.
     */
    function getNumAllowlistMint() public view returns (uint256) {
        return numAllowlistMint;
    }

    /**
     * @notice Get the number of tokens minted in the allowlist tier.
     * @return The number of tokens minted in the allowlist tier.
     */
    function getNumUBAllowlistMint() public view returns (uint256) {
        return numUBAllowlistMint;
    }

    /**
     * @notice Get the merkle root for the UB allowlist.
     * @return The merkle root for the UB allowlist.
     */
    function getUBAllowlistMerkleRoot() public view returns (bytes32) {
        return merkleRootUBAllowlist;
    }

    /**
     * @notice Get the merkle root for the allowlist.
     * @return The merkle root for the allowlist.
     */
    function getAllowlistMerkleRoot() public view returns (bytes32) {
        return merkleRootAllowlist;
    }

    /**
     * @notice Get the UB allowlist mint status.
     * @return The UB allowlist mint status.
     */
    function getIsUBAllowlistMintActive() public view returns (bool) {
        return isUBAllowlistMintActive;
    }

    /**
     * @notice Get the allowlist mint status.
     * @return The allowlist mint status.
     */
    function getIsAllowlistMintActive() public view returns (bool) {
        return isAllowlistMintActive;
    }

    /**
     * @notice Get the mint status.
     * @return The mint status.
     */
    function getIsMintActive() public view returns (bool) {
        return isMintActive;
    }

    /**
     * @notice Get the total number of tokens that can ever be minted in the collection.
     * @return The total number of tokens.
     */
    function getCollectionSize() public view returns (uint256) {
        return collectionSize;
    }

    /**
     * @notice Get the maximum number of tokens that can be minted per wallet in the UB allowlist phase.
     * @return The maximum number of tokens that can be minted per wallet in the UB allowlist phase. These also count towrds the general allowlist.
     */
    function getMaxPerWalletUBAllowlist() public view returns (uint256) {
        return maxPerWalletUBAllowlist;
    }

    /**
     * @notice Get the maximum number of tokens that can be minted per wallet in the allowlist phase.
     * @return The maximum number of tokens that can be minted per wallet in the allowlist phase.
     */
    function getMaxPerWalletAllowlist() public view returns (uint256) {
        return maxPerWalletAllowlist;
    }

    /**
     * @notice Get the maximum number of tokens that can be minted per wallet in the public phase.
     * @return The maximum number of tokens that can be minted per wallet in the public phase.
     */
    function getMaxPerWallet() public view returns (uint256) {
        return maxPerWallet;
    }

    /**
     * @notice Get the mint price for UB allowlist sale.
     * @return The UB allowlist mint price.
     */
    function getAllowlistUBMintPrice() public view returns (uint256) {
        return allowlistUBMintPrice;
    }

    /**
     * @notice Get the mint price for allowlist sale.
     * @return The allowlist mint price.
     */
    function getAllowlistMintPrice() public view returns (uint256) {
        return allowlistMintPrice;
    }

    /**
     * @notice Get the mint price for public sale.
     * @return The public mint price.
     */
    function getPublicMintPrice() public view returns (uint256) {
        return publicMintPrice;
    }

    /**
     * @notice Get the address that gets funds distributed to in the withdraw() method.
     * @return The number of tokens minted by the contract.
     */
    function getDevAddress() public view returns (address) {
        return devAddress;
    }

    /**
     * @notice Get the number of tokens that address has minted from the UB allowlist.
     * @return The number of free mint tokens that address has  minted from the UB allowlist.
     */
    function getOwnerAllowlistUBMintCount(
        address owner
    ) public view returns (uint16) {
        (uint16 ownerAllowlistUBMints, , ) = unpack(_getAux(owner));
        return ownerAllowlistUBMints;
    }

    /**
     * @notice Get the number of tokens that address has minted from the allowlist.
     * @return The number of free mint tokens that address has  minted from the allowlist.
     */
    function getOwnerAllowlistMintCount(
        address owner
    ) public view returns (uint16) {
        (, uint16 ownerAllowlistMints, ) = unpack(_getAux(owner));
        return ownerAllowlistMints;
    }


    /**
     * @notice Get the number of tokens that address has been gifted.
     * @return The number of free mint tokens that address has been gifted.
     */
    function getOwnerGiftsCount(address owner) public view returns (uint16) {
        (, , uint16 ownerGifts) = unpack(_getAux(owner));
        return ownerGifts;
    }

    /**
     * @notice Returns the Uniform Resource Identifier (URI) for `tokenId` token.
     * @param tokenId The token ID to query.
     */
    function tokenURI(
        uint256 tokenId
    ) public view virtual override(IERC721A, ERC721A) returns (string memory) {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        string memory baseURI = _baseURI();
        return
            bytes(baseURI).length != 0
                ? string(abi.encodePacked(baseURI, _toString(tokenId), ".json"))
                : "";
    }

    // Internal read-only functions
    // ---------------------------------------------------------------

    /**
     * @dev Returns base token metadata URI.
     * @return Base token metadata URI.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseTokenURI;
    }

    // Owner only administration functions
    // ---------------------------------------------------------------

    /**
     * @notice Set the merkle root for the UB paid mint allowlist.
     * @param _merkleRootUBAllowlist The new merkle root.
     */
    function setUBAllowlistMerkleRoot(
        bytes32 _merkleRootUBAllowlist
    ) external onlyOwner {
        merkleRootUBAllowlist = _merkleRootUBAllowlist;
    }

    /**
     * @notice Set the merkle root for the paid mint allowlist.
     * @param _merkleRootAllowlist The new merkle root.
     */
    function setAllowlistMerkleRoot(
        bytes32 _merkleRootAllowlist
    ) external onlyOwner {
        merkleRootAllowlist = _merkleRootAllowlist;
    }

    /**
     * @notice Set UB allowlist paid mint to active or inactive.
     * @param _isUBAllowlistMintActive True to set free mint to active, false to set to inactive.
     */
    function setUBAllowlistMintActive(
        bool _isUBAllowlistMintActive
    ) external onlyOwner {
        isUBAllowlistMintActive = _isUBAllowlistMintActive;
    }

    /**
     * @notice Set allowlist paid mint to active or inactive.
     * @param _isAllowlistMintActive True to set free mint to active, false to set to inactive.
     */
    function setAllowlistMintActive(
        bool _isAllowlistMintActive
    ) external onlyOwner {
        isAllowlistMintActive = _isAllowlistMintActive;
    }

    /**
     * @notice Set public mint to active or inactive.
     * @param _isMintActive True to set mint to active, false to set to inactive.
     */
    function setMintActive(bool _isMintActive) external onlyOwner {
        isMintActive = _isMintActive;
    }

    /**
     * @notice Reduce the number of tokens that can be minted.
     * @param _collectionSize The new number of total tokens that can ever be minted in the collection. Cannot be greater than the current collection size or smaller than the remaining tokens.
     */
    function setCollectionSize(uint256 _collectionSize) external onlyOwner {
        require(
            _collectionSize <= collectionSize,
            "Cannot increase collection size."
        );
        require(
            _collectionSize >= totalSupply(),
            "Cannot set collection size to less than the number of tokens already minted."
        );
        collectionSize = _collectionSize;
    }

    /**
     * @notice Set the maximum number of tokens that can be minted per wallet in the UB allowlist phase.
     * @param _maxPerWalletUBAllowlist The new maximum number of tokens that can be minted per wallet in the UB allowlist phase.
     */
    function setMaxPerWalletUBAllowlist(
        uint256 _maxPerWalletUBAllowlist
    ) external onlyOwner {
        maxPerWalletUBAllowlist = _maxPerWalletUBAllowlist;
    }

    /**
     * @notice Set the maximum number of tokens that can be minted per wallet in the allowlist phase.
     * @param _maxPerWalletAllowlist The new maximum number of tokens that can be minted per wallet in the allowlist phase.
     */
    function setMaxPerWalletAllowlist(
        uint256 _maxPerWalletAllowlist
    ) external onlyOwner {
        maxPerWalletAllowlist = _maxPerWalletAllowlist;
    }

    /**
     * @notice Set the maximum number of tokens that can be minted per wallet in the public phase.
     * @param _maxPerWallet The new maximum number of tokens that can be minted per wallet in the public phase.
     */
    function setMaxPerWallet(uint256 _maxPerWallet) external onlyOwner {
        maxPerWallet = _maxPerWallet;
    }

    /**
     * @notice Set the UB allowlist mint price.
     * @param _allowlistUBMintPrice The new mint price.
     */
    function setUBAllowlistMintPrice(
        uint256 _allowlistUBMintPrice
    ) external onlyOwner {
        allowlistUBMintPrice = _allowlistUBMintPrice;
    }

    /**
     * @notice Set the allowlist mint price.
     * @param _allowlistMintPrice The new mint price.
     */
    function setAllowlistMintPrice(
        uint256 _allowlistMintPrice
    ) external onlyOwner {
        allowlistMintPrice = _allowlistMintPrice;
    }

    /**
     * @notice Set the public mint price.
     * @param _publicMintPrice The new mint price.
     */
    function setPublicMintPrice(uint256 _publicMintPrice) external onlyOwner {
        publicMintPrice = _publicMintPrice;
    }

    /**
     * @notice Set the address that gets funds distributed to in the withdraw() method.
     * @param _devAddress The new dev address.
     */
    function setDevAddress(address _devAddress) external onlyOwner {
        devAddress = _devAddress;
    }

    /**
     * @notice Set the base metadata URI.
     * @param baseURI The new base metadata URI.
     */
    function setBaseURI(string calldata baseURI) external onlyOwner {
        _baseTokenURI = baseURI;
    }

    /**
     * @notice Withdraw ETH from the contract.
     * @dev 80% of the contract balance is sent to the owner and 20% is sent to the dev address.
     */
    function withdraw() external onlyOwner nonReentrant {
        (bool ownerWithdrawSuccess, ) = msg.sender.call{
            value: (address(this).balance * 8000) / 10000
        }("");
        require(ownerWithdrawSuccess, "Owner transfer failed");
        (bool devWithdrawSuccess, ) = devAddress.call{
            value: address(this).balance
