// File: AGI.sol

pragma solidity ^0.8.8;


contract AGI is ERC20, ERC20Burnable, ERC20Snapshot, ERC20Permit, Ownable, Pausable, ReentrancyGuard {
    mapping(address => bool) private _blacklist;
    mapping(address => bool) public isExchange;

    uint256 public buyTaxRate;
    uint256 public sellTaxRate;
    uint256 public transferTaxRate;
    address public taxReceiver;

    event BlacklistUpdated(address indexed account, bool isBlacklisted);
    event TaxRatesUpdated(uint256 buyTaxRate, uint256 sellTaxRate, uint256 transferTaxRate);
    event TaxReceiverUpdated(address taxReceiver);

    constructor() ERC20("AGI", "AGI") ERC20Permit("AGI") {
        _mint(msg.sender, 1_000_000_000_000 * 10 ** decimals());
        buyTaxRate = 0;
        sellTaxRate = 0;
        transferTaxRate = 0;
        taxReceiver = msg.sender; // Default tax receiver is the contract owner
    }

    function setTaxRates(uint256 _buyTaxRate, uint256 _sellTaxRate, uint256 _transferTaxRate) public onlyOwner {
        // Tax rates are set for network sustainability and not for profit-sharing.
        require(_buyTaxRate <= 100 && _sellTaxRate <= 100 && _transferTaxRate <= 100, "AGI: Invalid tax rates");
        buyTaxRate = _buyTaxRate;
        sellTaxRate = _sellTaxRate;
        transferTaxRate = _transferTaxRate;
        emit TaxRatesUpdated(_buyTaxRate, _sellTaxRate, _transferTaxRate);
    }

    function setTaxReceiver(address _taxReceiver) public onlyOwner {
        require(_taxReceiver != address(0), "AGI: Invalid tax receiver address");
        taxReceiver = _taxReceiver;
        emit TaxReceiverUpdated(_taxReceiver);
    }

    function setExchangeAddress(address exchange, bool status) public onlyOwner {
        isExchange[exchange] = status;
    }

    function transfer(address recipient, uint256 amount) public virtual override(ERC20) whenNotPaused returns (bool) {
        uint256 taxRate = _getTaxRate(msg.sender, recipient);
        uint256 tax = (amount * taxRate) / 10000; // Using basis points
        uint256 netAmount = amount - tax;
        _transfer(msg.sender, taxReceiver, tax);
        return super.transfer(recipient, netAmount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) public virtual override(ERC20) whenNotPaused returns (bool) {
        uint256 taxRate = _getTaxRate(sender, recipient);
        uint256 tax = (amount * taxRate) / 10000; // Using basis points
        uint256 netAmount = amount - tax;
        _transfer(sender, taxReceiver, tax);
        return super.transferFrom(sender, recipient, netAmount);
    }

    function _getTaxRate(address sender, address recipient) private view returns (uint256) {
        if (isExchange[sender] && !isExchange[recipient]) return buyTaxRate;
        if (!isExchange[sender] && isExchange[recipient]) return sellTaxRate;
        return transferTaxRate;
    }

    function snapshot() public onlyOwner {
        _snapshot();
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function addToBlacklist(address account) public onlyOwner {
        require(!_blacklist[account], "AGI: Account already blacklisted");
        _blacklist[account] = true;
        emit BlacklistUpdated(account, true);
    }

    function removeFromBlacklist(address account) public onlyOwner {
        require(_blacklist[account], "AGI: Account not blacklisted");
        _blacklist[account] = false;
        emit BlacklistUpdated(account, false);
    }

    function isBlacklisted(address account) public view returns (bool) {
        return _blacklist[account];
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal whenNotPaused override(ERC20, ERC20Snapshot) {
        require(!_blacklist[from], "AGI: Sender is blacklisted");
        require(!_blacklist[to], "AGI: Recipient is blacklisted");
        super._beforeTokenTransfer(from, to, amount);
    }

    function mint(address account, uint256 amount) public onlyOwner {
        _mint(account, amount);
    }

    function customBurn(address account, uint256 amount) public onlyOwner {
        _burn(account, amount);
    }
}
