contract AscPresale is Ownable {
    address public sellerAddress;
    bool public presaleActive = true;
    uint256 public totalSold = 0;
    uint256 public totalFund = 0 ether;
    uint256 public constant startTime = 1724173200;

    mapping(address => uint256) public buyers;
    mapping(string => uint256) public voucherIncome;

    constructor(address _seller) Ownable(_seller) {
        sellerAddress = _seller;
    }

    function buyNode(string memory voucherCode) public payable {
        require(presaleActive, "Presale is not active!");
        require(startTime <= block.timestamp, "Presale has not started yet!");

        uint256 _currentPrice = getCurrentPrice();
        uint256 nodesAmount = msg.value / _currentPrice;

        buyers[msg.sender] += nodesAmount;
        totalSold += nodesAmount;
        totalFund += msg.value;

        if (bytes(voucherCode).length > 0) {
            voucherIncome[voucherCode] += msg.value;
        }
    }

    function setSellerAddress(address _seller) public onlyOwner {
        sellerAddress = _seller;
    }

    function togglePresaleActive() public onlyOwner {
        presaleActive = !presaleActive;
    }

    function getAllocation(address _buyer) public view returns (uint256) {
        return buyers[_buyer];
    }

    function setAllocation(address _buyer, uint256 _allocation) public onlyOwner {
        buyers[_buyer] = _allocation;
    }

    function getMyAllocation() public view returns (uint256) {
        return buyers[msg.sender];
    }

    function getCurrentPrice() public view returns (uint256) {
        uint256 _currentPrice = 1.6 ether;

        if (block.timestamp < 1725382800) {
            _currentPrice = 0.25 ether;
        } else if (block.timestamp < 1726592400) {
            _currentPrice = 0.4 ether;
        } else if (block.timestamp < 1727802000) {
            _currentPrice = 0.45 ether;
        } else if (block.timestamp < 1729011600) {
            _currentPrice = 0.5 ether;
        } else if (block.timestamp < 1731430800) {
            _currentPrice = 0.6 ether;
        } else if (block.timestamp < 1732640400) {
            _currentPrice = 0.7 ether;
        } else if (block.timestamp < 1733850000) {
            _currentPrice = 0.8 ether;
        } else if (block.timestamp < 1735059600) {
            _currentPrice = 0.9 ether;
        } else if (block.timestamp < 1736269200) {
            _currentPrice = 1 ether;
        } else if (block.timestamp < 1737478800) {
            _currentPrice = 1.2 ether;
        } else if (block.timestamp < 1738688400) {
            _currentPrice = 1.4 ether;
        }
        return _currentPrice;
    }

    function withdrawFunds() public onlyOwner {
        require(
            payable(msg.sender).send(address(this).balance),
            "Failed withdraw!"
        );
    }
}
