// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./BRC20.sol";
import "hardhat/console.sol";

contract BRC20Factory {
    bytes32 private constant DOMAIN_NAME = keccak256("Bridge");
    bytes32 public constant DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );
    bytes32 public constant MINT_TYPEHASH =
        keccak256(
            abi.encodePacked(
                "Mint(address token,address to,uint256 amount,string txid)"
            )
        );
    bytes32 public DOMAIN_SEPARATOR;

    struct Parameters {
        string name;
        string symbol;
        uint8 decimals;
    }

    bool private entered;
    Parameters public parameters;
    address public owner;
    address public minter;
    uint256 public fee;

    mapping(bytes32 => bool) public used;

    event OwnerChanged(address indexed oldOwner, address indexed newOwner);
    event MinterChanged(address indexed oldMinter, address indexed newMinter);

    event FeeChanged(uint256 indexed oldFee, uint256 indexed newFee);

    event BRC20Created(address indexed sender, address indexed dog20);

    event Minted(
        address indexed token,
        address indexed to,
        uint256 indexed amount,
        string txid
    );

    event Burned(
        address indexed token,
        address indexed from,
        uint256 indexed amount,
        uint256 fee,
        string receiver
    );

    modifier nonReentrant() {
        require(!entered, "REENTRANT");
        entered = true;
        _;
        entered = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "UNAUTHORIZED");
        _;
    }

    modifier onlyMinter() {
        require(msg.sender == minter, "UNAUTHORIZED");
        _;
    }

    constructor() {
        owner = msg.sender;
        minter = msg.sender;
        emit OwnerChanged(address(0), msg.sender);
        emit MinterChanged(address(0), msg.sender);

        fee = 0.01 ether;

        uint256 chainId;
        assembly {
            chainId := chainid()
        }
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                DOMAIN_TYPEHASH,
                DOMAIN_NAME,
                keccak256(bytes("1")),
                chainId,
                address(this)
            )
        );
    }

    function createBRC20(
        string memory name,
        string memory symbol,
        uint8 decimals
    ) external onlyOwner returns (address brc20) {
        parameters = Parameters({
            name: name,
            symbol: symbol,
            decimals: decimals
        });
        brc20 = address(
            new BRC20{salt: keccak256(abi.encode(name, symbol, decimals))}()
        );
        delete parameters;
        emit BRC20Created(msg.sender, brc20);
    }

    function mint(
        address[] memory tokens,
        address[] memory tos,
        uint256[] memory amounts,
        string[] memory txids
    ) external onlyMinter nonReentrant {
        uint256 tokens_len = tokens.length;
        uint256 tos_len = tos.length;
        uint256 amounts_len = amounts.length;
        uint256 txids_len = txids.length;
        require(
            tokens_len == tos_len &&
                tos_len == amounts_len &&
                amounts_len == txids_len,
            "Input error"
        );

        for (uint256 i = 0; i < tokens_len; i++) {
            address token = tokens[i];
            address to = tos[i];
            uint256 amount = amounts[i];
            string memory txid = txids[i];
            bytes32 digest = buildMintSeparator(token, to, amount, txid);
            require(!used[digest], "reuse");
            used[digest] = true;
            BRC20(token).mint(to, amount);
            emit Minted(token, to, amount, txid);
        }
    }

    function burn(
        address token,
        uint256 amount,
        string memory receiver
    ) external payable nonReentrant {
        require(msg.value >= fee, "invalid ether");

        BRC20(token).transferFrom(msg.sender, address(this), amount);
        BRC20(token).burn(amount);

        emit Burned(token, msg.sender, amount, fee, receiver);
    }

    function withdraw(address to) external onlyOwner {
        uint256 balance = address(this).balance;
        payable(to).transfer(balance);
    }

    function setOwner(address _owner) external onlyOwner {
        emit OwnerChanged(owner, _owner);
        owner = _owner;
    }

    function setMinter(address _minter) external onlyOwner {
        emit MinterChanged(minter, _minter);
        minter = _minter;
    }

    function setFee(uint256 _fee) external onlyOwner {
        emit FeeChanged(fee, _fee);
        fee = _fee;
    }

    function buildMintSeparator(
        address token,
        address to,
        uint256 amount,
        string memory txid
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    "\x19\x01",
                    DOMAIN_SEPARATOR,
                    keccak256(
                        abi.encode(
                            MINT_TYPEHASH,
                            token,
                            to,
                            amount,
                            keccak256(bytes(txid))
                        )
                    )
                )
            );
    }
}
